INSERT INTO `tbl_category1` (`id`, `id_category`, `id_presentation`, `name`, `info`, `key`, `order`) VALUES
(12, 14, 4, 'Rùa - Rắn', '', 'rua-ran', 3),
(14, 14, 4, 'Đồ đồng', '<div class="col-2">\n								<div class="category-image">\n									<img src="https://lh5.googleusercontent.com/-DitY76P9I9Q/VDVvUxu84PI/AAAAAAAAAN0/iViXp233tSE/s800/MonCaLocDong.png" alt="Cá lóc" class="img-responsive"/>\n								</div>\n							</div>\n							<div class="col-2 last">\n								<div class="category-title">\n									<h2>Cá lóc đồng</h2>\n									<p>Thịt cá ngọt và thơm, ngoài để dùng làm thức ăn còn có thể chữa được nhiều bệnh vì thịt ít mỡ, giàu khoáng và vitamin. Thường được dùng chế biến thành món ăn dưỡng sinh được ưa chuộng hiện nay trên thế giới vì rất bổ dưỡng và có tác dụng chống oxy hóa...</p>\n								</div>\n							</div>', 'do-dong', 2),
(29, 14, 4, 'Khai vị', '<div class="col-2">\n								<div class="category-image">\n									<img src="https://lh4.googleusercontent.com/-zxelsdtV97o/VDVvcN7yHrI/AAAAAAAAAOs/haymM2DHnCc/s800/MonOcBuou.png" alt="Ốc bưu" class="img-responsive"/>\n								</div>\n							</div>\n							<div class="col-2 last">\n								<div class="category-title">\n									<h2>Ốc bươu</h2>\n									<p>Ốc bươu là món ăn dân dã, quen thuộc và rất ngon miệng, bình dân có thể chế biến nhiều món ngon như: ốc hấp tiêu, ốc bươu hấp chấm với nước mắm gừng, ốc bươu nhồi...</p>\n								</div>\n							</div>', 'khai-vi', 1),
(69, 14, 4, 'Khô - Gỏi', '<div class="col-2">\n								<div class="category-image">\n									<img src="https://lh3.googleusercontent.com/-c7v-c28DrhA/VDVvcmOrUqI/AAAAAAAAAOw/vxlAgZQgITc/s800/MonRan.png" alt="Rắn" class="img-responsive"/>\n								</div>\n							</div>\n							<div class="col-2 last">\n								<div class="category-title">\n									<h2>Rắn</h2>\n									<p>Thịt rắn được xếp vào hàng những món ăn cao cấp và bổ dưỡng. Mỗi loại rắn có vị hơi khác nhau nhưng chúng đều là món ăn bài thuốc chống đau nhức khớp, chữa bệnh phong thấp và tăng cường sức khỏe.</p>\n								</div>\n							</div>', 'kho-goi', 5),
(70, 14, 4, 'Dịch vụ', '<div class="col-2">\n								<div class="category-image">\n									<img src="https://lh4.googleusercontent.com/-26C1b5kxm-A/VDVvXP1CZoI/AAAAAAAAAOA/NgZDOnZTnhQ/s800/MonCaRoDong.png" alt="Cá rô" class="img-responsive"/>\n								</div>\n							</div>\n							<div class="col-2 last">\n								<div class="category-title">\n									<h2>Cá rô</h2>\n									<p>Những con cá rô mề to mập mạp dùng để chế biến những món kho tộ, canh chua, nướng mọi ...</p>\n								</div>\n							</div>', 'dich-vu', 7),
(71, 14, 4, 'Giải khát', '<div class="col-2">\n								<div class="category-image">\n									<img src="https://lh3.googleusercontent.com/-ktSoj1dWeUo/VDVva3I6ucI/AAAAAAAAAOg/HHj0kvb8e3w/s640/MonLuonDong.png" alt="Lươn" class="img-responsive"/>\n								</div>\n							</div>\n							<div class="col-2 last">\n								<div class="category-title">\n									<h2>Lươn</h2>\n									<p>Theo các chuyên gia của Viện dinh dưỡng, thịt lươn có hàm lượng giá trị dinh dưỡng rất cao, thậm chí cao hơn hẳn tôm, cua ... Lươn có thể chế biến những món kho nghệ, kho xả ớt, lẩu chua, nướng mọi, um ...</p>\n								</div>\n							</div>', 'giai-khat', 6);

