INSERT INTO `tbl_product_info` (`id`, `idproduct`, `image1`, `image2`, `info`) VALUES
(111, 147, '/mvc/templates/front/img/item-2/6.jpg', '/mvc/templates/front/img/item-2/6.jpg', '<p>\r\n	<font color="#000000" face="Verdana, Geneva, sans-serif" size="2"><span style="line-height: 20.7999992370605px;">Video giới thiệu m&oacute;n C&aacute; L&oacute;c nướng trui của Đ&agrave;i Truyền H&igrave;nh Đồng Th&aacute;p thực hiện.</span></font></p>\r\n<p>\r\n	<iframe align="middle" frameborder="0" height="480" scrolling="no" src="http://www.youtube.com/embed/5u43mMzSLmk" width="100%"></iframe></p>\r\n<p>\r\n	&nbsp;</p>\r\n'),
(112, 146, '/mvc/templates/front/img/item/1.jpg', '/mvc/templates/front/img/item/1.jpg', '<p>\r\n	<iframe frameborder="0" height="480" scrolling="no" src="http://www.youtube.com/embed/BEx436PJf_0" width="100%"></iframe></p>\r\n'),
(113, 145, '/mvc/templates/front/img/item-2/2.jpg', '/mvc/templates/front/img/item/2.jpg', '<p>\r\n	C&aacute; l&oacute;c kho tộ</p>\r\n'),
(114, 144, '/mvc/templates/front/img/item-2/3.jpg', '/mvc/templates/front/img/item/3.jpg', '<p>\r\n	Lẩu ch&aacute;o c&aacute;</p>\r\n'),
(115, 143, '/mvc/templates/front/img/item/4.jpg', '/mvc/templates/front/img/item/4.jpg', '<p style="text-align: justify;">\r\n	<span style="font-size:14px;"><b style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">C&aacute; l&oacute;c hấp bầu c&oacute; vị ngọt tự nhi&ecirc;n của c&aacute;, bầu v&agrave; l&agrave; m&oacute;n ăn d&acirc;n d&atilde; gi&uacute;p qu&iacute; kh&aacute;ch h&agrave;ng thay đổi khẩu vị. M&oacute;n c&oacute; c&aacute;ch l&agrave;m đơn giản v&agrave; nguy&ecirc;n liệu th&igrave; quen thuộc, dễ kiếm, nhưng để m&oacute;n ngon c&ograve;n phụ thuộc rất nhiều v&agrave;o việc chọn c&aacute; v&agrave; c&aacute;ch n&ecirc;m nếm gia vị sao cho hợp khẩu vị.</b></span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<img pagespeed_url_hash="970460923" src="http://www.bepnhata.com/img/b5d9679f5deac444a40baf2fc7a90aa56d50ce3328f6cb4e1655e2b01e19c5ec36360e6abfef2eea63fe.jpg" style="margin: 0px; padding: 0px; border: none; color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" /><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Bầu l&agrave;m sạch vạt một miếng mỏng theo chiều dọc v&agrave; kho&eacute;t bỏ hết ruột (lưu &yacute; kh&ocirc;ng gọt vỏ). C&agrave; rốt gọt vỏ, bổ miếng, h&agrave;nh l&aacute; rửa sạch th&aacute;i nhuyễn l&agrave;m mỡ h&agrave;nh, h&agrave;nh t&acirc;y lột vỏ th&aacute;i miếng nhỏ. Đậu phộng cho ch&uacute;t muối v&agrave;o rang ch&iacute;n, tẩy vỏ v&agrave; gi&atilde; dập. C&aacute; l&oacute;c mua c&ograve;n tươi sống bỏ ruột, lấy mang, đ&aacute;nh vảy v&agrave; l&agrave;m sạch nhớt.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Cho c&aacute; l&oacute;c đ&atilde; ướp ch&uacute;t gia vị rồi cho v&agrave;o tr&aacute;i bầu đ&atilde; bỏ ruột. Sau đ&oacute; xếp c&aacute;c nguy&ecirc;n liệu gồm c&agrave; rốt, h&agrave;nh t&acirc;y v&agrave; b&ecirc;n cạnh c&aacute; l&oacute;c v&agrave; n&ecirc;m th&ecirc;m ch&uacute;t gia vị. Cho c&aacute;, bầu v&agrave;o nồi hấp khoảng 20 ph&uacute;t l&agrave; được. Khi c&aacute; ch&iacute;n b&agrave;y ra đĩa v&agrave; rắc đậu phộng, mỡ h&agrave;nh l&ecirc;n tr&ecirc;n.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">M&oacute;n ăn k&egrave;m c&ugrave;ng nước tương dầm ớt th&aacute;i khoanh v&agrave; n&ecirc;n d&ugrave;ng n&oacute;ng để thịt c&aacute; kh&ocirc;ng bị tanh.</span></p>\r\n'),
(116, 142, '/mvc/templates/front/img/item/5.jpg', '/mvc/templates/front/img/item/5.jpg', '<p style="text-align: justify;">\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">C&aacute; l&oacute;c ngon nhất l&agrave; v&agrave;o đầu m&ugrave;a mưa, bụng đầy trứng; hoặc ra gi&ecirc;ng c&aacute; trưởng th&agrave;nh, b&eacute;o bở, mập &uacute;, d&ugrave; nướng lửa than, lửa rơm hay c&aacute;ch n&agrave;o cũng ngon tuyệt.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">C&aacute; l&oacute;c l&agrave; lo&agrave;i c&aacute; thịt ngon v&agrave; hiền nhất trong c&aacute;c lo&agrave;i c&aacute; đồng. C&oacute; người đ&atilde; thi vị h&oacute;a m&oacute;n c&aacute; l&oacute;c th&agrave;nh nhiều cấp bậc &ldquo;nhất hấp, nh&igrave; quay, tam x&agrave;o, tứ luộc&rdquo;. Thật ra, c&aacute; l&oacute;c l&agrave;m g&igrave; cũng ngon nhờ thịt ngọt, hiền lại &iacute;t xương v&agrave; nhiều đạm. Từ c&aacute; l&oacute;c nấu ch&aacute;o, nấu b&aacute;nh canh, nấu c&agrave; ri, nấu l&aacute; me non cho tới dồn thịt, rang muối, l&agrave;m mắm, phơi kh&ocirc;, chi&ecirc;n tươi, kho rim nước dừa... m&oacute;n n&agrave;o cũng &quot;c&oacute; đẳng cấp&quot;.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Song c&aacute; l&oacute;c nướng lại l&agrave; một m&oacute;n ăn v&ocirc; c&ugrave;ng đa dạng. N&agrave;o nướng trui, nướng lửa than, nướng l&aacute; sen, nướng đất s&eacute;t cho đến quay lu, rang muối, nướng muối ... m&oacute;n n&agrave;o cũng ngon tuyệt v&agrave; đ&ograve;i hỏi phải c&oacute; những b&agrave;n tay kh&eacute;o l&eacute;o, tỉ mẫn của người chế biến.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<img pagespeed_url_hash="1321439159" src="http://www.bepnhata.com/img/b5d9679f5deac444a40baf2fc7a90aa56d50ce3329f6c94f1654e2b01e19c5ec38300a02d1f06af074.jpg" style="margin: 0px; padding: 0px; border: none; color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" /><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">C&aacute; l&oacute;c nướng rơm. Ảnh: Thi&ecirc;n Lộc.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Anh Nguyễn Minh Trung, một đầu bếp nổi tiếng ở Cao L&atilde;nh, Đồng Th&aacute;p cho biết c&aacute; l&oacute;c ngon nhất l&agrave; v&agrave;o đầu m&ugrave;a mưa, bụng đầy trứng; hoặc ra gi&ecirc;ng, c&aacute; trưởng th&agrave;nh, b&eacute;o bở, mập &uacute;. D&ugrave; nướng lửa than, lửa rơm hay c&aacute;ch n&agrave;o cũng ngon tuyệt.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Trước hết phải chọn cho được những con c&aacute; l&oacute;c mập &uacute; rồi nướng bằng lửa than hồng hoặc d&ugrave;ng rơm để đốt (c&ograve;n gọi l&agrave; nướng trui). Sau khi ch&iacute;n đều, da c&aacute; chuyển sang m&agrave;u v&agrave;ng ruộm, m&ugrave;i thơm bốc l&ecirc;n phưng phức. D&ugrave;ng que tre cạo bỏ hết lớp vảy kh&eacute;t b&ecirc;n ngo&agrave;i rồi d&ugrave;ng đũa x&eacute; c&aacute; ra l&agrave;m đ&ocirc;i, rắc l&ecirc;n &iacute;t đậu phộng rang v&agrave;ng v&agrave; rưới th&ecirc;m mỡ h&agrave;nh để l&agrave;m tăng độ thơm của c&aacute;.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Nhưng muốn cầu kỳ v&agrave; tinh tế hơn, bạn c&oacute; thể chọn những con c&aacute; c&ograve;n tươi rồi d&ugrave;ng muối bọt sền sệt tr&eacute;t đều l&ecirc;n m&igrave;nh c&aacute; trước khi đặt l&ecirc;n bếp than hồng. Khi bắt đầu ch&iacute;n, da c&aacute; từ từ nứt ra để lộ những mảng thịt trắng ngần, bốc m&ugrave;i thơm lựng như muốn n&iacute;u k&eacute;o thực kh&aacute;ch.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Hiện nay, v&agrave;o m&ugrave;a kh&ocirc; hạn, c&aacute; l&oacute;c đồng kh&ocirc;ng đủ cung cấp cho c&aacute;c nh&agrave; h&agrave;ng, qu&aacute;n ăn n&ecirc;n nhiều đầu bếp phải chọn c&aacute; nu&ocirc;i hoăc c&aacute; b&ocirc;ng để thay thế. Đặc biệt l&agrave; c&aacute; b&ocirc;ng, thịt hơi mềm v&agrave; kh&ocirc;ng ngọt đậm bằng c&aacute; l&oacute;c n&ecirc;n nhiều đầu bếp điệu nghệ đ&atilde; biến tấu ra c&aacute;ch nướng d&ugrave;ng một kh&uacute;c m&iacute;a lau đập giập rồi xuy&ecirc;n từ đầu đến đu&ocirc;i c&aacute; để nướng. Ch&iacute;nh chất ngọt từ m&iacute;a đ&atilde; thấm v&agrave;o c&aacute; l&agrave;m cho m&oacute;n ăn trở n&ecirc;n đậm đ&agrave; quyến rũ.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<img pagespeed_url_hash="2405961128" src="http://www.bepnhata.com/img/b5d9679f5deac444a40baf2fc7a90aa56d50ce3329f6c94f1654e2b01e19c5ec38300a02d2f06af074.jpg" style="margin: 0px; padding: 0px; border: none; color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" /><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Đọt sen mới h&aacute;i để ăn k&egrave;m c&aacute; l&oacute;c nướng. Ảnh: Thi&ecirc;n Lộc.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">C&aacute; l&oacute;c nướng c&oacute; thể ăn k&egrave;m với b&uacute;n cuốn b&aacute;nh tr&aacute;ng, nhưng th&uacute; vị nhất l&agrave; cuốn đọt sen non, thứ đọt h&aacute;i v&agrave;o buổi s&aacute;ng c&ograve;n ngậm sương đ&ecirc;m. Nhưng m&oacute;n n&agrave;y kh&ocirc;ng phải l&uacute;c n&agrave;o cũng c&oacute;, m&agrave; phải biết chọn đ&uacute;ng chỗ, đ&uacute;ng thời điểm, chẳng hạn như ở vuờn Quốc gia Tr&agrave;m Chim - Tam N&ocirc;ng, rừng tr&agrave;m Tr&agrave; Sư - An Giang hoặc khu sinh th&aacute;i G&aacute;o Giồng - Cao L&atilde;nh (Đồng Th&aacute;p), nơi c&oacute; nguồn c&aacute; l&oacute;c đồng dồi d&agrave;o v&agrave; nhiều ao sen, l&aacute;ng sen.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Ngo&agrave;i ra, thưởng thức m&oacute;n c&aacute; l&oacute;c nướng m&agrave; bỏ qua &ldquo;bộ đồ l&ograve;ng c&aacute;&rdquo; e rằng c&ograve;n thiếu s&oacute;t. Ruột c&aacute; l&oacute;c l&agrave; bộ phận ngon đ&aacute;o để, mất n&oacute; th&agrave; kh&ocirc;ng ăn c&ograve;n hơn. Bao tử c&aacute; cho v&agrave;o miệng nhai nghe như sần sật, tim c&aacute; vừa cứng vừa gi&ograve;n, ruột c&aacute; dai dai, c&ograve;n gan v&agrave; trứng th&igrave; vừa b&ugrave;i vừa b&eacute;o v&agrave; nh&acirc;n nhẩn.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Khi v&agrave;o tiệc, mỗi người d&ugrave;ng những đọt sen cuốn chung với b&uacute;n v&agrave; c&aacute; c&ograve;n n&oacute;ng hổi, k&egrave;m th&ecirc;m ch&uacute;t rau thơm như quế, h&uacute;ng, khế, dưa leo, chuối ch&aacute;t, k&egrave;o n&egrave;o... chấm với mắm n&ecirc;m hoặc nước mắm me mới tận hưởng hết m&ugrave;i vị của &ldquo;bưng biền&rdquo; Nam bộ.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">M&oacute;n n&agrave;y hơn thua nhau ở chỗ nước chấm. C&aacute;c b&agrave; nội trợ thường chăm ch&uacute;t tỉ mỉ, nhất l&agrave; nước mắm me. N&ecirc;n chọn những tr&aacute;i me non đem nướng ch&iacute;n, b&oacute;c vỏ bỏ hột, xong h&ograve;a chung v&agrave;o ch&eacute;n nước mắm h&ograve;n, th&ecirc;m tỏi, ớt, đường sao cho vừa ăn, tạo n&ecirc;n một hương vị nồng thấm, đậm đặc nhưng kh&ocirc;ng qu&aacute; mặn. Đ&acirc;y l&agrave; m&oacute;n nước chấm kỳ c&ocirc;ng v&agrave; tinh tế nhất.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<img pagespeed_url_hash="3490483097" src="http://www.bepnhata.com/img/b5d9679f5deac444a40baf2fc7a90aa56d50ce3329f6c94f1654e2b01e19c5ec38300a02d3f06af074.jpg" style="margin: 0px; padding: 0px; border: none; color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" /><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Bữa tiệc c&aacute; l&oacute;c nướng cuốn đọt sen. Ảnh: Thi&ecirc;n Lộc.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">T&iacute;nh hấp dẫn của m&oacute;n ăn n&agrave;y l&agrave; sự kết hợp th&uacute; vị giữa sản vật phương Nam tr&ugrave; ph&uacute; với c&aacute;ch chế biến t&agrave;i t&igrave;nh của con người, đặc biệt l&agrave; sự phối hợp giữa c&aacute; v&agrave; l&aacute; sen, giữa gia vị v&agrave; rau củ. Ch&iacute;nh vị ch&aacute;t, chua, thơm, ngọt, mặn của rau thơm v&agrave; hương vị tuyệt vời của nước chấm đ&atilde; gi&uacute;p cho m&oacute;n ăn thăng hoa. So với m&oacute;n b&aacute;nh tr&aacute;ng cuốn th&igrave; đọt sen vượt trội hơn ở vị đăng đắng, ng&ograve;n ngọt, m&ugrave;i đặc trưng thơm, ngon, gi&ograve;n v&agrave; lạ miệng. Những người s&agrave;nh điệu về ăn uống c&ograve;n coi đọt sen l&agrave; một loại thảo dược vị đắng, t&iacute;nh b&igrave;nh, c&oacute; t&aacute;c dụng bổ t&igrave;, vị.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Thưởng thức m&oacute;n nầy kh&ocirc;ng nhất thiết phải v&agrave;o nh&agrave; h&agrave;ng hay qu&aacute;n ăn m&agrave; chỗ n&agrave;o cũng c&oacute; thể b&agrave;y tiệc. Th&uacute; vị nhất l&agrave; tr&ecirc;n bờ đ&ecirc;, tr&ecirc;n mui ghe hoặc b&ecirc;n ao sen, miễn sao mọi người c&ugrave;ng vui, c&ugrave;ng cạn ly rượu nồng để qu&ecirc;n hết chuyện buồn phiền mệt nhoc.</span></p>\r\n'),
(117, 152, '/mvc/templates/front/img/item/6.jpg', '/mvc/templates/front/img/item/6.jpg', '<p>\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Những ng&agrave;y mưa, nước nổi khắp c&aacute;c c&aacute;nh đồng. Thời điểm n&agrave;y cũng l&agrave; l&uacute;c ốc bươu xuất hiện kh&aacute; nhiều sau bao ng&agrave;y tr&aacute;nh nắng.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Ốc bươu l&agrave; một m&oacute;n d&acirc;n d&atilde; được nhiều người th&iacute;ch từ l&acirc;u nay. D&ugrave; l&agrave;m m&oacute;n g&igrave; th&igrave; c&ocirc;ng đoạn trước ti&ecirc;n l&agrave; phải đem to&agrave;n bộ số ốc ng&acirc;m nước lạnh, xả đi xả lại nhiều lần trong ng&agrave;y. C&oacute; người sợ ốc ốm mất ngon n&ecirc;n thường ng&acirc;m ốc với nước vo gạo, c&aacute;ch n&agrave;y vừa l&agrave;m cho ốc mau ra chất b&ugrave;n vừa l&agrave;m cho con ốc giữ được chất dinh dưỡng nhờ h&uacute;t từ nước gạo.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">C&aacute;c m&oacute;n truyền thống của ốc bươu l&acirc;u nay th&igrave; vẫn cứ hấp sả, luộc, um chuối xanh. Thế nhưng, chẳng lẽ cứ mấy m&oacute;n n&agrave;y ăn ho&agrave;i th&igrave; cũng ch&aacute;n. Mới đ&acirc;y, c&aacute;c chị ở x&oacute;m t&ocirc;i lại &ldquo;ph&aacute;t minh&rdquo; ra một m&oacute;n mới ho&agrave;n to&agrave;n, vừa lạ vừa ngon lại mang ch&uacute;t d&aacute;ng dấp ph&ugrave; hợp với văn h&oacute;a ẩm thực của người Việt, đ&oacute; ch&iacute;nh l&agrave; m&oacute;n ốc bươu xốt nước cốt dừa.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<img pagespeed_url_hash="1422104211" src="http://www.bepnhata.com/img/b5d9679f5deac444a40baf2fc7a90aa56d50c93326f6cd52024ffdb21f1cd9f23931046fbfef2eea63fe.jpg" style="margin: 0px; padding: 0px; border: none; color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" /><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<i style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Ốc bươu xốt nước cốt dừa l&agrave; một m&oacute;n hảo hạng</i><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Ốc c&oacute; rồi, dừa vườn nh&agrave; th&igrave; kh&ocirc;ng thiếu. Thường c&aacute;c chị chọn loại dừa vừa gi&agrave;. Tr&aacute;i dừa bổ ra, lấy nước để ri&ecirc;ng rồi d&ugrave;ng c&acirc;y s&ograve; hay c&aacute;i nắp chai bia nạo phần cơm dừa ra từng sợi mảnh, cho v&agrave;o cối gi&atilde; thật nhuyễn. Sau khi gi&atilde; xong, cho phần nước dừa đ&atilde; để ri&ecirc;ng v&agrave;o phần cơm dừa rồi d&ugrave;ng tay trộn đều cho nước dừa v&agrave; cơm dừa h&ograve;a quyện.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Sau đ&oacute; lược lấy phần nước cốt dừa c&oacute; m&agrave;u trắng đục đem đổ v&agrave;o nồi ốc đ&atilde; ướp gia vị trước đ&oacute;. Đợi một ch&uacute;t cho nước cốt dừa thấm đều từng con ốc rồi đậy nắp đem bắc l&ecirc;n bếp, cứ thế nấu đến khi n&agrave;o nước cốt ngấm v&agrave;o thịt ốc v&agrave; chỉ c&ograve;n xăm xấp trong nồi ốc th&igrave; c&oacute; thể khui nồi d&ugrave;ng được.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Ốc bươu xốt nước cốt dừa l&agrave; một m&oacute;n hảo hạng. Ốc được m&uacute;c ra đĩa, tỏa hơi thơm phức. D&ugrave; ở qu&ecirc; nhưng c&aacute;c chị cũng l&yacute; giải rất hợp t&igrave;nh hợp l&yacute;, l&agrave; ăn ốc nước dừa c&oacute; t&iacute;nh m&aacute;t rất dễ bị h&agrave;n n&ecirc;n nhất thiết phải c&oacute; ch&eacute;n mắm gừng, &iacute;t đọt rau răm để kề b&ecirc;n mới c&acirc;n bằng &acirc;m dương trong t&igrave; vị, nếu c&oacute; ch&uacute;t rượu ng&acirc;m nhấm nh&aacute;p c&agrave;ng hay.</span><br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<br style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;" />\r\n	<span style="color: rgb(0, 0, 0); font-family: Verdana, Geneva, sans-serif; font-size: 13px; line-height: 20.7999992370605px;">Phải n&oacute;i với m&oacute;n n&agrave;y, con ốc bươu từ nơi b&ugrave;n s&acirc;u lấm l&aacute;p đ&atilde; h&oacute;a th&acirc;n th&agrave;nh một đặc sản ẩm thực độc đ&aacute;o trong m&ugrave;a nước nổi.</span></p>\r\n'),
