INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(1, 1, '2013-01-03 17:00:00', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Xu hướng tiếp tục hạ gi&aacute; để tiếp cận người mua được đơn vị tư vấn CBRE Việt Nam đưa ra trong bản b&aacute;o c&aacute;o thị trường mới đ&acirc;y d&ugrave; trước đấy TP Hồ Ch&iacute; Minh gi&aacute; nh&agrave; đ&atilde; giảm trung b&igrave;nh xấp xỉ 20%, c&aacute; biệt c&oacute; dự &aacute;n giảm 50% gi&aacute;&hellip;</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="325" src="http://saigonnhadat.vn/upload/UserFiles/image/04_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2010.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo CBRE Việt Nam, TP HCM c&ograve;n hơn 28.000 căn hộ đ&atilde; ch&agrave;o b&aacute;n tr&ecirc;n thị trường nhưng chưa c&oacute; chủ sở hữu v&agrave; phải mất 5 năm nữa mới c&oacute; thể vượt qua t&igrave;nh trạng thừa cung.</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo CBRE Việt Nam, TP Hồ Ch&iacute; Minh tồn kho 28 ngh&igrave;n căn hộ</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Khảo s&aacute;t của CBRE, trong qu&yacute; IV/2012, thị trường căn hộ b&aacute;n tại TP HCM tiếp tục giảm gi&aacute; tr&ecirc;n tất cả c&aacute;c ph&acirc;n kh&uacute;c. Trong đ&oacute; căn hộ cao cấp c&oacute; mức giảm nhiều nhất so với qu&yacute; trước (2,3%) v&agrave; rớt 7,6% so với c&ugrave;ng kỳ năm 2011. Việc giảm gi&aacute; b&aacute;n được thực hiện chủ yếu dưới 2 h&igrave;nh thức: trực tiếp v&agrave; gi&aacute;n tiếp.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Đơn vị nghi&ecirc;n cứu thị trường n&agrave;y đ&aacute;nh gi&aacute;, nguồn cung nh&agrave; ở b&aacute;n tại TP HCM tiếp tục giảm 18,5% so với qu&yacute; trước v&agrave; giảm 16,1% so với c&ugrave;ng kỳ năm ngo&aacute;i.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">C&aacute; biệt, theo đơn vị nghi&ecirc;n cứu thị trường n&agrave;y th&igrave; trong qu&yacute; IV/2012, một v&agrave;i căn biệt thự tại quận Thủ Đức (TP HCM) đ&atilde; rớt gi&aacute; từ nửa triệu đ&ocirc; xuống c&ograve;n 250-300 ngh&igrave;n USD một căn, giảm đến 50% so với qu&yacute; III.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ngo&agrave;i ra nh&agrave; ở gắn liền với đất c&ograve;n vấp phải sự cạnh tranh từ c&aacute;c dự &aacute;n mua để cho thu&ecirc; lại v&agrave; c&aacute;c dự &aacute;n đất nền ở c&aacute;c tỉnh l&acirc;n cận TP HCM như B&igrave;nh Dương, Đồng Nai, Long An.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Đơn vị tư vấn khảo s&aacute;t bất động sản n&agrave;y dự b&aacute;o t&igrave;nh trạng giảm gi&aacute; sẽ tiếp tục diễn ra ở nhiều ph&acirc;n kh&uacute;c. Thử th&aacute;ch vẫn c&ograve;n ở ph&iacute;a trước đối với thị trường nh&agrave; ở TP HCM, đặc biệt l&agrave; đối với c&aacute;c dự &aacute;n c&oacute; vị tr&iacute; kh&ocirc;ng đẹp.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nhận định tổng qu&aacute;t về năm nay, CBRE Việt Nam cho rằng nhiều khả năng thị trường nh&agrave; ở b&aacute;n sẽ kh&ocirc;ng c&oacute; sự chuyển m&igrave;nh lớn mặc d&ugrave; c&oacute; những nỗ lực của Ch&iacute;nh phủ nhằm giải cứu bất động sản.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Những dự b&aacute;o về gi&aacute; nh&agrave; giảm s&acirc;u, rớt gi&aacute; thảm hại năm 2012của đơn vị tư vấn n&agrave;y cũng tr&ugrave;ng với nhiều &yacute; kiến được đưa ra trong nhiều hội thảo diễn ra trước đấy. Tuy nhi&ecirc;n, trong một cuộc gặp gỡ gần đ&acirc;y nhất quy tụ nhiều doanh nghiệp địa ốc TP Hồ Ch&iacute; Minh th&igrave; nhiều doanh nghiệp của th&agrave;nh phố đ&atilde; hạ quyết t&acirc;m kh&ocirc;ng giảm gi&aacute;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng L&ecirc; Hữu Nghĩa, Tổng gi&aacute;m đốc C&ocirc;ng ty địa ốc L&ecirc; Th&agrave;nh, đơn vị doanh nghiệp ti&ecirc;u biểu cho tr&agrave;o lưu giảm gi&aacute; căn hộ tại TP Hồ Ch&iacute; Minh cho biết: &quot;Hiện gi&aacute; căn hộ m&agrave; c&ocirc;ng ty đưa ra đều thấp dưới mức cơ sở, nhưng b&aacute;n kh&ocirc;ng hề dễ. Người mua nh&agrave; vẫn đang tr&ocirc;ng đợi gi&aacute; sẽ giảm hơn. Họ cho rằng đ&acirc;y vẫn chưa phải l&agrave; gi&aacute; trị thật v&agrave; chủ đầu tư vẫn c&ograve;n c&oacute; l&atilde;i&quot;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Vị l&atilde;nh đạo n&agrave;y khẳng định doanh nghiệp của &ocirc;ng kh&ocirc;ng thể giảm th&ecirc;m v&igrave; hệ số lợi nhuận đ&atilde; hạ đến mức tối thiểu.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;Theo D&acirc;n Tr&iacute;</strong></em></p>\r\n</div>\r\n', 'TPHCM: Giá nhà năm nay sẽ giảm chưa từng thấy', 0),
(2, 1, '2013-01-06 03:08:35', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Năm mới, h&agrave;ng loạt chủ đầu tư địa ốc đ&atilde; tung dự &aacute;n mới, đ&oacute;n đầu thời cơ.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Tiếp tục ra h&agrave;ng</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nối tiếp th&agrave;nh c&ocirc;ng năm cũ, C&ocirc;ng ty cổ phần Bất động sản Hưng Thịnh (Hưng Thịnh Land) đ&atilde; chọn dự &aacute;n Screc II Metro Apartment do C&ocirc;ng ty X&acirc;y dựng - Kinh doanh nh&agrave; S&agrave;i G&ograve;n l&agrave;m chủ đầu tư, tung ra thị trường ngay từ đầu năm. Dự &aacute;n nằm trong khu đ&ocirc; thị An Ph&uacute; - An Kh&aacute;nh, quận 2, c&oacute; 94 căn hộ, diện t&iacute;ch 89m2 - 110m2/căn, hiện tại đ&atilde; thi c&ocirc;ng đến tầng thứ 5. Theo c&ocirc;ng bố từ chủ đầu tư, chung cư c&oacute; gi&aacute; b&aacute;n 17 triệu đồng/m2, thanh to&aacute;n 8 đợt, giao nh&agrave; v&agrave;o cuối năm 2013.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Nguyễn Duy Minh, Tổng Gi&aacute;m đốc Hưng Thịnh Land, cho biết: &ldquo;Dự &aacute;n c&oacute; vị tr&iacute; tốt nhưng gi&aacute; b&aacute;n rất rẻ so với c&aacute;c dự &aacute;n kh&aacute;c trong khu vực&rdquo;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Chiết khấu cao, xem như một h&igrave;nh thức hạ gi&aacute; cũng được c&aacute;c chủ đầu tư khai th&aacute;c triệt để. Từng mở b&aacute;n từ khi mới x&acirc;y xong phần m&oacute;ng, nhưng b&aacute;n trầy trật, nay nh&acirc;n sự kiện kh&aacute;nh th&agrave;nh c&acirc;y cầu nối qua s&ocirc;ng &Ocirc;ng Lớn, dự &aacute;n cũng đ&atilde; xong phần th&ocirc;, C&ocirc;ng ty Him Lam mở b&aacute;n lại chung cư Him Lam Riverside. Chủ đầu tư khuyến m&atilde;i rộn r&agrave;ng: Kh&aacute;ch h&agrave;ng nộp 50% sẽ nhận nh&agrave;. Nếu kh&ocirc;ng ở, c&ocirc;ng ty sẽ thu&ecirc; lại gi&aacute; từ 16 - 24 triệu đồng/th&aacute;ng t&ugrave;y theo diện t&iacute;ch mua căn hộ; hỗ trợ to&agrave;n bộ l&atilde;i vay trong năm đầu ti&ecirc;n (12 th&aacute;ng) trong trường hợp người mua c&oacute; nhu cầu vay vốn, cho vay 70% tổng gi&aacute; trị căn hộ, thời hạn 15 năm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">C&ocirc;ng ty Địa ốc Ho&agrave;ng Anh S&agrave;i G&ograve;n mở b&aacute;n căn hộ &Acirc;u Cơ Tower (quận T&acirc;n Ph&uacute;), với gi&aacute; b&aacute;n khoảng 1,2 tỷ đồng/căn hộ, kh&aacute;ch h&agrave;ng sẽ được chiết khấu đến 100 triệu đồng. Khi k&yacute; hợp đồng thanh to&aacute;n 50%, chiết khấu l&ecirc;n đến 3%; nếu thanh to&aacute;n 95%, chiết khấu l&ecirc;n đến 100 triệu đồng/căn hộ. Ng&acirc;n h&agrave;ng BIDV hỗ trợ vay theo l&atilde;i suất hỗ trợ k&iacute;ch cầu của Ch&iacute;nh phủ với thời hạn l&ecirc;n đến 15 năm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Một số căn hộ gi&aacute; thấp cũng được tưng bừng mở b&aacute;n như Anh Tuấn Apartment (9,7 triệu đồng), Tanibuilding Sơn Kỳ 2 (13 triệu đồng), Carina Plaza (13,2 triệu đồng)... Trong khi đ&oacute;, tổng kết lại sau hai tuần c&ocirc;ng bố dự &aacute;n Ngọc B&iacute;ch Residence tại Đồng Nai, C&ocirc;ng ty Kim Oanh đ&atilde; b&aacute;n được 122 nền, mức gi&aacute; khởi điểm 3,3 triệu đồng/m2. Đ&acirc;y l&agrave; con số kh&aacute; ấn tượng trong thời điểm thị trường c&ograve;n đ&oacute;ng băng!</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="280" src="http://saigonnhadat.vn/upload/UserFiles/image/08_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%209.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="400" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Dự &aacute;n Kenton x&acirc;y xong phần th&ocirc; đ&atilde; l&acirc;u, c&oacute; diện t&iacute;ch căn hộ rất lớn n&ecirc;n kh&oacute; b&aacute;n.</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">L&aacute;ch luật</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">C&aacute;c chủ đầu tư bằng mọi c&aacute;ch giải ph&oacute;ng h&agrave;ng tồn kho. Một giải ph&aacute;p đang thu h&uacute;t sự ch&uacute; &yacute; của tất cả giới địa ốc l&agrave; chẻ nhỏ căn hộ, hiện đang c&oacute; nhiều th&ocirc;ng tin tr&aacute;i chiều. Một b&aacute;o c&aacute;o mới đ&acirc;y, TPHCM khẳng định đ&atilde; cho ph&eacute;p chủ đầu tư x&acirc;y dựng căn hộ xong nhưng chưa b&aacute;n, được điều chỉnh lại diện t&iacute;ch nhưng kh&ocirc;ng tăng diện t&iacute;ch s&agrave;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">C&aacute;ch nay chưa l&acirc;u, tại một cuộc hội thảo, &ocirc;ng Nguyễn Mạnh H&agrave;, Cục trưởng Cục Quản l&yacute; nh&agrave; (Bộ X&acirc;y dựng), cho biết đ&atilde; soạn xong đề &aacute;n cho ph&eacute;p chẻ nhỏ căn hộ, đang tr&igrave;nh bộ trưởng k&yacute; ban h&agrave;nh.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo ph&acirc;n t&iacute;ch của một l&atilde;nh đạo Hiệp hội Bất động sản TPHCM, x&eacute;t về mặt luật ph&aacute;p, kh&ocirc;ng c&oacute; văn bản n&agrave;o cho ph&eacute;p chẻ nhỏ căn hộ. Bởi quy định hiện h&agrave;nh mỗi căn hộ chỉ được ph&eacute;p ở từ 3,5 - 4 người, khi chẻ nhỏ căn hộ sẽ l&agrave;m tăng căn hộ, tức l&agrave; tăng d&acirc;n số. Tuy nhi&ecirc;n, thực tế vẫn diễn ra &ldquo;l&agrave;m chui&rdquo; chẻ nhỏ căn hộ v&agrave; kh&ocirc;ng bị ch&iacute;nh quyền &ldquo;thổi c&ograve;i&rdquo;, v&igrave; quy định của ph&aacute;p luật kh&ocirc;ng ngăn cấm &ldquo;1 căn hộ c&oacute; 2 cửa ra v&agrave;o, 2 toa l&eacute;t&rdquo;. Kết quả sẽ dẫn đến hai t&igrave;nh huống đều&hellip; &ldquo;bất an&rdquo;: nếu b&aacute;n th&igrave; hai căn hộ sẽ c&oacute; chung một sổ đỏ; trong trường hợp kh&ocirc;ng b&aacute;n được sẽ k&yacute; hợp đồng thu&ecirc;, sổ đỏ đứng t&ecirc;n chủ đầu tư.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Gi&aacute;m đốc một c&ocirc;ng ty địa ốc đang l&agrave;m thủ tục chẻ nhỏ căn hộ cho biết, hiện nay b&aacute;n căn hộ theo kiểu n&agrave;y rất chậm, nhưng quan trọng l&agrave; l&agrave;m trước để đ&oacute;n đầu ch&iacute;nh s&aacute;ch. Khi Nh&agrave; nước c&oacute; văn bản ph&aacute;p luật ch&iacute;nh thống cho ph&eacute;p căn hộ 2 người, l&uacute;c đ&oacute; sẽ hợp thức h&oacute;a ngay tức khắc, tức l&agrave; tăng căn hộ nhưng kh&ocirc;ng tăng d&acirc;n số.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&ldquo;H&agrave;ng loạt chủ đầu tư đ&atilde; chuẩn bị cơ sở hạ tầng kỹ thuật, khi Nh&agrave; nước cho ph&eacute;p h&igrave;nh th&agrave;nh căn hộ 2 người sẽ tiến h&agrave;nh chẻ ngay v&agrave; b&aacute;n rầm rộ, chắc chắn l&uacute;c đ&oacute; m&atilde;i lực rất mạnh, h&agrave;ng tồn kho giảm rất nhiều, c&aacute;c căn hộ c&oacute; diện t&iacute;ch lớn được giải ph&oacute;ng sạch sẽ. V&igrave; khi chẻ nhỏ căn hộ, gi&aacute; căn hộ giảm c&ograve;n một nửa, l&uacute;c đ&oacute; người c&oacute; nhu cầu, nếu thu nhập thấp cũng c&oacute; thể mua được&rdquo;, vị n&agrave;y khẳng định.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo S&agrave;i G&ograve;n Giải Ph&oacute;ng</strong></em></p>\r\n</div>\r\n', 'Địa ốc vừa “bung” vừa “lách”', 0),
(3, 1, '2013-01-16 17:00:00', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Năm 2013, thị trường bất động TP.HCM được kỳ vọng chuyển động tốt hơn. Trong đ&oacute;, ph&acirc;n kh&uacute;c sản phẩm c&oacute; mức gi&aacute; tốt, chủ đầu tư uy t&iacute;n v&agrave; đảm bảo tiến độ x&acirc;y dựng sẽ thu h&uacute;t mạnh kh&aacute;ch h&agrave;ng.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="240" src="http://saigonnhadat.vn/upload/UserFiles/image/11_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2013.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="300" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Kết quả nghi&ecirc;n cứu của CBRE cho thấy, xuất ph&aacute;t từ sự kh&oacute; khăn của thị trường, mặc d&ugrave; dự &aacute;n x&acirc;y dựng dở dang năm qua tăng l&ecirc;n đ&aacute;ng kể, nhưng c&aacute;c chủ dự &aacute;n đ&atilde; &eacute;m h&agrave;ng khiến cho nguồn cung dự &aacute;n được ch&agrave;o b&aacute;n ra thị trường giảm 72% so với năm 2011 với số lượng căn hộ thực tế ch&agrave;o b&aacute;n chỉ c&ograve;n khoảng gần 3.500 căn. Phần lớn c&aacute;c dự &aacute;n ch&agrave;o b&aacute;n trong năm 2012 l&agrave; những dự &aacute;n đ&atilde; từng ch&agrave;o b&aacute;n ra thị trường trước đ&acirc;y. C&aacute;c chủ đầu tư đ&atilde; l&agrave;m mới dự &aacute;n bằng c&aacute;ch chỉ định c&aacute;c s&agrave;n giao dịch ch&agrave;o b&aacute;n v&agrave; tiếp thị dự &aacute;n. Về gi&aacute; b&aacute;n, theo CBRE, hầu như giảm gi&aacute; tr&ecirc;n tất cả c&aacute;c ph&acirc;n kh&uacute;c, nhưng kh&ocirc;ng giảm trực tiếp, m&agrave; th&ocirc;ng qua c&aacute;c h&igrave;nh thức khuyến m&atilde;i bằng phiếu qu&agrave; tặng, v&eacute; đi du lịch, v&agrave;ng miếng hoặc chiếc khấu bằng tiền mặt nếu thanh to&aacute;n một lần hay trước hạn.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tương tự, cũng nhận định thị trường bất động sản năm 2012 c&oacute; nhiều kh&oacute; khăn. Tuy nhi&ecirc;n, theo ph&acirc;n t&iacute;ch từ Savills, c&agrave;ng về cuối năm 2012, d&ugrave; kh&oacute; khăn vẫn c&ograve;n trước mắt, song đ&agrave; suy giảm của thị trường đ&atilde; chững lại. Cụ thể, chỉ t&iacute;nh trong qu&yacute; IV/2012, thị trường đ&atilde; b&aacute;n khoảng 1.100 căn hộ, tỷ lệ hấp thụ đạt 8%, gần như kh&ocirc;ng thấp hơn qu&yacute; trước v&agrave; tăng nhẹ so với c&ugrave;ng kỳ năm 2011. Trong đ&oacute;, hơn 80% giao dịch sơ cấp thuộc căn hộ hạng C với mức gi&aacute; trung b&igrave;nh 19,8 triệu đồng/m2. &ldquo;Hiện Ng&acirc;n h&agrave;ng Nh&agrave; nước (NHNN) đ&atilde; giảm l&atilde;i suất trần 1%, xuống c&ograve;n 8%/năm. C&ugrave;ng với mức lạm ph&aacute;t một con số, đ&acirc;y l&agrave; những cơ hội tốt để NHNN c&oacute; thể giảm l&atilde;i suất cho vay trong tương lai gần. L&atilde;i suất vay thấp hơn sẽ t&aacute;c động tốt đến cả chủ đầu tư v&agrave; người mua tr&ecirc;n thị trường&rdquo;, &ocirc;ng Trương An Dương, Trưởng Bộ phận Nghi&ecirc;n cứu của Savills nhận định v&agrave; cho rằng, mặc d&ugrave; những kh&oacute; khăn của thị trường vẫn c&ograve;n trước mắt, song c&aacute;c đề xuất hỗ trợ từ Ch&iacute;nh phủ th&ocirc;ng qua c&aacute;c ch&iacute;nh s&aacute;ch mới trong đầu năm 2013, như giảm ph&iacute; sử dụng đất v&agrave; thuế tr&ecirc;n c&aacute;c giao dịch bất động sản, nới lỏng d&ograve;ng tiền, l&atilde;i suất hạ v&agrave; ưu đ&atilde;i cho người mua nh&agrave;... c&oacute; thể gi&uacute;p thị trường hồi phục v&agrave; kỳ vọng những kh&oacute; khăn của thị trường năm 2012 sẽ được kh&eacute;p lại.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ở một g&oacute;c độ kh&aacute;c, ghi nhận thực tế cho thấy, hiện c&oacute; kh&aacute; nhiều dự &aacute;n căn hộ ở TP.HCM đang được tung ra thị trường. Cuối tuần trước, C&ocirc;ng ty cổ phần S&agrave;n giao dịch bất động sản Hưng Thịnh (Hung Thinh Land) đ&atilde; mở b&aacute;n Dự &aacute;n căn hộ Carina Plaza (quận 8, TP.HCM) do C&ocirc;ng ty cổ phần Đầu tư Năm Bảy Bảy l&agrave;m chủ đầu tư; C&ocirc;ng ty Địa ốc Him Lam khai trương nh&agrave; mẫu v&agrave; b&aacute;n Dự &aacute;n căn hộ Him Lam Rivesie (quận 7). Theo kế hoạch, ng&agrave;y 19/1 tới, C&ocirc;ng ty Địa ốc Ho&agrave;ng Anh S&agrave;i G&ograve;n sẽ mở b&aacute;n căn hộ &Acirc;u Cơ Tower (quận T&acirc;n Ph&uacute;, TP.HCM) v&agrave; ng&agrave;y 21/1, C&ocirc;ng ty Hưng Thịnh sẽ mở b&aacute;n 104 căn hộ thuộc Dự &aacute;n căn hộ Metro Aparment do Saigon Corp l&agrave;m chủ đầu tư tại quận 2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo &ocirc;ng Nguyễn Duy Minh, Tổng gi&aacute;m đốc Hung Thinh Land, với sự quan t&acirc;m của Ch&iacute;nh phủ, c&aacute;c bộ, ng&agrave;nh đối với lĩnh vực bất động sản thời gian qua, thị trường &iacute;t nhiều đ&atilde; c&oacute; sự chuyển động tốt, t&igrave;nh h&igrave;nh giao dịch đ&atilde; tăng l&ecirc;n, nhiều người c&oacute; nhu cầu thực về nh&agrave; ở đang quan t&acirc;m v&agrave; chấp nhận bỏ tiền mua căn hộ để an cư trước Tết Nguy&ecirc;n đ&aacute;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo &ocirc;ng Minh, thị trường bất động sản năm 2013 sẽ c&oacute; sự ph&acirc;n h&oacute;a lớn. &ldquo;Kh&aacute;ch h&agrave;ng chỉ quan t&acirc;m tới sản phẩm của c&aacute;c chủ đầu tư c&oacute; uy t&iacute;n, c&oacute; đủ năng lực t&agrave;i ch&iacute;nh để x&acirc;y dựng dự &aacute;n đ&uacute;ng tiến độ. C&ograve;n với những dự &aacute;n, d&ugrave; c&oacute; gi&aacute; b&aacute;n tốt, nhưng kh&ocirc;ng chứng tỏ được năng lực t&agrave;i ch&iacute;nh, thiếu uy t&iacute;n sẽ kh&oacute; b&aacute;n h&agrave;ng&rdquo;, &ocirc;ng Minh nhận định.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo B&aacute;o Đầu Tư</strong></em></p>\r\n</div>\r\n', 'Thị trường bất động sản TP.HCM 2013: Sẽ phân hoá mạnh', 0);
INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(5, 1, '2013-01-07 14:49:38', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Nợ xấu c&ugrave;ng với tồn kho lớn l&agrave; hai t&aacute;c nh&acirc;n g&acirc;y n&ecirc;n t&igrave;nh trạng đ&oacute;ng băng của thị trường bất động sản. Điều kiện ti&ecirc;n quyết để ph&aacute; vỡ cục diện hiện tại, đưa thị trường phục hồi trong năm 2013 l&agrave; phải giải quyết nợ xấu.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="240" src="http://saigonnhadat.vn/upload/UserFiles/image/08_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2010.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="300" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Nợ xấu được giải quyết sẽ giải ph&oacute;ng doanh nghiệp bất động sản khỏi t&igrave;nh trạng &ldquo;mắc cạn&rdquo;. Ảnh: Đ.T</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Quyết giảm tỷ lệ nợ xấu bất động sản</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Việc th&agrave;nh lập C&ocirc;ng ty Mua b&aacute;n nợ xấu gần như đ&atilde; được &ldquo;đ&oacute;ng khung&rdquo; v&agrave; sẽ triển khai hoạt động trong năm 2013. Điều n&agrave;y cho thấy sự khẩn trương của Ch&iacute;nh phủ, Ng&acirc;n h&agrave;ng Nh&agrave; nước (NHNN) trong việc xử l&yacute; nợ xấu. Đối với thị trường bất động sản, th&ocirc;ng tin n&agrave;y l&agrave; một t&iacute;n hiệu t&iacute;ch cực, nhất l&agrave; khi hầu hết doanh nghiệp bất động sản đều đang &ldquo;mắc cạn&rdquo; bởi cục nợ xấu bất động sản l&ecirc;n tới hơn 28.000 tỷ đồng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong năm 2013, NHNN x&aacute;c định, xử l&yacute; nợ xấu l&agrave; một trong c&aacute;c nhiệm vụ rất quan trọng của hệ thống ng&acirc;n h&agrave;ng v&agrave; bằng c&aacute;c biện ph&aacute;p trực tiếp, c&oacute; thể giải quyết được 4-5% nợ xấu (trong tổng số khoảng 8%) của hệ thống ng&acirc;n h&agrave;ng. Cụ thể, NHNN đ&atilde; c&oacute; những giải ph&aacute;p để c&aacute;c ng&acirc;n h&agrave;ng tự giải quyết nợ xấu qua nguồn tr&iacute;ch lập dự ph&ograve;ng rủi ro, dự kiến trong năm 2013 c&oacute; thể giải quyết được 40.000 - 50.000 tỷ đồng nợ xấu. Đề &aacute;n th&agrave;nh lập C&ocirc;ng ty quản l&yacute; t&agrave;i sản quốc gia cũng đ&atilde; khởi động, khi vận h&agrave;nh c&oacute; thể giải quyết được khoảng tr&ecirc;n 100.000 tỷ đồng nợ xấu nữa.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Nguyễn Văn B&igrave;nh, Thống đốc NHNN cho biết, trong năm 2013, NHNN sẽ tập trung xử l&yacute; khoảng 100.000 - 150.000 tỷ đồng nợ trong bất động sản. Ngo&agrave;i ra, cơ quan n&agrave;y sẽ cung ứng từ 20.000 - 40.000 tỷ đồng cho c&aacute;c ng&acirc;n h&agrave;ng thương mại để cho vay mua nh&agrave; trong thời hạn 5 - 10 năm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo Bộ trưởng Bộ X&acirc;y dựng Trịnh Đ&igrave;nh Dũng, nợ xấu chiếm khoảng 13,5% tổng dư nợ bất động sản, nhưng đại đa số doanh nghiệp nợ bất động sản kh&oacute; c&oacute; khả năng thanh to&aacute;n khi đến hạn, do kh&ocirc;ng b&aacute;n được sản phẩm. V&igrave; vậy, việc tập trung phục hồi v&agrave; ph&aacute;t triển thị trường bất động sản bằng giải ph&aacute;p xử l&yacute; nợ xấu &iacute;t tốn k&eacute;m nhất, đồng thời bảo đảm lợi &iacute;ch của c&aacute;c b&ecirc;n li&ecirc;n quan.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">&ldquo;Hiến kế&rdquo;xử l&yacute; nợ xấu</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Để giải quyết nợ xấu, &ocirc;ng Trần Bắc H&agrave;, Chủ tịch HĐQT Ng&acirc;n h&agrave;ng TMCP Đầu tư v&agrave; Ph&aacute;t triển Việt Nam (BIDV) đề xuất, Ch&iacute;nh phủ cho ph&eacute;p c&aacute;c ng&acirc;n h&agrave;ng cơ cấu lại khoản nợ đối với c&aacute;c chủ đầu tư dự &aacute;n v&agrave; được giữ nguy&ecirc;n nh&oacute;m nợ như trước khi thực hiện cơ cấu. B&ecirc;n cạnh đ&oacute;, đề nghị Ch&iacute;nh phủ xem x&eacute;t chấp thuận việc th&agrave;nh lập C&ocirc;ng ty t&agrave;i ch&iacute;nh t&aacute;i cho vay thế chấp nh&agrave; ở quốc gia. Hoạt động của c&ocirc;ng ty n&agrave;y sẽ g&oacute;p phần tạo ra nguồn vốn trung v&agrave; d&agrave;i hạn cho thị trường, giải quyết tồn kho bất động sản v&agrave; nợ xấu cho c&aacute;c ng&acirc;n h&agrave;ng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Một giải ph&aacute;p kh&aacute;c được &ocirc;ng L&ecirc; Hữu Nghĩa, Gi&aacute;m đốc C&ocirc;ng ty L&ecirc; Th&agrave;nh đưa ra l&agrave;, bơm tiền c&oacute; kiểm so&aacute;t cho thị trường bất động sản. G&oacute;i k&iacute;ch cầu n&agrave;y sẽ c&oacute; l&atilde;i suất thấp, &aacute;p dụng cho người mua nh&agrave; gi&aacute; rẻ được miễn thuế gi&aacute; trị gia tăng, được vay tối đa 50% gi&aacute; trị hợp đồng. Khi b&aacute;n được h&agrave;ng, doanh nghiệp sẽ c&oacute; tiền trả cho nh&agrave; thầu. Nh&agrave; cung ứng vật tư cũng được thanh to&aacute;n nợ đọng. &ldquo;Ba nh&agrave;&rdquo; n&agrave;y cũng trả l&atilde;i v&agrave; một phần nợ gốc cho ng&acirc;n h&agrave;ng, h&agrave;ng tồn kho v&agrave; nợ xấu sẽ được xử l&yacute; dần.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Hiệp hội C&aacute;c nh&agrave; đầu tư t&agrave;i ch&iacute;nh (VAFI) n&ecirc;u kiến nghị, NHNN n&ecirc;n hạ l&atilde;i suất tiền gửi ngoại tệ xuống mức 1%/năm, sau đ&oacute; l&agrave; 0%/năm, thay v&igrave; 2%/năm như hiện tại, đồng thời &aacute;p thuế gi&aacute; trị gia tăng cho hoạt động mua v&agrave;ng miếng v&agrave; v&agrave;ng trang sức ở mức 10% tr&ecirc;n gi&aacute; trị mua. Qua đ&oacute;, sẽ hạ được l&atilde;i suất cho vay xuống dưới mức 10%/năm, l&atilde;i suất cho vay ngắn hạn chỉ c&ograve;n khoảng 8%/năm, tức l&agrave; l&atilde;i suất huy động VND sẽ ở mức 5%/năm v&agrave;o thời điểm trước ng&agrave;y 30/6/2013. Những giải ph&aacute;p n&agrave;y kh&ocirc;ng chỉ gi&uacute;p nhanh ch&oacute;ng ph&aacute; băng thị trường bất động sản, m&agrave; c&ograve;n hỗ trợ tiến tr&igrave;nh xử l&yacute; nợ xấu v&agrave; nhanh ch&oacute;ng phục hồi nền kinh tế.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo TS. Alan Phạm, Kinh tế gia trưởng của VinaCapital, để giảm nợ xấu bất động sản, đầu ti&ecirc;n, Ch&iacute;nh phủ cần lập C&ocirc;ng ty Mua b&aacute;n nợ xấu quốc gia (AMC) v&agrave; tạo điều kiện để người d&acirc;n c&oacute; cơ hội sở hữu nh&agrave;. C&oacute; thể mức vốn ban đầu của c&ocirc;ng ty n&agrave;y l&agrave; 100.000 tỷ đồng, c&oacute; sự tham gia của nh&agrave; đầu tư tư nh&acirc;n quan t&acirc;m đến t&agrave;i sản giảm gi&aacute;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">C&aacute;c bất động sản thế chấp để vay nợ từ ng&acirc;n h&agrave;ng được đưa ra đấu gi&aacute; c&ocirc;ng khai để x&aacute;c định gi&aacute; thị trường. Tiền mua bất động sản đấu gi&aacute; sẽ bao gồm 30% vốn tự c&oacute; của người mua v&agrave; 70% vốn vay l&atilde;i suất thấp từ AMC. Nh&agrave; đầu tư sau đ&oacute; c&oacute; thể b&aacute;n t&agrave;i sản n&agrave;y tr&ecirc;n thị trường, t&igrave;m kiếm lợi nhuận v&agrave; ho&agrave;n trả c&aacute;c khoản vay cho AMC.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo &ocirc;ng Nguyễn Đức Trung, Ph&oacute; viện trưởng Viện Nghi&ecirc;n cứu khoa học ng&acirc;n h&agrave;ng (Học viện Ng&acirc;n h&agrave;ng), 80% tổng số nợ xấu c&oacute; t&agrave;i sản đảm bảo, trong đ&oacute; 57% được đảm bảo bằng bất động sản. Theo đ&oacute;, nợ xấu c&oacute; thể được xử l&yacute; theo 3 kịch bản.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trước hết, nếu nền kinh tế phục hồi nhanh, việc xử l&yacute; t&agrave;i sản đảm bảo trở n&ecirc;n nhanh ch&oacute;ng. Năm 2013 khi lạm ph&aacute;t được kiềm chế, l&atilde;i suất sẽ c&oacute; xu hướng giảm th&igrave; thị trường bất động sản sẽ khởi sắc trở lại.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Kịch bản thứ 2 l&agrave; nền kinh tế được phục hồi dần dần th&igrave; mức độ thu hồi gi&aacute; trị t&agrave;i sản đảm bảo đạt mức trung b&igrave;nh h&ograve;a vốn khoảng 50%. Trong trường hợp n&agrave;y, với mức nợ xấu l&agrave; 8,82% th&igrave; ho&agrave;n to&agrave;n xử l&yacute; được, c&ograve;n với trường hợp nợ xấu l&agrave; 15% th&igrave; khoản nợ xấu c&ograve;n lại chưa c&oacute; nguồn xử l&yacute; l&agrave; 75.920 tỷ đồng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Kịch bản thứ 3 l&agrave;, khi nền kinh tế chưa tho&aacute;t khỏi t&igrave;nh trạng suy tho&aacute;i, việc ph&aacute;t mại t&agrave;i sản đảm bảo v&ocirc; c&ugrave;ng kh&oacute; khăn, khiến gi&aacute; bất động sản v&agrave; t&agrave;i sản kh&aacute;c giảm s&acirc;u th&ecirc;m. Khi đ&oacute;, mức độ thu hồi gi&aacute; trị t&agrave;i sản đảm bảo sẽ v&agrave;o khoảng 30%.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Như vậy, việc xử l&yacute; nợ xấu n&oacute;i chung v&agrave; nợ xấu bất động sản n&oacute;i ri&ecirc;ng sẽ l&agrave; trọng t&acirc;m h&agrave;nh động trong năm 2013. Tất nhi&ecirc;n, để xử l&yacute; nợ xấu bất động sản, cần phải &aacute;p dụng đồng bộ nhiều giải ph&aacute;p kh&aacute;c. Nhưng d&ugrave; sao, đ&acirc;y cũng l&agrave; một t&iacute;n hiệu t&iacute;ch cực cho thị trường trong giai đoạn tới.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo B&aacute;o Đầu Tư</strong></em></p>\r\n</div>\r\n', 'Thị trường BĐS 2013: Trọng tâm là rã băng nợ xấu', 0),
(6, 1, '2013-01-07 17:00:00', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Thị trường bất động sản TP. HCM đang c&oacute; sự ph&acirc;n h&oacute;a theo khu vực. Trong khi ph&iacute;a T&acirc;y v&agrave; Nam c&oacute; vẻ vẫn c&ograve;n đang &ldquo;ngủ đ&ocirc;ng&rdquo;, th&igrave; ph&iacute;a Đ&ocirc;ng lại đắt kh&aacute;ch.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Xuất hiện mặt bằng gi&aacute; mới</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Khu Đ&ocirc;ng TP. HCM gồm c&aacute;c quận 2, quận 9 v&agrave; Thủ Đức. Trong đ&oacute;, quận 2 được xem l&agrave; t&acirc;m điểm thị trường của khu vực n&agrave;y với h&agrave;ng loạt dự &aacute;n bất động sản được đầu tư. Chỉ t&iacute;nh ri&ecirc;ng khu vực An Ph&uacute; - An Kh&aacute;nh c&oacute; quy m&ocirc; gần 90 h&eacute;c-ta đ&atilde; c&oacute; h&agrave;ng loạt dự &aacute;n căn hộ được h&igrave;nh th&agrave;nh như Estela, Cantavil An Ph&uacute; - Cantavil Premier, Imperi An Ph&uacute;, An Khang, An Cư&hellip; Ngo&agrave;i ra, gần khu vực n&agrave;y cũng c&oacute; nhiều dự &aacute;n cũng đ&atilde; v&agrave; đang được h&igrave;nh th&agrave;nh như Dự &aacute;n căn hộ cao cấp 38,4 h&eacute;c-ta B&igrave;nh Kh&aacute;nh, Dự &aacute;n Khu 30,2 h&eacute;c-ta B&igrave;nh Kh&aacute;nh do C&ocirc;ng ty TNHH Ph&aacute;t triển quốc tế Thế kỷ 21 đầu tư v&agrave; Dự &aacute;n ở Khu căn hộ 17,3 h&eacute;c-ta B&igrave;nh Kh&aacute;nh do C&ocirc;ng ty TNHH Nam Rạch Chiếc đầu tư&hellip;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="281" src="http://saigonnhadat.vn/upload/UserFiles/image/08_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2019.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Bất động sản khu Đ&ocirc;ng TP. HCM s&ocirc;i động nhờ gi&aacute; hấp dẫn v&agrave; cơ sở hạ tầng ph&aacute;t triển</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Qua ghi nhận của ph&oacute;ng vi&ecirc;n ĐTCK, hiện khu vực n&agrave;y đang được x&aacute;c lập một bằng gi&aacute; mới. Nếu như ở khu vực An Ph&uacute; - An Kh&aacute;nh, phần lớn dự &aacute;n căn hộ c&oacute; mức gi&aacute; b&aacute;n tương đối cao, như Imperi An Ph&uacute; c&oacute; mức gi&aacute; trung b&igrave;nh 28,5 triệu đồng/m2, Cantavil 33 triệu đồng/m2, An Khang 23,2 triệu đồng/m2..., th&igrave; một số dự &aacute;n kh&aacute;c trong khu vực như Metro Aparment do Saigon Corp l&agrave;m chủ đầu tư v&agrave; C&ocirc;ng ty Hưng Thịnh độc quyền ph&acirc;n phối c&oacute; gi&aacute; trung b&igrave;nh khoảng 17 triệu đồng/m2, Dự &aacute;n An Lộc 19 triệu đồng/m2, An Thịnh 19 triệu đồng/m2 v&agrave; Dự &aacute;n PertroVietnam Landmark c&oacute; gi&aacute; 19,9 triệu đồng/m2....</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ghi nhận của ch&uacute;ng t&ocirc;i từ c&aacute;c s&agrave;n giao dịch bất động sản cho thấy, thời gian gần đ&acirc;y, d&ugrave; kh&ocirc;ng c&oacute; cảnh tranh mua, tranh b&aacute;n, song với t&igrave;nh h&igrave;nh chung của thị trường, mức độ giao dịch th&agrave;nh c&ocirc;ng tại c&aacute;c dự &aacute;n khu vực quận 9, quận 2 đ&atilde; tăng l&ecirc;n đ&aacute;ng kể.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Gi&aacute;m đốc một s&agrave;n giao dịch bất động sản quận 2 cho biết, nguy&ecirc;n nh&acirc;n khu vực n&agrave;y nhận được sự quan t&acirc;m của kh&aacute;ch h&agrave;ng l&agrave; xuất ph&aacute;t từ yếu tố gi&aacute; b&aacute;n của nhiều dự &aacute;n kh&aacute; hợp với t&uacute;i tiền, c&ugrave;ng với việc h&agrave;ng loạt c&ocirc;ng tr&igrave;nh tầng của khu Đ&ocirc;ng đ&atilde; v&agrave; đang được h&igrave;nh th&agrave;nh, đang l&agrave;m thay đổi diện mạo của khu vực n&agrave;y.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Đ&oacute;n đầu tiềm năng</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Hiện nay, CTCP Đầu tư hạ tầng kỹ thuật TP. HCM, chủ đầu tư Dự &aacute;n cầu S&agrave;i G&ograve;n 2 đang tăng tốc đầu tư để kịp đưa dự &aacute;n v&agrave;o sử dụng trong năm 2013. Đ&acirc;y l&agrave; c&ocirc;ng tr&igrave;nh quan trọng kết nối với Dự &aacute;n Xa lộ H&agrave; Nội mở rộng th&agrave;nh 16 l&agrave;n xe, nối với cầu Đồng Nai. Như vậy, ngo&agrave;i cầu S&agrave;i G&ograve;n 2, hiện TP. HCM đ&atilde; c&oacute; 3 c&ocirc;ng tr&igrave;nh &ldquo;vượt s&ocirc;ng&rdquo; tầm cỡ kh&aacute;c đ&atilde; được đưa v&agrave;o sử dụng gồm đường hầm Thủ Thi&ecirc;m, cầu Thủ Thi&ecirc;m v&agrave; cầu Ph&uacute; Mỹ. Điều đ&aacute;ng n&oacute;i, tất cả những c&ocirc;ng tr&igrave;nh n&agrave;y đều kết nối với quận 2, mở ra một hướng lưu th&ocirc;ng mới cho to&agrave;n khu Đ&ocirc;ng của Th&agrave;nh phố.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo &ocirc;ng Nguyễn Xu&acirc;n Lộc, Tổng gi&aacute;m đốc C&ocirc;ng ty Địa ốc Techcom Real, nh&igrave;n một c&aacute;ch tổng thể, thị trường nh&agrave; đất ở c&aacute;c quận nội th&agrave;nh của TP. HCM như B&igrave;nh Thạnh, Ph&uacute; Nhuận, Khu Nam S&agrave;i G&ograve;n&hellip; đ&atilde; c&oacute; mức gi&aacute; kh&aacute; cao v&agrave; đang b&atilde;o h&ograve;a. Trong khi đ&oacute;, khu vực ph&iacute;a Đ&ocirc;ng, gi&aacute; nh&agrave; đất c&ograve;n đang ở mức vừa phải v&agrave; đặc biệt l&agrave; c&oacute; tiềm năng ph&aacute;t triển rất tốt nhờ v&agrave;o ch&iacute;nh s&aacute;ch ph&aacute;t triển hạ tầng của khu vực n&agrave;y.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&ldquo;Việc gần đ&acirc;y, nhiều nh&agrave; đầu tư quan t&acirc;m thị trường bất động sản quận 2 n&oacute;i ri&ecirc;ng v&agrave; khu Đ&ocirc;ng n&oacute;i chung l&agrave; v&igrave;, ngo&agrave;i mục đ&iacute;ch an cư, họ c&ograve;n &lsquo;ngắm&rsquo; v&agrave;o tiềm năng ph&aacute;t triển c&ograve;n lớn của khu vực n&agrave;y&rdquo;, &ocirc;ng Lộc n&oacute;i v&agrave; ph&acirc;n t&iacute;ch, hiện c&aacute;c tuyến đường quan trọng nối quận 1 v&agrave; quận 2 đ&atilde; được đưa v&agrave;o sử dụng như hầm Thủ Thi&ecirc;m, cầu Thủ Thi&ecirc;m, cầu Ph&uacute; Mỹ, một số c&ocirc;ng tr&igrave;nh hạ tầng kh&aacute;c cũng đang h&igrave;nh th&agrave;nh như Đại lộ V&otilde; Văn Kiệt, đường Li&ecirc;n tỉnh lộ 25B nối quận 2 v&agrave; quận 1 sắp ho&agrave;n tất, cầu S&agrave;i G&ograve;n dự kiến ho&agrave;n th&agrave;nh năm 2013, tuyến metro Bến Th&agrave;nh - Suối Ti&ecirc;n đang được x&acirc;y dựng... Rồi quận 2 được quy hoạch sẽ l&agrave; trung t&acirc;m t&agrave;i ch&iacute;nh - thương mại - dịch vụ mang tầm quốc tế được v&iacute; như phố Đ&ocirc;ng của Thượng Hải... đang mở ra hướng ph&aacute;t triển mạnh, hứa hẹn gi&aacute; trị bất động sản của khu vực n&agrave;y tăng theo rất nhiều.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo Đầu Tư Chứng Kho&aacute;n</strong></em></p>\r\n</div>\r\n', 'Bất động sản TP. HCM: Sức hút từ khu Đông', 0);
INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(7, 1, '2013-01-07 14:49:44', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Sau khi đề &aacute;n ph&aacute;t triển tổng thể đảo Ph&uacute; Quốc được Ch&iacute;nh phủ ph&ecirc; duyệt v&agrave; S&acirc;n bay quốc tế Ph&uacute; Quốc được khai trương, gi&aacute; đất tại đ&acirc;y đ&atilde; tăng đột biến.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Tạo mọi điều kiện để ph&aacute;t triển Ph&uacute; Quốc</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ng&agrave;y 15/12/2012, sau 4 năm x&acirc;y dựng, Tổng c&ocirc;ng ty Cảng h&agrave;ng kh&ocirc;ng Việt Nam (ACV) đ&atilde; ch&iacute;nh thức tổ chức lễ khai trương Cảng h&agrave;ng kh&ocirc;ng quốc tế Ph&uacute; Quốc tại x&atilde; Dương Tơ. Đ&acirc;y l&agrave; s&acirc;n bay quốc tế đầu ti&ecirc;n được x&acirc;y mới kể từ ng&agrave;y giải ph&oacute;ng đất nước, c&aacute;c s&acirc;n bay quốc tế kh&aacute;c đều do cải tạo v&agrave; n&acirc;ng cấp.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ngay sau khi S&acirc;n bay Quốc tế Ph&uacute; Quốc vừa đi v&agrave;o hoạt động, đường bay thẳng H&agrave; Nội - Ph&uacute; Quốc cũng được khai th&aacute;c ngay để phục vụ nhu cầu đến Ph&uacute; Quốc của nhiều du kh&aacute;ch từ c&aacute;c tỉnh ph&iacute;a Bắc. Đ&acirc;y l&agrave; đường bay nội địa d&agrave;i nhất Việt Nam với thời gian bay dự kiến khoảng 2 giờ 25 ph&uacute;t.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tại lễ khai trương, Thủ tướng Ch&iacute;nh phủ Nguyễn Tấn Dũng cho biết: &quot;Ch&iacute;nh phủ sẽ hết sức ủng hộ, tạo mọi điều kiện thuận lợi để ph&aacute;t triển Ph&uacute; Quốc. S&acirc;n bay Ph&uacute; Quốc l&agrave; động lực rất quan trọng th&uacute;c đẩy tiến tr&igrave;nh h&igrave;nh th&agrave;nh đặc khu kinh tế Ph&uacute; Quốc ph&aacute;t triển nhanh, g&oacute;p phần ph&aacute;t triển kinh tế x&atilde; hội của Ph&uacute; Quốc, tỉnh Ki&ecirc;n Giang v&agrave; cả v&ugrave;ng&rdquo;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="294" src="http://saigonnhadat.vn/upload/UserFiles/image/09_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%203.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">S&acirc;n bay quốc tế Ph&uacute; Quốc được kh&aacute;nh th&agrave;nh ng&agrave;y 15/12/2012</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Thời cơ cho du lịch, bất động sản</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Mặc d&ugrave;, s&acirc;n bay mới với c&ocirc;ng suất lớn hơn s&acirc;n bay cũ gấp nhiều lần (2,65 triệu h&agrave;nh kh&aacute;ch/năm) phải hoạt động li&ecirc;n tục với tần suất, lịch bay d&agrave;y đặc, nhưng vẫn kh&ocirc;ng đủ phục vụ nhu cầu của một lượng lớn du kh&aacute;ch cả trong v&agrave; ngo&agrave;i nước đến Ph&uacute; Quốc. Đặc biệt l&agrave; thời điểm n&agrave;y, khi đang v&agrave;o m&ugrave;a cao điểm về du lịch lễ tết, lượng kh&aacute;ch ng&agrave;y c&agrave;ng tăng cao, n&ecirc;n t&igrave;nh trạng khan hiếm v&eacute; diễn ra thường xuy&ecirc;n hơn.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Do chưa c&oacute; nhiều kh&aacute;ch sạn, resort, n&ecirc;n v&agrave;o c&aacute;c dịp lễ tết, du kh&aacute;ch đến Ph&uacute; Quốc thường c&oacute; xu hướng t&igrave;m nh&agrave; nghỉ hoặc nh&agrave; d&acirc;n để lưu tr&uacute;, một &ldquo;phong c&aacute;ch&rdquo; du lịch rất ri&ecirc;ng của Ph&uacute; Quốc. Người d&acirc;n thường sử dụng ch&iacute;nh ng&ocirc;i nh&agrave; của m&igrave;nh để l&agrave;m địa điểm du lịch, l&agrave; nơi lưu tr&uacute; hoặc cung cấp c&aacute;ch dịch vụ thiết yếu cho du kh&aacute;ch. Kh&aacute;ch du lịch c&oacute; thể ở nh&agrave; d&acirc;n, ăn cơm với người d&acirc;n, mua sản phẩm do d&acirc;n l&agrave;m ra v&agrave; t&igrave;m hiểu văn h&oacute;a truyền thống nơi n&agrave;y&hellip;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Thực tế, với những bước chuyển m&igrave;nh của Ph&uacute; Quốc th&igrave; kh&ocirc;ng phải b&acirc;y giờ người d&acirc;n tại h&ograve;n đảo xinh đẹp n&agrave;y mới cảm nhận được. Thay v&igrave; sống chủ yếu bằng nghề đi biển, l&agrave;m n&ocirc;ng như trước đ&acirc;y, hầu hết cư d&acirc;n của h&ograve;n đảo n&agrave;y đều ph&aacute;t triển kinh tế nhờ v&agrave;o du lịch với nhiều dịch vụ đi k&egrave;m, n&ecirc;n đời sống người d&acirc;n ng&agrave;y c&agrave;ng tăng l&ecirc;n r&otilde; rệt.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ngo&agrave;i du lịch, bất động sản tại Ph&uacute; Quốc cũng c&oacute; những bước chuyển m&igrave;nh mạnh mẽ. Từ một v&ugrave;ng đất hoang sơ, sau khi được Ch&iacute;nh phủ ch&uacute; trọng đầu tư, nhiều c&ocirc;ng tr&igrave;nh hạ tầng lớn đang dần ho&agrave;n thiện, c&aacute;c nh&agrave; đầu tư trong v&agrave; ngo&agrave;i nước nhận thấy được tiềm năng lớn của h&ograve;n đảo xinh đẹp n&agrave;y, cũng dồn về đ&acirc;y để đầu tư, c&ugrave;ng với lượng kh&aacute;ch du lịch ng&agrave;y c&agrave;ng tăng đ&atilde; l&agrave;m cho bất động sản Ph&uacute; Quốc biến động kh&ocirc;ng ngừng. Đất đai sốt l&ecirc;n từng giờ, từng ng&agrave;y. Những mảnh vườn ng&agrave;y n&agrave;o lặng lẽ, giờ đ&acirc;y lu&ocirc;n được c&aacute;c nh&agrave; đầu tư săn đ&oacute;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="253" src="http://saigonnhadat.vn/upload/UserFiles/image/09_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%204.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Bất động sản Ph&uacute; Quốc đang &quot;n&oacute;ng l&ecirc;n&quot; từng ng&agrave;y</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Sau khi Đề &aacute;n &ldquo;Ph&aacute;t triển tổng thể đảo Ph&uacute; Quốc, tỉnh Ki&ecirc;n Giang đến năm 2010 v&agrave; tầm nh&igrave;n đến năm 2020&rdquo; được Ch&iacute;nh phủ ph&ecirc; duyệt, trong đ&oacute; quy hoạch Khu đ&ocirc; thị Dương Đ&ocirc;ng l&agrave; trung t&acirc;m h&agrave;nh ch&iacute;nh, dịch vụ thương mại v&agrave; t&agrave;i ch&iacute;nh quốc tế, Khu đ&ocirc; thị Dương Tơ l&agrave; trung t&acirc;m kinh tế, du lịch sinh th&aacute;i, nghỉ dưỡng, giải tr&iacute; cao cấp tạo nguồn thu phục vụ cho đầu tư ph&aacute;t triển đảo. Ri&ecirc;ng khu B&atilde;i Trường, thuộc Dương Tơ được quy hoạch để trở th&agrave;nh trung t&acirc;m du lịch tầm cỡ quốc tế, l&agrave; đ&ograve;n bẩy cho sự ph&aacute;t triển to&agrave;n đảo. Đặc biệt l&agrave; sự kiện kh&aacute;nh th&agrave;nh S&acirc;n bay Quốc tế Ph&uacute; Quốc v&agrave;o ng&agrave;y 15/12/2012, đ&atilde; l&agrave;m cho gi&aacute; đất tại khu vực n&agrave;y tăng đột biến. Nhất l&agrave; trong những ng&agrave;y gần đ&acirc;y, người d&acirc;n c&oacute; đất gần khu vực s&acirc;n bay đang x&ocirc;n xao v&igrave; những lời đề nghị mua đất hấp dẫn từ những tay &ldquo;c&ograve; đất&quot; lẻ, cho đến c&aacute;c nh&agrave; đầu tư chuy&ecirc;n nghiệp.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong khi thị trường bất động sản cả nước n&oacute;i chung hiện nay đang ảm đạm, nhiều dự &aacute;n phải giảm gi&aacute;, b&aacute;n lỗ để vớt v&aacute;t cuối năm, th&igrave; gi&aacute; đất tại Ph&uacute; Quốc lại đang sốt l&ecirc;n từng giờ, từng ng&agrave;y. Điều đ&oacute; cho thấy, Ph&uacute; Quốc sẽ l&agrave; điểm đến hấp dẫn cho c&aacute;c nh&agrave; đầu tư c&oacute; tầm nh&igrave;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo Đầu Tư Chứng Kho&aacute;n</strong></em></p>\r\n</div>\r\n', 'Thời cơ cho bất động sản Phú Quốc', 0),
(8, 1, '2013-01-07 14:49:45', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Khảo s&aacute;t của CBRE Việt Nam về thị trường văn ph&ograve;ng cho thu&ecirc; tại Tp.HCM trong qu&yacute; 4/2012 cho thấy nhiều dấu hiệu t&iacute;ch cực sau một thời gian d&agrave;i trầm lắng.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="281" src="http://saigonnhadat.vn/upload/UserFiles/image/09_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2012.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Gi&aacute; thu&ecirc; trung b&igrave;nh cho cả hai thị trường A v&agrave; B tăng 1,5%, đạt 21,2 USD/m2/th&aacute;ng.</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo đ&oacute;, gi&aacute; thu&ecirc; văn ph&ograve;ng tăng nhẹ tr&ecirc;n cả hai ph&acirc;n kh&uacute;c hạng A v&agrave; hạng B, trong khi lượng diện t&iacute;ch trống đang dần được lấp đầy. Thị trường tiếp tục c&oacute; những dấu hiệu chạm đ&aacute;y như dự đo&aacute;n trong qu&yacute; trước, khi m&agrave; gi&aacute; thu&ecirc; trung b&igrave;nh cho cả hai thị trường A v&agrave; B tăng 1,5%, đạt 21,2 USD/m2/th&aacute;ng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Đ&aacute;ng ch&uacute; &yacute;, c&aacute;c t&ograve;a nh&agrave; hạng A l&acirc;u năm vẫn giữ gi&aacute; ổn định trong khi c&aacute;c t&ograve;a nh&agrave; mới hơn c&oacute; khuynh hướng tăng gi&aacute; thu&ecirc; khi diện t&iacute;ch trống dần được lấp đầy. Gi&aacute; thu&ecirc; ph&acirc;n kh&uacute;c hạng B tiếp tục tăng nhẹ, tuy nhi&ecirc;n với một tốc độ chậm dần (khoảng 0,8%, thấp hơn nhiều so với con số 1,4% được ghi nhận trong qu&yacute; 3/2012) do ảnh hưởng của nguồn cung mới trong qu&yacute;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nguồn cung đến từ c&aacute;c c&ocirc;ng ty mới gia nhập thị trường, mở rộng hoặc dịch chuyển văn ph&ograve;ng. C&aacute;c c&ocirc;ng ty dược, luật v&agrave; dầu kh&iacute; tiếp tục sở hữu nguồn ng&acirc;n s&aacute;ch dồi d&agrave;o v&agrave; đ&oacute;ng một vai tr&ograve; quan trọng trong nguồn cung của thị trường.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">B&ecirc;n cạnh đ&oacute;, việc s&aacute;p nhập c&aacute;c văn ph&ograve;ng ri&ecirc;ng biệt về một vị tr&iacute; đang l&agrave; xu hướng đ&aacute;ng ch&uacute; &yacute; trong những qu&yacute; gần đ&acirc;y. Nguồn cung mới đến dần dần v&agrave; thị trường vẫn đang ổn định.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo khảo s&aacute;t của CBRE, nguồn cung mới trong qu&yacute; 4/2012 v&agrave; qu&yacute; 1/2013 đến từ c&aacute;c dự &aacute;n chậm tiến độ (Empress Tower, Times Square v&agrave; President Place) hơn l&agrave; từ c&aacute;c dự &aacute;n được vốn được mong đợi sẽ đi v&agrave;o hoạt động trong thời điểm n&agrave;y. Qu&yacute; 4/2012 kh&aacute; ổn định với lượng diện t&iacute;ch trống giảm dần, lựa chọn về diện t&iacute;ch v&agrave; vị tr&iacute; cho kh&aacute;ch thu&ecirc; kh&ocirc;ng c&ograve;n nhiều.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Dự kiến t&igrave;nh h&igrave;nh n&agrave;y sẽ tiếp diễn trong qu&yacute; 1/2013 khi c&aacute;c t&ograve;a nh&agrave; Empress Tower, Times Square v&agrave; President Place d&ugrave; chưa ch&iacute;nh thức khai trương nhưng đ&atilde; được ch&agrave;o thu&ecirc; v&agrave; một phần diện t&iacute;ch đ&atilde; t&igrave;m được kh&aacute;ch thu&ecirc;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ngo&agrave;i ra, c&aacute;c t&ograve;a nh&agrave; kh&aacute;c dự kiến sẽ sớm đi v&agrave;o hoạt động trong năm 2013 gồm Saigon Airport Plaza, Lim Tower v&agrave; Le Meridien.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ở mảng thị trường b&aacute;n lẻ cũng c&oacute; nhiều t&iacute;n hiệu khả quan hơn. Nguồn cung mới trong qu&yacute; 4/2012 đến từ Vincom Center A v&agrave; Pandora City với tổng diện t&iacute;ch s&agrave;n ghi nhận l&agrave; 78.000 m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Với lợi thế nằm ở vị tr&iacute;, dự &aacute;n Vincom Center A ch&agrave;o thu&ecirc; tầng trệt v&agrave; tầng một cao hơn 10% mặt bằng chung, dẫn tới gi&aacute; thu&ecirc; trung b&igrave;nh của trung t&acirc;m thương mại - khu trung t&acirc;m tăng 2,6% so với qu&yacute; trước.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong khi đ&oacute;, t&ograve;a Pandora City cũng đạt được sự th&agrave;nh c&ocirc;ng đ&aacute;ng kể với 80% diện t&iacute;ch cam kết lấp đầy v&agrave; thu h&uacute;t được những kh&aacute;ch thu&ecirc; như Big C, Payless, Megastar, v&agrave; si&ecirc;u thị điện m&aacute;y Nguyễn Kim.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo CBRE, mặc d&ugrave; tổng nguồn cung tăng, song tỷ lệ trống cũng như gi&aacute; ch&agrave;o thu&ecirc; của trung t&acirc;m thương mại vẫn kh&ocirc;ng đổi theo qu&yacute;, đ&acirc;y l&agrave; một bằng chứng về nguồn cung hiện hữu cho c&aacute;c vị tr&iacute; v&agrave; dự &aacute;n được định vị tốt.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo VnEconomy</strong></em></p>\r\n</div>\r\n', '“Giá thuê văn phòng tại Tp.HCM tăng trở lại”', 0);
INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(9, 1, '2013-01-07 14:49:47', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Thị trường bất động sản đang thuộc về người mua, bất động sản đ&atilde; v&agrave; đang khẳng định ch&acirc;n l&yacute; &ldquo;chất lượng quyết định gi&aacute; trị&rdquo; ngay cả khi thị trường trầm lắng nhất.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Khuyến m&atilde;i rầm rộ</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Xu thế chung của thị trường bất động sản 2012 l&agrave; những đợt giảm gi&aacute;, chiết khấu, khuyến m&atilde;i của nhiều dự &aacute;n khi tung h&agrave;ng ra thị trường. Như th&ocirc;ng lệ, thời điểm cuối năm l&agrave; l&uacute;c chủ dự &aacute;n tung ra mạnh nhất với nhiều h&igrave;nh thức phổ biến như giảm gi&aacute;, chiết khấu, tặng nội thất, hỗ trợ l&atilde;i suất,&hellip;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Thị trường H&agrave; Nội gần đ&acirc;y đ&oacute;n nhiều th&ocirc;ng tin giảm gi&aacute; của nhiều dự &aacute;n căn hộ. Đơn cử như cuối th&aacute;ng 11 H&agrave; Đ&ocirc; mở b&aacute;n đợt 3 căn hộ H&agrave; Đ&ocirc; Park View với gi&aacute; 22,7 triệu đồng/m2 (chưa bao gồm VAT, nội thất ho&agrave;n thiện). So với gi&aacute; 26,3 triệu đồng/m2 trong đợt mở b&aacute;n trước đ&oacute; v&agrave;o ng&agrave;y 19/10, gi&aacute; của dự &aacute;n n&agrave;y đ&atilde; giảm gần 14%.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">S&agrave;n giao dịch bất động sản Coma 18cũng vừa ch&agrave;o b&aacute;n với mức gi&aacute; 20,2 triệu đồng/m2, kh&aacute;ch h&agrave;ng sẽ được chiết khấu 8% khi mua căn hộ v&agrave; được ng&acirc;n h&agrave;ng BIDV hỗ trợ cho vay với l&atilde;i suất ưu đ&atilde;i. 400 căn hộ Ph&uacute;c Thịnh Tower tại khu đ&ocirc; thị T&acirc;n T&acirc;y Đ&ocirc;, Ho&agrave;i Đức, HN cũng vừa được b&aacute;n với gi&aacute; từ 720 triệu đồng/căn hơn 53m2, hỗ trợ l&atilde;i suất vay 3% trong thời gian 12 th&aacute;ng cho kh&aacute;ch h&agrave;ng, một số s&agrave;n BĐS chiết khấu cho kh&aacute;ch 9%.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ở ph&acirc;n kh&uacute;c nh&agrave; thu nhập thấp, C&ocirc;ng ty cổ phần X&acirc;y dựng số 3 H&agrave; Nội cũng cho biết, từ 1/12, C&ocirc;ng ty tiếp tục nhận hồ sơ đăng k&yacute; mua nh&agrave; thu nhập thấp đợt 4 tại khu đ&ocirc; thị mới S&agrave;i Đồng (quận Long Bi&ecirc;n, H&agrave; Nội) với mức gi&aacute; b&aacute;n từ 10,6 đến 13 triệu đồng/1m2 (đ&atilde; bao gồm VAT).</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tại thị trường TP.HCM, ng&agrave;y 9/11, C&ocirc;ng ty Cổ phần Ph&aacute;t triển nh&agrave; B&agrave; Rịa &ndash; Vũng T&agrave;u (HODECO) ch&iacute;nh thức ch&agrave;o b&aacute;n đợt I dự &aacute;n Khu biệt thự Đồi Ngọc Tước II với gi&aacute; từ 14,3 triệu/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">D&ugrave; kh&ocirc;ng th&ocirc;ng b&aacute;o gi&aacute; b&aacute;n nhưng chủ đầu tư đ&atilde; đưa ra phương thức thanh to&aacute;n kh&aacute; linh hoạt, đ&oacute; l&agrave; kh&aacute;ch h&agrave;ng c&oacute; thể nhận nền ngay khi thanh to&aacute;n 39 triệu đồng v&agrave; số tiền c&ograve;n l&atilde;i sẽ được thanh to&aacute;n trong v&ograve;ng 36 th&aacute;ng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ở mảng bất động sản nghỉ dưỡng, Việt &Acirc;u đưa ra phương thức thanh to&aacute;n trả chậm 5 năm cho kh&aacute;ch mua dự &aacute;n Việt &Acirc;u ở H&ograve;a B&igrave;nh với gi&aacute; 1,9 triệu đồng/m2. C&ocirc;ng ty CP Hồng Hạc Đại Lải cũng vừa tung ra 10 căn biệt thự dự &aacute;n Flamingo Đại Lải Resort, kh&aacute;ch mua cũng được giảm gi&aacute; từ 30% trong đợt mở b&aacute;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo &ocirc;ng Trương Ch&iacute; Ki&ecirc;n, Ph&oacute; TGĐ Him Lam Thủ Đ&ocirc;, mức giảm gi&aacute; chung tr&ecirc;n thị trường căn hộ năm 2012 khoảng 20%, c&oacute; dự &aacute;n giảm nhiều, c&oacute; dự &aacute;n giảm &iacute;t. Năm 2013 dự b&aacute;o thị trường sẽ c&oacute; thanh khoản tốt hơn nhưng xu thế giảm gi&aacute; sẽ vẫn diễn ra.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Dự &aacute;n &ldquo;tốt&rdquo; vẫn b&aacute;n được h&agrave;ng</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Thị trường bất động sản được đ&aacute;nh gi&aacute; l&agrave; đ&atilde; &ldquo;đổi chủ&rdquo; từ đầu năm 2012, muốn b&aacute;n được h&agrave;ng c&aacute;c chủ đầu tư v&agrave; dự &aacute;n buộc phải hướng đến người c&oacute; nhu cầu thực.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nếu chỉ giảm gi&aacute;, tặng qu&agrave;, khuyến m&atilde;i th&igrave; kh&oacute; thuyết phục được người mua xuống tiền m&agrave; thay v&agrave;o đ&oacute; chất lượng v&agrave; tiến độ dự &aacute;n l&agrave; yếu tố quyết định tạo n&ecirc;n gi&aacute; trị cũng như sức cạnh tranh cho mỗi dự &aacute;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tr&ecirc;n thực tế, một v&agrave;i dự &aacute;n cũng đ&atilde; thể hiện được kh&aacute; r&otilde; xu thế n&agrave;y. Chẳng hạn như dự &aacute;n Hapulico Complex sau 3 năm khởi c&ocirc;ng, biệt thự, nh&agrave; vườn đ&atilde; đưa v&agrave;o sử dụng; 7 t&ograve;a th&aacute;p căn hộ cao 17-24 tầng, 800 căn hộ v&agrave; 1 t&ograve;a th&aacute;p văn ph&ograve;ng 24 tầng vừa ho&agrave;n th&agrave;nh v&agrave; b&agrave;n giao nh&agrave; th&aacute;ng 11/2012, sớm hơn 2 th&aacute;ng so với kế hoạch đ&atilde; đề ra.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Gi&aacute; giao dịch căn hộ dự &aacute;n n&agrave;y tr&ecirc;n thị trường vẫn giữ ở mức khoảng 33 triệu đồng/m2, vẫn cao hơn so với gi&aacute; gốc từ 5-6 triệu đồng/m2. Trong khi nhiều dự &aacute;n kh&aacute;c đ&atilde; giảm 15-20% so với gi&aacute; gốc chủ đầu tư b&aacute;n ra.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Dự &aacute;n Sails Tower nằm tại phường Kiến Hưng, H&agrave; Đ&ocirc;ng, H&agrave; Nội do C&ocirc;ng ty Khải Hưng l&agrave;m chủ đầu tư gồm gồm 2 t&ograve;a th&aacute;p cao 30 v&agrave; 27 tầng cung cấp 51 tầng căn hộ với 01 tầng hầm v&agrave; 03 tầng dịch vụ th&ocirc;ng nhau c&ugrave;ng nhiều tiện &iacute;ch sống kh&aacute;c.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Dự &aacute;n n&agrave;y đang ho&agrave;n thiện, dự kiến b&agrave;n giao 9/2013, c&aacute;c căn hộ được Si&ecirc;u thị dự &aacute;n tại số 137 Nguyễn Ngọc Vũ ph&acirc;n phối độc quyền, gi&aacute; đơn vị n&agrave;y b&aacute;n ra từ 16 triệu đồng/m2, tương đương khoảng từ 800 triệu đồng/căn, ng&acirc;n h&agrave;ng hỗ trợ 70% gi&aacute; trị hợp đồng trong v&ograve;ng 15 năm, l&atilde;i suất 9,9% trong 12 th&aacute;ng đầu. B&ecirc;n cạnh đ&oacute; kh&aacute;ch h&agrave;ng sẽ được tặng xe Honda Vision trị gi&aacute; khoảng 30 triệu đồng khi mua căn hộ diện t&iacute;ch tr&ecirc;n 70m2, chủ đầu tư c&ograve;n c&oacute; nhiều ưu đ&atilde;i ri&ecirc;ng d&agrave;nh cho kh&aacute;ch h&agrave;ng l&agrave; c&ocirc;ng chức nh&agrave; nước, người c&oacute; học vấn từ cao đẳng trở l&ecirc;n,&hellip;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Do đ&oacute;, dự &aacute;n đang thu h&uacute;t được kh&aacute; nhiều lượng giao dịch th&agrave;nh c&ocirc;ng trong thời gian qua, đặc biệt l&agrave; khu vực quận H&agrave; Đ&ocirc;ng, quận Ho&agrave;ng Mai, Thanh Xu&acirc;n,&hellip;nơi tiếp cận dễ d&agrave;ng tới dự &aacute;n. Theo th&ocirc;ng tin từ đơn vị ph&acirc;n phối hiện đ&atilde; c&oacute; tr&ecirc;n 300 giao dịch th&agrave;nh c&ocirc;ng từ dự &aacute;n n&agrave;y sau khi mở b&aacute;n từ th&aacute;ng 11/2012.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Nguyễn Đức Cử - Gi&aacute;m đốc C&ocirc;ng ty TNHH Khải Hưng cho biết, sẽ đảm bảo tiến độ của Sails Tower, đồng thời cam kết nhập 4 thang m&aacute;y từ Đức cho dự &aacute;n, 100% đường điện trong t&ograve;a nh&agrave; kh&ocirc;ng sử dụng mối nối đảm bảo an to&agrave;n tuyệt đối cho d&acirc;n cư. Hệ thống camera an ninh gi&aacute;m s&aacute;t được lắp dọc h&agrave;nh lang nhằm tạo sự an t&acirc;m cho cư d&acirc;n sinh sống trong t&ograve;a nh&agrave;.&rdquo;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">C&ograve;n theo nhận định của &ocirc;ng Ki&ecirc;n, những dự &aacute;n c&oacute; vị tr&iacute; thực tốt, DN c&oacute; tiềm lực t&agrave;i ch&iacute;nh thực sự th&igrave; mới c&oacute; khả năng cạnh tranh được với thị trường, c&ograve;n DN n&agrave;o kh&ocirc;ng c&oacute; năng lực cốt l&otilde;i th&igrave; rất kh&oacute; tồn tại được, bởi thị trường sẽ cạnh tranh rất khốc liệt. Ch&iacute;nh v&igrave; vậy, thị trường BĐS năm 2013 sẽ đi ngang v&agrave; cạnh tranh về gi&aacute; trị, chất lượng của sản phẩm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo B&aacute;o Tr&iacute; Thức Trẻ</strong></em></p>\r\n</div>\r\n', '“Săn” căn hộ dự án sắp hoàn thiện, giá hợp lý dịp cuối năm', 0),
(10, 1, '2013-01-07 14:49:48', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Bất chấp thị trường đ&oacute;ng băng, nh&agrave; đầu tư đi&ecirc;u đứng, nhiều căn villa cổ, mặt đường trung t&acirc;m th&agrave;nh phố, biệt thự ven Hồ T&acirc;y vẫn h&eacute;t gi&aacute; tr&ecirc;n dưới 100 tỷ đồng thậm ch&iacute; hơn 200 tỷ đồng.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Anh Mạnh H&ugrave;ng, chủ nh&agrave; đang rao b&aacute;n một khu biệt thự x&acirc;y th&ocirc; 4 tầng ở khu biệt thự gần Phủ T&acirc;y Hồ, Quảng An, T&acirc;y Hồ với gi&aacute; l&ecirc;n tới 450 triệu đồng mỗi m2. Như vậy, căn biệt thự rộng 255 m2 với mặt tiền 12 m2 v&agrave; chiều s&acirc;u hơn 21 m c&oacute; gi&aacute; tới 114 tỷ đồng. Anh cho hay, khu biệt thự gần s&aacute;t nh&agrave; một &ocirc;ng &quot;bầu&quot; lớn, to&agrave;n c&aacute;c sếp ở. &quot;Biệt thự nằm ngay ven hồ, c&oacute; hai mặt tho&aacute;ng, đường rộng 9m v&agrave; đường nội bộ 8m, trước mặt l&agrave; Hồ T&acirc;y cảnh đẹp tho&aacute;ng m&aacute;t n&ecirc;n gi&aacute; kh&ocirc;ng thể rẻ&quot;, anh n&oacute;i.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Cũng theo anh, do địa ốc đang trong bối cảnh khủng hoảng, n&ecirc;n chỉ c&oacute; thể giảm đ&ocirc;i ch&uacute;t, xuống c&ograve;n khoảng 108 tỷ đồng. Trong trường hợp muốn mua gi&aacute; rẻ hơn, anh cho biết c&oacute; thể mua một căn biệt thự vu&ocirc;ng g&oacute;c mặt hồ rộng 245 m2 với gi&aacute; 410 triệu đồng mỗi m2. Như vậy, cả căn sẽ c&oacute; gi&aacute; kh&ocirc;ng dưới 100 tỷ đồng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/10_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%204.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Biệt thự ven hồ T&acirc;y được ch&agrave;o với gi&aacute; trăm tỷ đồng. Ảnh: Ho&agrave;ng H&agrave;.</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Chủ một biệt thự ở đường Trường Chinh, Đống Đa, H&agrave; Nội cũng rao gi&aacute; tới... 140 tỷ đồng. Người b&aacute;n cho biết, nh&agrave; rộng khoảng 700 m2, diện t&iacute;ch sử dụng hơn 100 m2, mặt tiền 14 m, nở hậu v&agrave; sổ đỏ ch&iacute;nh chủ. Villa 2 tầng với đầy đủ ph&ograve;ng kh&aacute;ch, ph&ograve;ng ngủ, ph&ograve;ng tắm, nh&agrave; ăn, c&oacute; vườn tr&aacute;i c&acirc;y b&ecirc;n h&ocirc;ng nh&agrave;, s&acirc;n trước s&acirc;n sau đầy đủ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&quot;Một c&ocirc;ng ty bất động sản đ&atilde; định gi&aacute; khu villa 200 triệu đồng mỗi m2 n&ecirc;n mức gi&aacute; tr&ecirc;n l&agrave; hợp l&yacute;. Tất nhi&ecirc;n c&oacute; thể thương lượng trong bối cảnh địa ốc trầm như hiện nay nhưng kh&ocirc;ng thể hạ gi&aacute; qu&aacute; nhiều&quot;, vị chủ nh&agrave; n&oacute;i.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Một m&ocirc;i giới cho hay, &ocirc;ng đang rao b&aacute;n hộ một biệt thự Ph&aacute;p cổ tr&ecirc;n đường Trần Hưng Đạo, quận Ho&agrave;n Kiếm với gi&aacute; hơn 250 tỷ đồng. Biệt thự 2,5 tầng gồm 24 hộ d&acirc;n sinh sống v&agrave; khu đất nở hậu hướng Đ&ocirc;ng Nam n&ecirc;n &quot;rất ph&aacute;t&quot;. Mặc d&ugrave; nằm ngay ở khu phố lớn với vị tr&iacute; đắc địa, nhưng ch&agrave;o suốt từ th&aacute;ng 6/2012 đến nay vẫn chưa c&oacute; kh&aacute;ch hỏi mua v&igrave; gi&aacute; qu&aacute; cao.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&quot;24 hộ sẽ gồm 24 sổ đỏ, nhưng ai mua ch&uacute;ng t&ocirc;i sẽ l&agrave;m thủ tục gộp chung th&agrave;nh 1 sổ. Ch&uacute;ng t&ocirc;i sẵn s&agrave;ng bao sang t&ecirc;n v&agrave; lo thủ tục giấy ph&eacute;p x&acirc;y dựng khi kh&aacute;ch h&agrave;ng muốn x&acirc;y lại. Khu đất được x&acirc;y tối đa 7 tầng&quot;, vị m&ocirc;i giới tiết lộ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong bối cảnh địa ốc gần như đ&oacute;ng băng, kh&ocirc;ng phải nh&agrave; đầu tư n&agrave;o cũng đủ tiền mua những căn biệt thự gi&aacute; khủng. Cuối năm 2012, một ca sĩ đ&atilde; r&oacute;t 100 tỷ đồng mua v&agrave; ho&agrave;n thiện ng&ocirc;i nh&agrave; si&ecirc;u sang cũng từng g&acirc;y x&ocirc;n xao dư luận. Nội thất xa hoa trong căn nh&agrave; 6 tầng với diện t&iacute;ch 500m2 c&ugrave;ng c&aacute;ch b&agrave;i tr&iacute; sang trọng kh&ocirc;ng kh&aacute;c g&igrave; biệt thự xa hoa của c&aacute;c ng&ocirc;i sao Hollywood khiến kh&ocirc;ng &iacute;t người phải ngưỡng mộ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Nguyễn Thế Cường, đại diện văn ph&ograve;ng nh&agrave; đất Long Bi&ecirc;n cho hay, trong bối cảnh địa ốc khủng hoảng như hiện nay, chỉ c&oacute; những người c&oacute; tiềm lực t&agrave;i ch&iacute;nh rất mạnh, tầm cỡ &quot;đại gia&quot; mới đủ sức để mua những biệt thự l&ecirc;n tới trăm tỷ đồng. &Ocirc;ng Cường cho hay, thực tế, những khu nh&agrave; cổ c&oacute; gi&aacute; tới v&agrave;i ba trăm triệu mỗi m2, diện t&iacute;ch lại rộng n&ecirc;n gi&aacute; trở n&ecirc;n &quot;cực kỳ đắt đỏ&quot;. Tr&ecirc;n thị trường bất động sản, biệt thự phố cổ, khu ven Hồ T&acirc;y thuộc dạng đắc địa, n&ecirc;n gi&aacute; kh&ocirc;ng rẻ. Bản th&acirc;n &ocirc;ng cũng từng l&agrave;m dịch vụ cho khu phố cổ h&agrave;ng Gỗ l&ecirc;n tới 250 triệu đồng mỗi m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Cường cho hay, biệt thự ven hồ T&acirc;y, nh&agrave; phố cổ hoặc khu nh&agrave; tại c&aacute;c trung t&acirc;m th&agrave;nh phố lớn thường kh&ocirc;ng phải để &quot;lướt s&oacute;ng&quot; m&agrave; chủ yếu sinh lời do kinh doanh mang lại n&ecirc;n gi&aacute; b&aacute;n kh&ocirc;ng hạ nhiệt mặc cho khủng hoảng địa ốc l&agrave;m nhiều nh&agrave; đầu tư đi&ecirc;u đứng. Đơn cử, nh&agrave; phố cổ hoặc villa tại c&aacute;c mặt đường lớn c&oacute; thể, kinh doanh bu&ocirc;n b&aacute;n hoặc cho thu&ecirc; tầm 5.000 - 7.000 USD mỗi th&aacute;ng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Th&ocirc;ng thường c&aacute;c khu nh&agrave; biệt thự cổ rao b&aacute;n theo gi&aacute; v&agrave;ng, sau đ&oacute; mới quy đổi ra tiền đồng để &quot;giữ gi&aacute;&quot;. &Ocirc;ng ph&acirc;n t&iacute;ch, trước đ&acirc;y, khi mua nh&agrave; 10 &quot;c&acirc;y&quot; mỗi m2 th&igrave; nay, người b&aacute;n cũng phải ch&agrave;o 10 &quot;c&acirc;y&quot;. Lượng v&agrave;ng vẫn giữ nguy&ecirc;n, nhưng khi quy đổi ra tiền đồng th&igrave; ch&ecirc;nh lệch lại rất lớn, bởi vậy mới c&oacute; chuyện nh&agrave; phố cổ gi&aacute; 500 triệu đến 1 tỷ đồng mỗi m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&quot;Với mức gi&aacute; l&ecirc;n tới h&agrave;ng trăm tỷ đồng, nhiều người sẽ lựa chọn căn biệt thự sang trọng tại c&aacute;c khu đ&ocirc; thị mới, thay v&igrave; c&aacute;c biệt thự cũ n&ecirc;n giao dịch th&agrave;nh c&ocirc;ng tại c&aacute;c khu biệt thự hiện nay kh&ocirc;ng nhiều&quot;, &ocirc;ng Cường n&oacute;i.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tr&ecirc;n thị trường địa ốc, nh&agrave; rao b&aacute;n h&agrave;ng trăm tỷ đồng chủ yếu thuộc về c&aacute;c khu biệt thự, c&ograve;n chung cư gi&aacute; thường rẻ hơn. Hồi th&aacute;ng 3 năm 2012, căn hộ được mệnh danh l&agrave; đế vương của H&agrave; Nội được ch&agrave;o b&aacute;n với gi&aacute; 25 tỷ đồng cũng đ&atilde; từng g&acirc;y sốc v&agrave; c&aacute;c chuy&ecirc;n gia dự b&aacute;o thị trường sẽ kh&ocirc;ng dễ hấp thụ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">TS Đ&agrave;o Ngọc Nghi&ecirc;m, Ph&oacute; chủ tịch Hội Quy hoạch kiến tr&uacute;c H&agrave; Nội cho hay, hiện nay gi&aacute; đất đang &quot;ảo&quot;, sau 1/7 tới, khi Luật Thủ đ&ocirc; c&oacute; hiệu lực gi&aacute; đất sẽ giảm. &Ocirc;ng Nghi&ecirc;m ph&acirc;n t&iacute;ch, theo Luật Thủ đ&ocirc;, H&agrave; Nội dự t&iacute;nh x&acirc;y 5 th&agrave;nh phố vệ tinh, nh&agrave; cao tầng ki&ecirc;n quyết cấm x&acirc;y qu&aacute; 9 tầng trong c&aacute;c quận nội th&agrave;nh cũ. &quot;C&ugrave;ng với ch&iacute;nh s&aacute;ch siết nhập cư, v&agrave; đ&aacute;nh thuế lũy tiến đối với c&aacute;c trường hợp sỡ hữu đất đai vượt định mức n&ecirc;n chắc chắn năm 2013, ngoại trừ ph&acirc;n kh&uacute;c nh&agrave; ở x&atilde; hội, thu nhập thấp, gi&aacute; bất động sản sẽ giảm mạnh&quot;, &ocirc;ng Nghi&ecirc;m n&oacute;i.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Sở dĩ gi&aacute; đất ở c&aacute;c khu phố đắc địa vẫn kh&ocirc;ng giảm, theo &ocirc;ng Nghi&ecirc;m v&igrave; một số đối tượng &quot;nhạy b&eacute;n&quot; đ&atilde; tranh thủ t&igrave;m c&aacute;ch đẩy gi&aacute; nh&agrave; l&ecirc;n cao trước khi Luật thủ đ&ocirc; c&oacute; hiệu lực. Khi quy hoạch ph&acirc;n khu chi tiết được ch&iacute;nh thức ph&ecirc; duyệt, d&acirc;n số nội đ&ocirc; giảm đi một phần ba, khu phố cổ 860.000 giảm xuống c&ograve;n 600.000 người n&ecirc;n gi&aacute; đất ở c&aacute;c khu đất đắc địa như phố cổ v&agrave; c&aacute;c quận nội th&agrave;nh sẽ giảm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;Theo VnExpress</strong></em></p>\r\n</div>\r\n', 'Rao bán biệt thự trăm tỷ đồng thời địa ốc khủng hoảng', 0);
INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(11, 1, '2013-01-07 14:49:50', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Nhằm vực dậy thị trường bất động sản (BĐS), Bộ X&acirc;y dựng đ&atilde; đưa ra 6 nh&oacute;m giải ph&aacute;p, trong đ&oacute; sẽ tập trung &quot;khơi th&ocirc;ng&quot; đầu ra vốn bị &quot;đ&oacute;ng băng&quot; trong hơn năm qua.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="274" src="http://saigonnhadat.vn/upload/UserFiles/image/10_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%205.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Với sự nỗ lực của c&aacute;c DN, cộng đồng tr&aacute;ch nhiệm của c&aacute;c tổ chức t&iacute;n dụng, thị trường bất động sản sẽ c&oacute; hy vọng khởi sắc trong năm 2013. Ảnh: Minh Nguyễn</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong 6 nh&oacute;m giải ph&aacute;p được Bộ X&acirc;y dựng đưa ra, th&igrave; nh&oacute;m giải ph&aacute;p về t&iacute;n dụng được coi l&agrave; &quot;xương sống&quot; để vực dậy thị trường BĐS. Cụ thể, Bộ đề xuất kết hợp giải quyết nợ xấu với việc k&iacute;ch th&iacute;ch thị trường BĐS bằng c&aacute;ch h&igrave;nh th&agrave;nh c&aacute;c g&oacute;i t&iacute;n dụng trung, d&agrave;i hạn l&atilde;i suất ph&ugrave; hợp cho người mua nh&agrave; để hỗ trợ đầu ra cho thị trường. Ph&acirc;n t&iacute;ch về giải ph&aacute;p n&agrave;y, Bộ X&acirc;y dựng cho rằng, giải quyết nợ xấu bằng việc phục hồi, ph&aacute;t triển thị trường l&agrave; c&aacute;ch l&agrave;m &iacute;t tốn k&eacute;m nhất nhưng hiệu quả, bảo đảm lợi &iacute;ch c&aacute;c b&ecirc;n li&ecirc;n quan.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Giải ph&aacute;p cụ thể do Ng&acirc;n h&agrave;ng Nh&agrave; nước quyết định, song cần quan t&acirc;m đến xử l&yacute; h&agrave;ng tồn kho, ưu ti&ecirc;n c&aacute;c khoản nợ c&oacute; bảo l&atilde;nh bằng BĐS; chỉ đạo ng&acirc;n h&agrave;ng thương mại (NHTM) tiếp tục mở van t&iacute;n dụng cho BĐS, nhất l&agrave; dự &aacute;n dở dang, c&oacute; khả năng thanh khoản, từng bước đưa l&atilde;i suất cho vay về mức b&igrave;nh thường (khoảng 10%/năm). G&oacute;i giải ph&aacute;p về t&agrave;i ch&iacute;nh cũng nhận được sự đồng thuận của nhiều tổ chức, chuy&ecirc;n gia. Hiệp hội c&aacute;c nh&agrave; đầu tư t&agrave;i ch&iacute;nh cho rằng, hỗ trợ cho người thu nhập thấp v&agrave; trung b&igrave;nh hưởng l&atilde;i suất ưu đ&atilde;i mua nh&agrave; l&agrave; cần thiết.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Hiệp hội n&agrave;y đề nghị, người mua nh&agrave; trị gi&aacute; dưới 2 tỷ đồng/căn được vay ưu đ&atilde;i 7%/năm cho 3 năm đầu, Nh&agrave; nước sẽ cấp b&ugrave; l&atilde;i suất 3-5%/năm. Ước t&iacute;nh c&oacute; khoảng 8.000 tỷ đồng d&agrave;nh cấp b&ugrave; l&atilde;i suất, Nh&agrave; nước c&oacute; thể thu h&uacute;t số vốn đầu tư l&ecirc;n tới 120.000 tỷ đồng. Cơ chế b&ugrave; l&atilde;i suất cho NHTM tương tự cơ chế đ&atilde; thực hiện năm 2008. Trước c&acirc;u hỏi trong t&igrave;nh h&igrave;nh ng&acirc;n s&aacute;ch nh&agrave; nước căn cơ như hiện nay, khoản 8.000 tỷ đồng liệu c&oacute; khả thi? Hiệp hội cho rằng, nguồn cấp b&ugrave; l&atilde;i suất chỉ l&agrave; nguồn tạm ứng. Khi thực hiện chương tr&igrave;nh, lượng h&agrave;ng tồn kho giải ph&oacute;ng, c&ugrave;ng với đ&oacute; giải ph&oacute;ng năng lực sản xuất ng&agrave;nh BĐS, vật liệu x&acirc;y dựng, dịch vụ t&agrave;i ch&iacute;nh&hellip; khoản thu từ thuế gi&aacute; trị gia tăng sẽ lớn hơn rất nhiều.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">L&agrave;m việc với Thủ tướng Ch&iacute;nh phủ về thị trường BĐS, UBND TP H&agrave; Nội cũng kiến nghị ban h&agrave;nh ch&iacute;nh s&aacute;ch k&iacute;ch cầu, tạo điều kiện cho người d&acirc;n c&oacute; nhu cầu thực sự về nh&agrave; ở tiếp cận nguồn vốn t&iacute;n dụng l&atilde;i suất thấp. Đồng thời, c&oacute; cơ chế chuyển đổi từ nh&agrave; ở thương mại sang nh&agrave; ở x&atilde; hội; nghi&ecirc;n cứu ch&iacute;nh s&aacute;ch hỗ trợ người thu nhập thấp v&agrave; trung b&igrave;nh hưởng l&atilde;i suất ưu đ&atilde;i mua nh&agrave; v&agrave; r&uacute;t ngắn thời gian cho ph&eacute;p được chuyển nhượng nh&agrave; ở thu nhập thấp, nh&agrave; ở x&atilde; hội&hellip; Ngo&agrave;i ra, cả Bộ X&acirc;y dựng cũng như H&agrave; Nội v&agrave; TP Hồ Ch&iacute; Minh đều kiến nghị giải ph&aacute;p t&agrave;i kh&oacute;a, thuế, theo đ&oacute; đề nghị Quốc hội cho miễn, giảm thuế gi&aacute; trị gia tăng, thuế thu nhập DN với hoạt động đầu tư, kinh doanh nh&agrave; ở x&atilde; hội, nh&agrave; c&oacute; diện t&iacute;ch nhỏ, gi&aacute; b&igrave;nh d&acirc;n. C&aacute;c địa phương c&oacute; lượng BĐS tồn kho lớn sẽ kh&ocirc;ng đầu tư nh&agrave; t&aacute;i định cư, nh&agrave; ở x&atilde; hội m&agrave; d&ugrave;ng vốn để mua lại nh&agrave; thương mại phục vụ nhu cầu t&aacute;i định cư, cho thu&ecirc;, thu&ecirc; mua nh&agrave;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Để l&agrave;m được điều đ&oacute;, ch&iacute;nh c&aacute;c DN phải chủ động điều chỉnh kinh doanh, cơ cấu sản phẩm, giảm gi&aacute; b&aacute;n, &aacute;p dụng phương thức b&aacute;n linh hoạt, chuyển h&igrave;nh thức thu&ecirc;, thu&ecirc; mua, tạo niềm tin với kh&aacute;ch h&agrave;ng&hellip; N&oacute;i như Thủ tướng Nguyễn Tấn Dũng, giờ l&agrave; l&uacute;c DN cũng phải chung sức, g&aacute;nh v&aacute;c c&ugrave;ng Ch&iacute;nh phủ, kh&ocirc;ng thể đổ tất cả l&ecirc;n Ch&iacute;nh phủ. Tại c&aacute;c buổi l&agrave;m việc của Thủ tướng với hai thị trường BĐS lớn l&agrave; H&agrave; Nội v&agrave; TP Hồ Ch&iacute; Minh, Bộ X&acirc;y dựng khẳng định sẽ gắn việc th&aacute;o gỡ kh&oacute; khăn cho thị trường BĐS với việc thực hiện chiến lược ph&aacute;t triển nh&agrave; ở quốc gia. Theo đ&oacute;, chuyển hướng từ nh&agrave; ở thương mại sang nh&agrave; ở x&atilde; hội phục vụ cho 8 nh&oacute;m đối tượng cần ưu ti&ecirc;n giải quyết nh&agrave; ở nhằm c&acirc;n đối cung cầu cho thị trường, đ&aacute;p ứng nhu cầu nh&agrave; ở cho đại đa số người c&oacute; thu nhập thấp, đồng thời tạo đầu ra, gỡ kh&oacute; cho DN, gi&uacute;p thị trường ấm l&ecirc;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Bộ trưởng Bộ X&acirc;y dựng Trịnh Đ&igrave;nh Dũng cho biết, hệ thống giải ph&aacute;p tổng thể m&agrave; Bộ X&acirc;y dựng, c&aacute;c bộ, ng&agrave;nh li&ecirc;n quan v&agrave; c&aacute;c địa phương đề xuất đ&atilde; được Thủ tướng Ch&iacute;nh phủ chấp thuận sẽ được ban h&agrave;nh th&agrave;nh Nghị quyết chuy&ecirc;n đề của Ch&iacute;nh phủ để tổ chức thực hiện ngay trong những ng&agrave;y đầu năm 2013. Với nỗ lực của c&aacute;c DN, cộng đồng tr&aacute;ch nhiệm của c&aacute;c tổ chức t&iacute;n dụng, c&ugrave;ng dự b&aacute;o sự hồi phục của kinh tế thế giới, sự ổn định của kinh tế vĩ m&ocirc; trong nước, nhất l&agrave; niềm tin của người d&acirc;n trở lại với thị trường, chắc chắn thị trường BĐS năm 2013 sẽ c&oacute; bước cải thiện, tạo đ&agrave; cho sự ph&aacute;t triển ổn định v&agrave;o c&aacute;c năm sau...</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;Theo H&agrave; Nội Mới</strong></em></p>\r\n</div>\r\n', '“Khơi thông” đầu ra cho thị trường bất động sản', 0),
(12, 1, '2013-01-07 14:49:52', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Theo khung gi&aacute; đất năm 2013 m&agrave; HĐND TP.H&agrave; Nội vừa th&ocirc;ng qua, mức gi&aacute; tối thiểu tại c&aacute;c quận l&agrave; 3,456 triệu đồng/m2, gi&aacute; tối đa l&agrave; 81 triệu đồng/m2 thuộc c&aacute;c khu đất vốn đắt nhất từ trước tới nay như phố H&agrave;ng Đ&agrave;o, H&agrave;ng Ngang, L&yacute; Th&aacute;i Tổ (Q.Ho&agrave;n Kiếm).</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="293" src="http://saigonnhadat.vn/upload/UserFiles/image/10_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2012.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="440" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Gi&aacute; nh&agrave; đất trong khu phố cổ H&agrave; Nội thuộc h&agrave;ng đắt nhất thế giới. Ảnh: GIANG HUY</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Cụ thể, đất ở tại c&aacute;c thị trấn của c&aacute;c huyện vẫn giữ nguy&ecirc;n như năm 2012. Đất ở tại c&aacute;c phường của thị x&atilde; Sơn T&acirc;y c&oacute; gi&aacute; từ 1,584 triệu đồng đến 16,7 triệu đồng/m2. Ở khu vực gi&aacute;p ranh với c&aacute;c quận thuộc huyện Từ Li&ecirc;m, Thanh Tr&igrave;, Gia L&acirc;m, Chương Mỹ, Thanh Oai, Ho&agrave;i Đức, mức gi&aacute; cơ bản giữ nguy&ecirc;n như năm trước.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Gi&aacute; đất của đường Y&ecirc;n H&ograve;a - đoạn thuộc x&atilde; Đại Mỗ, huyện Từ Li&ecirc;m - v&agrave; c&aacute;c x&atilde; gi&aacute;p ranh của huyện Ho&agrave;i Đức được điều chỉnh giảm xuống c&oacute; gi&aacute; tối thiểu 2,035 triệu đồng v&agrave; tối đa l&agrave; 32,4 triệu đồng mỗi m&eacute;t vu&ocirc;ng. Tại thị trấn của c&aacute;c huyện Từ Li&ecirc;m, Gia L&acirc;m, Thanh Tr&igrave;, Ho&agrave;i Đức c&oacute; gi&aacute; tối thiểu 1,92 triệu, gi&aacute; tối đa 27,6 triệu đồng/m2. C&aacute;c thị trấn của c&aacute;c huyện c&ograve;n lại c&oacute; gi&aacute; từ 750.000 đồng m&eacute;t vu&ocirc;ng đến 8,04 triệu đồng/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ở v&ugrave;ng n&ocirc;ng th&ocirc;n tại vị tr&iacute; ven c&aacute;c trục đường giao th&ocirc;ng ch&iacute;nh, điều chỉnh gi&aacute; c&ograve;n 433.000 đồng đến 11,25 triệu đồng/m2. Một số x&atilde; của huyện Ba V&igrave;, S&oacute;c Sơn, Mỹ Đức, Ph&uacute; Xuy&ecirc;n, Ứng H&ograve;a, Thanh Oai được điều chỉnh tăng từ 350.000 đồng - 2,25 triệu đồng/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Điều chỉnh gi&aacute; đất sản xuất kinh doanh phi n&ocirc;ng nghiệp tại c&aacute;c quận, khu vực gi&aacute;p ranh quận thuộc huyện Từ Li&ecirc;m l&agrave; 47,81 triệu đồng/m2. T&ugrave;y từng vị tr&iacute;, gi&aacute; đất sản xuất kinh doanh phi n&ocirc;ng nghiệp bằng khoảng 59 - 60% gi&aacute; đất ở c&ugrave;ng vị tr&iacute;, đường phố. Đất sản xuất kinh doanh phi n&ocirc;ng nghiệp tại thị x&atilde; Sơn T&acirc;y v&agrave; thị trấn c&aacute;c huyện từ 525.000 đồng đến 16,291 triệu đồng/m2. Đất sản xuất kinh doanh phi n&ocirc;ng nghiệp tại khu vực n&ocirc;ng th&ocirc;n nhưng gi&aacute;p ranh c&aacute;c quận c&oacute; gi&aacute; tối đa l&agrave; 19,124 triệu, gi&aacute; tối thiểu l&agrave; 1,221 triệu đồng/m2. Đất sản xuất kinh doanh phi n&ocirc;ng nghiệp tại khu vực n&ocirc;ng th&ocirc;n, nhưng nằm ven trục đầu mối giao th&ocirc;ng gi&aacute; từ 303.000 đồng đến 4,86 triệu đồng/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tuy nhi&ecirc;n, tr&ecirc;n thực tế, gi&aacute; thị trường &aacute;p dụng cho những khu đất n&oacute;i tr&ecirc;n đắt hơn cả chục lần, nhiều vị tr&iacute; c&oacute; gi&aacute; xấp xỉ h&agrave;ng tỉ đồng mỗi m&eacute;t vu&ocirc;ng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Đất thuộc khu vực phố cổ lu&ocirc;n c&oacute; gi&aacute; cao ngất ngưởng v&igrave; kh&ocirc;ng chỉ c&oacute; gi&aacute; trị về lịch sử, văn h&oacute;a m&agrave; c&ograve;n nằm ở vị tr&iacute; trung t&acirc;m, thuận tiện về bu&ocirc;n b&aacute;n. Nh&agrave; mặt tiền tại c&aacute;c phố H&agrave;ng Ngang, H&agrave;ng Đ&agrave;o d&ugrave; l&agrave; nh&agrave; cũ, nh&agrave; nhỏ, nhưng chưa khi n&agrave;o c&oacute; gi&aacute; dưới 800 triệu đồng/m2. C&aacute;c phố H&agrave;ng Bồ, h&agrave;ng Trống, h&agrave;ng Buồm c&oacute; gi&aacute; thấp hơn một ch&uacute;t, khoảng 600 - 700 triệu đồng/m2, nh&agrave; mặt phố B&aacute;t Đ&agrave;n cũng c&oacute; gi&aacute; 600 triệu đồng/m2...</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tại c&aacute;c tuyến phố quanh khu vực hồ Ho&agrave;n Kiếm như H&agrave;ng B&agrave;i, L&yacute; Th&aacute;i Tổ, Hai B&agrave; Trưng, gi&aacute; đất cũng thuộc top tiền tỉ mỗi m&eacute;t vu&ocirc;ng. Hồi cuối năm 2010, để giải tỏa được khu đất v&agrave;ng số 22-24 H&agrave;ng B&agrave;i v&agrave; 25-27 Hai B&agrave; Trưng cho một dự &aacute;n x&acirc;y dựng trung t&acirc;m thương mại - văn ph&ograve;ng, đơn vị chủ đầu tư đ&atilde; phải chấp nhận đền b&ugrave; với gi&aacute; 900 triệu đồng/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Kh&ocirc;ng chỉ đắt nhất VN, đất v&agrave;ng tại c&aacute;c khu vực trung t&acirc;m của H&agrave; Nội c&ograve;n thuộc h&agrave;ng đắt nhất thế giới. Theo Colliers International - đơn vị chuy&ecirc;n nghi&ecirc;n cứu thị trường bất động sản - th&igrave; nếu chỉ t&iacute;nh ở mức gi&aacute; phổ biến l&agrave; 27.200USD/m2 (tương đương gần 600 triệu đồng/m2) th&igrave; đất v&agrave;ng tại H&agrave; Nội đ&atilde; đắt ngang với Tokyo hay Paris - những th&agrave;nh phố thuộc top xa hoa nhất thế giới.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo Lao Động</strong></em></p>\r\n</div>\r\n', 'Giá đất nơi nào đắt nhất, nơi nào rẻ nhất?', 0);
INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(13, 1, '2013-01-07 14:49:53', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Bất chấp gi&aacute; căn hộ li&ecirc;n tục giảm nhưng người mua vẫn dửng dưng, h&agrave;ng ngh&igrave;n căn hộ sẽ c&ograve;n ế trong v&agrave;i năm tới.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Ch&igrave;m ngỉm</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Cuối tuần qua, Ba Đ&igrave;nh Land c&ugrave;ng với đối t&aacute;c mới l&agrave; C&ocirc;ng ty TNHH S&ocirc;ng Hằng t&aacute;i khởi động lại dự &aacute;n căn hộ Hattoco v&agrave; ch&iacute;nh thức mở b&aacute;n với gi&aacute; từ 15 triệu đồng/m2. So với gi&aacute; căn hộ mở b&aacute;n trước đ&acirc;y, mức gi&aacute; n&agrave;y đ&atilde; giảm tới 6 triệu đồng/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Được khởi c&ocirc;ng từ năm 2009, đến cuối năm 2011 Ba Đ&igrave;nh Land đ&atilde; đầu tư được 400 tỷ đồng để x&acirc;y dựng đến tầng 10 trong tổng số 39 tầng của dự &aacute;n. Nhưng sau đ&oacute;, gặp phải kinh tế kh&oacute; khăn, dự &aacute;n thiếu vốn triển khai n&ecirc;n phải tạm dừng thi c&ocirc;ng gần 1 năm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">H&agrave;ng loạt dự &aacute;n vừa mở b&aacute;n cũng đ&atilde; phải chấp nhận giảm gi&aacute; h&agrave;ng triệu đồng. Đơn cử, dự &aacute;n Nam Đ&ocirc; Complex của GP invest cũng đ&atilde; giảm xuống c&ograve;n 20 triệu đồng/m2. Trước đ&oacute;, chủ đầu tư n&agrave;y đ&atilde; từng cho rằng, gi&aacute; căn hộ hiện nay tương đối rẻ v&agrave; kh&oacute; c&oacute; chuyện giảm gi&aacute; nữa. Nhiều dự &aacute;n lớn kh&aacute;c đ&atilde; trở về mức gi&aacute; kh&aacute; &quot;mềm&quot; như The Westa 16,5 triệu đồng/m2, Hồ Gươm Plaza khoảng 17-18 triệu đồng/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="349" src="http://saigonnhadat.vn/upload/UserFiles/image/10_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2016.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Gi&aacute; cả v&agrave; niềm tin l&agrave; yếu tố quyết định cho thị trường BĐ.</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo số liệu thống k&ecirc; của CBRE, H&agrave; Nội hiện c&ograve;n tồn 20.500 căn chung cư, l&agrave; những căn do c&aacute;c chủ đầu tư ch&agrave;o b&aacute;n nhưng kh&ocirc;ng t&igrave;m được người mua. Đơn vị n&agrave;y ước t&iacute;nh, sẽ mất từ 1,5 năm đến 4 năm để ti&ecirc;u thụ hết lượng h&agrave;ng tồn kho n&agrave;y.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nếu như đầu năm 2012, c&aacute;c c&aacute; nh&acirc;n cắt lỗ lớn tr&ecirc;n thị trường thứ cấp với mức giảm c&oacute; thể l&ecirc;n tới 30% mức gi&aacute; mua v&agrave;o, th&igrave; đến cuối năm 2012, c&aacute;c chủ đầu tư quyết liệt giảm gi&aacute; ch&agrave;o b&aacute;n sơ cấp, c&oacute; trường hợp l&ecirc;n tới 40% mức gi&aacute; ch&agrave;o ban đầu. Mức giảm gi&aacute; lớn nhất tr&ecirc;n thị trường hiện nay vẫn thuộc về thứ cấp, h&agrave;ng loạt nh&agrave; đầu tư chấp nhận cắt lỗ tới 50% để r&uacute;t khỏi thị trường.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Hết thời đua nhau b&aacute;n, c&aacute;c chủ đầu tư mới t&iacute;nh chuyện x&acirc;y căn hộ. Năm 2012, thị trường căn hộ chứng kiến nguồn cung ch&agrave;o b&aacute;n mới sụt giảm mạnh, giảm tới 65% so với năm trước, chủ yếu do c&aacute;c chủ đầu tư trở n&ecirc;n hết sức thận trọng kể từ nửa cuối năm 2011. Trong khi đ&oacute;, số căn ho&agrave;n thiện trong năm 2012 tăng mạnh, đạt mức cao nhất trong 5 năm trở lại đ&acirc;y.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tại TP HCM, vẫn c&ograve;n tr&ecirc;n 28.000 căn hộ tồn kho tr&ecirc;n thị trường chưa c&oacute; chủ sở hữu, đặc biệt l&agrave; ở ph&acirc;n kh&uacute;c cao cấp v&agrave; hạng sang do mức gi&aacute; ch&agrave;o b&aacute;n của những ph&acirc;n kh&uacute;c n&agrave;y vẫn c&ograve;n rất cao so với khả năng chi trả của đại bộ phận d&acirc;n cư. CBRE VN nhận định sẽ mất &iacute;t nhất 5 năm để thị trường c&oacute; thể vượt qua t&igrave;nh trạng thừa cung n&agrave;y.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong ch&iacute;n th&aacute;ng đầu năm 2012, h&agrave;ng loạt chủ đầu tư tại TP HCM đ&atilde; tung ra c&aacute;c dự &aacute;n căn hộ diện t&iacute;ch vừa phải với gi&aacute; b&aacute;n từ 600 triệu đến 1 tỷ đồng v&agrave; đạt được con số b&aacute;n rất khả quan, v&iacute; dụ như dự &aacute;n Ehome 3 với 333 căn tại Quận B&igrave;nh T&acirc;n đạt tỷ lệ b&aacute;n khoảng 75% trong chưa đầy 5 th&aacute;ng ch&agrave;o b&aacute;n. Qu&yacute; cuối c&ugrave;ng của năm tiếp tục đ&oacute;n nhận th&ecirc;m một dự &aacute;n căn hộ b&igrave;nh d&acirc;n được ch&agrave;o b&aacute;n với c&ugrave;ng mức gi&aacute; như tr&ecirc;n l&agrave; dự &aacute;n Tanibuilding Sơn Kỳ 2 với 150 căn hộ v&agrave; dự &aacute;n n&agrave;y cũng đ&atilde; b&aacute;n được 75% căn hộ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Giảm gi&aacute; chả ai th&egrave;m</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Dường như c&aacute;c chương tr&igrave;nh khuyến mại cũng như ưu đ&atilde;i về l&atilde;i suất chưa thực sự hấp dẫn người mua. Ngay cả khi gi&aacute; b&aacute;n giảm, người mua chưa vội v&agrave;ng mua ngay bởi họ kỳ vọng mức gi&aacute; sẽ giảm hơn nữa.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo CBRE VN, người mua nh&agrave; đang c&ograve;n chần chừ xuất ph&aacute;t từ yếu tố t&acirc;m l&yacute; hơn l&agrave; khả năng chi trả. Ngay cả khi c&oacute; khả năng chi trả cho căn hộ, họ cũng chưa vội thực hiện giao dịch do t&acirc;m l&yacute; kỳ vọng gi&aacute; giảm th&ecirc;m, sự thiếu hụt niềm tin v&agrave;o khả năng thực hiện cam kết của c&aacute;c chủ đầu tư, cũng như quan niệm gi&aacute; cả đang ở mức qu&aacute; cao - hệ quả của cuộc đua tranh về gi&aacute; hiện tại. Do vậy, việc giảm gi&aacute; chỉ c&oacute; t&aacute;c dụng nếu như mức giảm lớn hơn kỳ vọng của người mua.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">B&agrave; Ng&ocirc; Thị Hương Giang, Ph&ograve;ng Nghi&ecirc;n cứu v&agrave; Tư vấn của CBRE Việt Nam ph&acirc;n t&iacute;ch, thời gian qua, việc h&agrave;ng loạt dự &aacute;n bị tr&igrave; ho&atilde;n chậm tiến độ đ&atilde; l&agrave;m mất l&ograve;ng tin của người mua nh&agrave;, ch&iacute;nh v&igrave; thế m&agrave; họ đang quan s&aacute;t v&agrave; chờ đợi. Việc giảm gi&aacute; của chủ đầu tư thực sự sẽ kh&ocirc;ng hiệu quả nếu kh&ocirc;ng t&iacute;nh tới tiến độ dự &aacute;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Để giải quyết vấn đề h&agrave;ng tồn kho, &ocirc;ng Marc Townsend, đại diện CBRE VN cho rằng, lượng h&agrave;ng tồn kho chỉ c&oacute; thể giải quyết trong 2 - 3 năm tới nếu gi&aacute; được điều chỉnh lại, người mua cảm thấy tự tin hơn hoặc c&oacute; khả năng tiếp cận được nhiều nguồn vốn vay hơn.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nhận định về thị trường 2013, CBRE VN vẫn tỏ ra quan ngại, gi&aacute; ch&agrave;o thứ cấp tiếp tục giảm 10%, tiếp nối mức giảm 15% trong năm 2012. Giao dịch sẽ vẫn ở mức thấp, tập trung chủ yếu ở c&aacute;c dự &aacute;n c&oacute; gi&aacute; b&aacute;n đặc biệt hấp dẫn so với đối thủ cạnh tranh v&agrave; tiến độ x&acirc;y dựng tốt. C&aacute;c chủ đầu tư sẽ tiếp tục giảm gi&aacute; ch&agrave;o do &aacute;p lực cạnh tranh, nhưng mức giảm phải l&ecirc;n tới 30%-50% mức gi&aacute; ch&agrave;o ban đầu mới hi vọng ph&aacute;t huy t&aacute;c dụng. Do vậy, 2013 dự kiến sẽ c&ograve;n l&agrave; một năm đầy th&aacute;ch thức với c&aacute;c chủ đầu tư.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo VEF</strong></em></p>\r\n</div>\r\n', 'Không ai tin nhà đất đã đến đáy', 0),
(14, 1, '2013-01-07 14:49:54', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">C&ugrave;ng với những ch&iacute;nh s&aacute;ch khuyến kh&iacute;ch sự ph&aacute;t triển của ph&acirc;n kh&uacute;c nh&agrave; ở b&igrave;nh d&acirc;n của Ch&iacute;nh phủ, h&agrave;ng loạt chủ đầu tư cũng t&igrave;m c&aacute;ch hướng dự &aacute;n đến ph&acirc;n kh&uacute;c đang rộng cửa n&agrave;y. Điều n&agrave;y dự b&aacute;o sẽ tạo n&ecirc;n một l&agrave;n s&oacute;ng c&aacute;c căn hộ b&igrave;nh d&acirc;n, tạo điều kiện cho người d&acirc;n tiếp cận nh&agrave; ở.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Nh&agrave; gi&aacute; thấp tạo s&oacute;ng</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Những động th&aacute;i kh&iacute;ch lệ của Ch&iacute;nh phủ v&agrave;o cuối năm 2012 đ&atilde; tạo n&ecirc;n những t&iacute;n hiệu t&iacute;ch cực tr&ecirc;n thị trường nh&agrave; ở, đặc biệt l&agrave; ph&acirc;n kh&uacute;c nh&agrave; ở gi&aacute; thấp. Theo số liệu thống k&ecirc; của HomeDirect, trong th&aacute;ng cuối c&ugrave;ng của năm 2012, thị trường H&agrave; Nội đ&oacute;n nhận 6 dự &aacute;n căn hộ mở b&aacute;n, tại TPHCM con số n&agrave;y l&ecirc;n tới 10 dự &aacute;n. Đặc biệt, những dự &aacute;n n&agrave;y đều c&oacute; mức gi&aacute; kh&aacute; dễ chịu, dưới 20 triệu đồng/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Bước sang năm 2013, ph&acirc;n kh&uacute;c căn hộ b&igrave;nh d&acirc;n tiếp tục thu h&uacute;t người mua lẫn người b&aacute;n khi nhiều dự &aacute;n gi&aacute; rẻ tiếp tục ch&agrave;o b&aacute;n. Tại H&agrave; Nội, chủ đầu tư dự &aacute;n Khu đ&ocirc; thị mới Đặng X&aacute;, Viglacera Land ch&iacute;nh thức tung ra thị trường 128 căn hộ CT6 thuộc đ&ocirc; thị mới Đặng X&aacute; với gi&aacute; 13,46 triệu đồng/m2. Tổng gi&aacute; trị căn hộ khoảng 800 triệu đồng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trước đ&oacute;, CTCP Đầu tư Dầu kh&iacute; To&agrave;n Cầu GP &ndash; Invest mở b&aacute;n đợt tiếp theo dự &aacute;n Nam Đ&ocirc; Complex (Trương Định, Ho&agrave;ng Mai), với gi&aacute; từ 19,5 triệu đồng/m2 (chưa VAT), kh&aacute;ch h&agrave;ng c&ograve;n được hỗ trợ l&atilde;i suất 6%/năm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="298" src="http://saigonnhadat.vn/upload/UserFiles/image/10_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2020.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Chung cư hỗn hợp Chợ Mơ mở đợt b&aacute;n cuối c&ograve;n 25 triệu đồng/m2.</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ngo&agrave;i Đặng X&aacute; v&agrave; Nam Đ&ocirc; Complex, nhiều dự &aacute;n kh&aacute;c tiếp tục giảm gi&aacute; b&aacute;n. C&oacute; thể kể đến dự &aacute;n Westa (Mỗ Lao, H&agrave; Đ&ocirc;ng), chủ đầu tư đưa ra ch&iacute;nh s&aacute;ch b&aacute;n với gi&aacute; mới chỉ từ 16,7 triệu đồng/m2 (đ&atilde; c&oacute; VAT v&agrave; 2% ph&iacute; bảo tr&igrave;).</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trước đ&oacute;, dự &aacute;n n&agrave;y đ&atilde; 1 lần điều chỉnh gi&aacute; b&aacute;n xuống c&ograve;n 17,9 triệu đồng/m2. Chủ đầu tư dự &aacute;n chung cư hỗn hợp Chợ Mơ (Vinaconex) cũng đ&atilde; c&ocirc;ng bố giảm gi&aacute; khoảng 3 triệu đồng/m2 trong đợt mở b&aacute;n cuối, xuống c&ograve;n 25 triệu đồng/m2&hellip;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Rầm rộ hơn l&agrave; khu vực ph&iacute;a Nam với h&agrave;ng loạt dự &aacute;n gi&aacute; thấp được ch&agrave;o b&aacute;n từ cuối năm 2012 đến nay như Anh Tuấn Apartment (9,7 triệu đồng/m2), Cherry 4 Apartment (12,06 triệu đồng/m2), Tanibuilding Sơn Kỳ 2 (13 triệu đồng/m2), Carina Plaza (13,2 triệu đồng/m2)...</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Thậm ch&iacute;, dự &aacute;n Ngọc B&iacute;ch Residence (B&igrave;nh Dương) do CTCP Du lịch Giang Điền v&agrave; CTCP Địa ốc Kim Oanh mở b&aacute;n với gi&aacute; từ 3,3 triệu đồng/m2 đang thực sự g&acirc;y &ldquo;sốc&rdquo; tr&ecirc;n thị trường.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Đ&aacute;ng ch&uacute; &yacute;, kh&ocirc;ng chỉ c&aacute;c dự &aacute;n mới tập trung v&agrave;o ph&acirc;n kh&uacute;c b&igrave;nh d&acirc;n m&agrave; một số chủ đầu tư nh&agrave; ở thương mại cũng đang rục rịch &ldquo;ngược d&ograve;ng&rdquo; xin chuyển đổi từ nh&agrave; ở thương mại sang nh&agrave; ở thu nhập thấp.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Cụ thể, CTCP Đầu tư - X&acirc;y dựng H&agrave; Nội vừa xin điều chỉnh mục ti&ecirc;u đầu tư quỹ nh&agrave; ở thương mại sang thu nhập thấp của dự &aacute;n khu đ&ocirc; thị Trung Văn mở rộng. Khu đ&ocirc; thị n&agrave;y c&oacute; diện t&iacute;ch 15,6ha, với tổng vốn đầu tư dự &aacute;n gần 400 tỷ đồng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Sau dự &aacute;n mở m&agrave;n n&agrave;y, nhiều chuy&ecirc;n gia dự b&aacute;o, sẽ c&oacute; nhiều dự &aacute;n nối g&oacute;t theo sau xin chuyển đổi sang nh&agrave; ở thu nhập thấp, nh&agrave; ở x&atilde; hội, bởi đ&acirc;y đang l&agrave; xu hướng tất yếu của thị trường.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Người mua dễ thở hơn</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo nhận định của Knight Frank, trong năm 2013, thị trường BĐS H&agrave; Nội dự b&aacute;o sẽ tiếp tục trầm lắng v&agrave; gi&aacute; ch&agrave;o b&aacute;n căn hộ c&oacute; thể suy giảm trong nửa đầu năm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Hầu hết giao dịch vẫn tập trung v&agrave;o căn hộ b&igrave;nh d&acirc;n, l&agrave; ph&acirc;n kh&uacute;c được cả người mua để ở v&agrave; nh&agrave; đầu tư hiện quan t&acirc;m nhiều nhất. Một khảo s&aacute;t tương tự của CBRE Việt Nam tại thị trường BĐS TPHCM trong qu&yacute; IV-2012 căn hộ b&aacute;n ra tiếp tục giảm gi&aacute; tr&ecirc;n tất cả c&aacute;c ph&acirc;n kh&uacute;c.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong đ&oacute;, gi&aacute; căn hộ cao cấp c&oacute; mức giảm nhiều nhất so với qu&yacute; trước (2,3%) v&agrave; rớt 7,6% so với c&ugrave;ng kỳ năm 2011. CBRE cũng dự b&aacute;o, t&igrave;nh trạng giảm gi&aacute; sẽ tiếp tục diễn ra ở nhiều ph&acirc;n kh&uacute;c.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trước đ&oacute;, chia sẻ với c&aacute;c doanh nghiệp BĐS, Bộ trưởng Bộ X&acirc;y dựng Trịnh Đ&igrave;nh Dũng cho rằng trong khi chờ c&aacute;c ch&iacute;nh s&aacute;ch của Nh&agrave; nước, doanh nghiệp phải tự cứu m&igrave;nh trước bằng c&aacute;ch giảm gi&aacute; b&aacute;n căn hộ, cơ cấu lại sản phẩm, nh&acirc;n lực, t&agrave;i ch&iacute;nh&hellip;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nhận định n&agrave;y đ&atilde; biến th&agrave;nh thực tế khi nhiều doanh nghiệp từ chỗ quyết liệt l&ecirc;n &aacute;n c&aacute;c dự &aacute;n b&aacute;n với mức gi&aacute; thấp l&agrave; &ldquo;ph&aacute; gi&aacute;&rdquo;, nay đ&atilde; phải điều chỉnh giảm gi&aacute; b&aacute;n căn hộ của m&igrave;nh. Động th&aacute;i n&agrave;y cho thấy ngưỡng &ldquo;kh&aacute;ng cự&rdquo; của nhiều doanh nghiệp đ&atilde; chạm đ&aacute;y v&agrave; trong thời gian tới sẽ c&ograve;n rất nhiều doanh nghiệp phải t&igrave;m c&aacute;ch giảm gi&aacute; b&aacute;n, tung c&aacute;c g&oacute;i khuyến mại hấp dẫn hay hỗ trợ người mua nhằm k&iacute;ch cầu.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nhiều chuy&ecirc;n gia cũng đưa ra dự b&aacute;o, nửa cuối năm 2013 sẽ l&agrave; thời điểm &ldquo;v&agrave;ng&rdquo; để người d&acirc;n c&oacute; nhu cầu thực c&oacute; thể lựa chọn cho m&igrave;nh một căn hộ vừa t&uacute;i tiền. Bởi khi đ&oacute;, c&aacute;c ch&iacute;nh s&aacute;ch hỗ trợ của Nh&agrave; nước đ&atilde; đi v&agrave;o thực tế, c&oacute; t&aacute;c động r&otilde; rệt l&ecirc;n thị trường, c&aacute;c doanh nghiệp cũng đ&atilde; &ldquo;đổi hướng&rdquo; th&agrave;nh c&ocirc;ng v&agrave; lượng cung cho ph&acirc;n kh&uacute;c n&agrave;y sẽ trở n&ecirc;n dồi d&agrave;o hơn bao giờ hết.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo ĐTTC</strong></em></p>\r\n</div>\r\n', '“Sóng lớn” ở phân khúc bình dân?', 0);
INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(15, 1, '2013-01-07 14:49:56', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Theo b&aacute;o c&aacute;o của c&ocirc;ng ty TNHH Savills Việt Nam, trong qu&yacute; IV/2012, c&aacute;c căn hộ c&oacute; gi&aacute; trị 1-2 tỷ đồng thanh khoản tốt.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="225" src="http://saigonnhadat.vn/upload/UserFiles/image/11_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%202.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="300" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo nghi&ecirc;n cứu của Savills, gi&aacute; trị căn hộ dưới 1 tỷ đồng chiếm khoảng 10%, căn hộ dưới 2 tỷ đồng chiếm khoảng 15%, c&ograve;n lại lượng cung căn hộ tập trung rất nhiều ở ph&acirc;n kh&uacute;c trung v&agrave; cao cấp từ tr&ecirc;n 2 tỷ đến 4 tỷ đồng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Vấn đề thanh khoản của thị trường vẫn được Savills đặc biệt lưu t&acirc;m trong qu&yacute; khi tỷ lệ giao dịch vẫn ở mức thấp, người mua chủ yếu mua căn hộ hạng thấp, ph&acirc;n kh&uacute;c n&agrave;y chỉ chiếm 10% trong tổng số cung căn hộ tr&ecirc;n thị trường.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Trần Như Trung, Ph&oacute; Gi&aacute;m đốc Savills Việt Nam nhận định: &ldquo;vừa qua, rất nhiều c&aacute;c đề xuất th&aacute;o gỡ kh&oacute; khăn đ&atilde; được đưa ra như giảm l&atilde;i suất, giảm thuế, chuyển đổi mục đ&iacute;ch dự &aacute;n,&hellip;Tuy nhi&ecirc;n, cho d&ugrave; những biện ph&aacute;p n&agrave;y c&oacute; thực thi ngay hay phải giữa năm 2013 mới c&oacute; hiệu lực, th&igrave; thị trường năm 2013 vẫn c&ograve;n nhiều kh&oacute; khăn.&rdquo;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&ldquo;Cho d&ugrave; gi&aacute; căn hộ c&oacute; giảm th&ecirc;m đi nữa, nếu c&oacute; giảm th&igrave; ph&acirc;n kh&uacute;c căn hộ dưới 1 tỷ hay dưới 2 tỷ khả quan lắm th&igrave; cũng chỉ tăng th&ecirc;m l&ecirc;n 25% thị phần, c&ograve;n lại chủ yếu l&agrave; căn hộ trung v&agrave; cao cấp.&rdquo; &Ocirc;ng Trung n&oacute;i</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Savills dự b&aacute;o, tổng nguồn cung tương lai khoảng 60.100 căn từ 110 dự &aacute;n tương lai. Trong đ&oacute;, nguồn cung ch&iacute;nh l&agrave; c&aacute;c quận H&agrave; Đ&ocirc;ng v&agrave; Từ Li&ecirc;m, cung cấp gần 26.000 căn, chiếm 43% tổng nguồn cung tương lai. Dự b&aacute;o năm 2013, thị trường sẽ tiếp tục kh&oacute; khăn.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo khảo s&aacute;t tại c&aacute;c s&agrave;n giao dịch bất động sản, thị trường bất động sản trầm lắng đặc biệt ph&acirc;n kh&uacute;c c&oacute; gi&aacute; trị lớn 4-5 tỷ đồng trở l&ecirc;n do lực cầu yếu. Tuy nhi&ecirc;n, c&aacute;c ph&acirc;n kh&uacute;c đất, nh&agrave; c&oacute; trị gi&aacute; thấp tr&ecirc;n dưới 2 tỷ đồng vẫn thu h&uacute;t được người mua. Những giao dịch th&agrave;nh c&ocirc;ng gần đ&acirc;y thường l&agrave; những căn hộ chung cư sắp sử dụng được, c&oacute; gi&aacute; tầm tr&ecirc;n dưới 2 tỷ, hoặc những căn nh&agrave; hoặc đất thổ cư nhỏ, gi&aacute; khoảng 1,5- 2 tỷ đồng. Tuy nhi&ecirc;n, do nguồn cung &iacute;t n&ecirc;n kh&aacute;ch h&agrave;ng kh&ocirc;ng dễ t&igrave;m kiếm được những căn hộ kiểu n&agrave;y.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo vnmedia</strong></em></p>\r\n</div>\r\n', 'Nhà giá trị dưới 2 tỷ đồng giao dịch tốt', 0),
(16, 1, '2013-01-07 14:49:58', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 15px; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Cuối năm, nhu cầu mua nh&agrave; đất an cư, đầu tư của người d&acirc;n tăng đ&atilde; đẩy lượng giao dịch nh&agrave; đất tại Tp.HCM tăng nhẹ.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Giao dịch tăng nhẹ</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Nguyễn Duy Minh, Tổng gi&aacute;m đốc Hưng Thịnh Land, cho biết đang mở b&aacute;n dự &aacute;n căn hộ cao cấp Screc II Metro Apartment nằm trong khu đ&ocirc; thị An Ph&uacute; - An Kh&aacute;nh (Q.2, Tp.HCM), gi&aacute; từ 17 triệu đồng/m2. B&igrave;nh qu&acirc;n mỗi ng&agrave;y c&ocirc;ng ty b&aacute;n được 4 căn hộ, tăng mạnh so với những th&aacute;ng trước đ&oacute;. Trong bối cảnh thị trường kh&oacute; khăn hiện nay th&igrave; đ&acirc;y l&agrave; một kết quả khả quan. Sở dĩ dự &aacute;n được nhiều kh&aacute;ch h&agrave;ng quan t&acirc;m bởi mức gi&aacute; đưa ra thấp hơn khoảng 30-40% so với c&aacute;c dự &aacute;n c&ugrave;ng khu vực, trong khi chất lượng cũng tương đương.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;">Ngo&agrave;i ra, dự &aacute;n nằm trong khu quy hoạch đ&ocirc; thị mới Thủ Thi&ecirc;m, được kết nối hạ tầng kh&aacute; tốt. Khi mua căn hộ kh&aacute;ch h&agrave;ng k&yacute; hợp đồng chỉ 20%, ng&acirc;n h&agrave;ng hỗ trợ cho vay 70% v&agrave; tặng k&egrave;m bộ nội thất trị gi&aacute; 25 triệu đồng. C&ocirc;ng ty cũng đang ch&agrave;o b&aacute;n h&agrave;ng loạt dự &aacute;n kh&aacute;c như Carina (Q.8), với gi&aacute; khoảng 13,2 triệu đồng/m2, kh&aacute;ch h&agrave;ng được chiết khấu 20%; dự &aacute;n 27 Trường Chinh (Q.T&acirc;n B&igrave;nh), với gi&aacute; 12,3 triệu đồng/m2, chiết khấu 15%; dự &aacute;n đất nền T&acirc;n An (Long An), với gi&aacute; khoảng 4 triệu đồng/m2.<br />\r\n<br />\r\n<img alt="" height="333" src="http://saigonnhadat.vn/upload/UserFiles/image/11_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%208.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /><br />\r\n<em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Nhiều dự &aacute;n tranh thủ tung h&agrave;ng nhằm t&igrave;m kiếm kh&aacute;ch h&agrave;ng dịp cuối năm</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">C&ocirc;ng ty địa ốc Ho&agrave;ng Anh S&agrave;i G&ograve;n cũng đang mở b&aacute;n dự &aacute;n &Acirc;u Cơ Tower (Q.T&acirc;n Ph&uacute;), với gi&aacute; b&aacute;n khoảng 1,2 tỉ đồng/căn hộ. Kh&aacute;ch h&agrave;ng c&oacute; thể được chiết khấu đến 100 triệu đồng khi thanh to&aacute;n 95% gi&aacute; trị căn hộ. Hiện đ&atilde; xong phần th&ocirc;, dự kiến cuối qu&yacute; 1/2013 sẽ giao nh&agrave; v&agrave; Ng&acirc;n h&agrave;ng BIDV hỗ trợ vay theo l&atilde;i suất hỗ trợ k&iacute;ch cầu của Ch&iacute;nh phủ, với thời hạn 15 năm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">C&ocirc;ng ty SSG2 trong lễ cất n&oacute;c v&agrave; kh&aacute;nh th&agrave;nh căn hộ mẫu dự &aacute;n Thảo Điền Pearl (Q.2) mới đ&acirc;y cũng đ&atilde; c&ocirc;ng bố mở b&aacute;n 30 căn hộ tại dự &aacute;n với gi&aacute; khoảng 30 triệu đồng/m2. Theo chủ đầu tư, đến nay hơn 80% số lượng căn hộ của dự &aacute;n đ&atilde; được kh&aacute;ch h&agrave;ng đặt mua. C&ocirc;ng ty Him Lam Land mở b&aacute;n dự &aacute;n Him Lam Riverside (Q.7), kh&aacute;ch h&agrave;ng nộp 50% sẽ nhận nh&agrave;. Nếu kh&ocirc;ng ở, c&ocirc;ng ty sẽ thu&ecirc; lại gi&aacute; từ 16 - 24 triệu đồng/th&aacute;ng t&ugrave;y theo diện t&iacute;ch mua căn hộ; hỗ trợ to&agrave;n bộ l&atilde;i vay trong năm đầu ti&ecirc;n nếu kh&aacute;ch h&agrave;ng vay vốn.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Theo &ocirc;ng Trương An Dương, Trưởng bộ phận nghi&ecirc;n cứu C&ocirc;ng ty Savills Việt Nam, hiện nay nguồn cung căn hộ tr&ecirc;n thị trường Tp.HCM c&oacute; khoảng 83.200 căn hộ từ 280 dự &aacute;n. Trong qu&yacute; 4/2012, ri&ecirc;ng căn hộ hạng C đ&atilde; c&oacute; 4 dự &aacute;n tham gia thị trường cung cấp th&ecirc;m 1.500 căn hộ. Ph&acirc;n kh&uacute;c biệt thự, nh&agrave; liền kề cung cấp khoảng 3.200 căn. &Ocirc;ng Dương cho rằng đ&atilde; nhận thấy sự hồi phục nhẹ của thị trường trong những th&aacute;ng cuối năm. Trong đ&oacute;, số lượng căn hộ b&aacute;n ra khoảng 1.100 căn.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Tr&ocirc;ng chờ kiều hối</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Theo &ocirc;ng Minh, những th&aacute;ng cuối năm thị trường địa ốc đ&oacute;n nhiều t&iacute;n hiệu khả quan như ng&acirc;n h&agrave;ng giảm l&atilde;i suất huy động th&ecirc;m 1%, c&aacute;c g&oacute;i giải cứu được c&aacute;c địa phương v&agrave; Ch&iacute;nh phủ c&ocirc;ng bố. Ngo&agrave;i ra, lượng kiều hối năm nay về Tp.HCM hơn 4 tỉ USD, cả nước dự kiến gần 10 tỉ USD. Đ&acirc;y l&agrave; cơ hội để c&aacute;c c&ocirc;ng ty bung h&agrave;ng h&uacute;t kh&aacute;ch.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Một l&atilde;nh đạo C&ocirc;ng ty SSG 2 cho biết qua khảo s&aacute;t c&aacute;c căn hộ cao cấp tại khu trung t&acirc;m Tp.HCM, hiện c&oacute; rất nhiều Việt kiều dọn về ở v&agrave;o dịp cuối năm như SaiGon Pearl, The Manor... Ch&iacute;nh v&igrave; vậy, c&ocirc;ng ty đ&atilde; tập trung giới thiệu nh&agrave; mẫu của dự &aacute;n Thảo Điền Pearl để đ&oacute;n nguồn kiều hối cuối năm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Tuy nhi&ecirc;n, để c&oacute; thể l&agrave;m cho thị trường tốt l&ecirc;n, lấy lại l&ograve;ng tin cho những người tham gia thị trường, &ocirc;ng Đo&agrave;n Ch&iacute; Thanh, Tổng gi&aacute;m đốc C&ocirc;ng ty Ho&agrave;ng Anh S&agrave;i G&ograve;n, đề xuất Ch&iacute;nh phủ, c&aacute;c bộ ng&agrave;nh n&ecirc;n nhanh ch&oacute;ng bắt tay v&agrave;o thực hiện những điều đ&atilde; hứa. Bởi c&agrave;ng &ldquo;ng&acirc;m&rdquo; l&acirc;u thị trường sẽ c&agrave;ng mất niềm tin, c&agrave;ng kh&oacute; khăn hơn.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Khi Ch&iacute;nh phủ c&ocirc;ng bố g&oacute;i cho vay l&atilde;i suất thấp l&ecirc;n đến 20.000 - 40.000 tỉ đồng, xử l&yacute; nợ xấu, c&aacute;c ch&iacute;nh s&aacute;ch miễn giảm thuế&hellip; đ&atilde; gi&uacute;p thị trường hưng phấn trở lại.Tuy nhi&ecirc;n, đ&oacute; chỉ l&agrave; một liều thuốc tinh thần. Nếu những ch&iacute;nh s&aacute;ch tr&ecirc;n th&agrave;nh hiện thực, chắc chắn thị trường sẽ khởi sắc. &ldquo;Với những đề xuất cứu thị trường bất động sản sớm đưa v&agrave;o thực tiễn, chủ đầu tư tạo niềm tin cho kh&aacute;ch h&agrave;ng bằng việc cam kết giữ đ&uacute;ng tiến độ x&acirc;y dựng dự &aacute;n cũng như chất lượng c&ocirc;ng với c&aacute;c ch&iacute;nh s&aacute;ch khuyến m&atilde;i, hậu m&atilde;i, chắc chắn sau tết thị trường sẽ c&oacute; sinh kh&iacute; mới&rdquo;, &ocirc;ng Thanh cho hay.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">(Theo TNO) </strong></em></p>\r\n</div>\r\n', 'Giao dịch địa ốc Tp.HCM tăng nhẹ', 0);
INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(17, 1, '2013-01-11 17:45:42', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Giới chuy&ecirc;n gia v&agrave; l&atilde;nh đạo c&aacute;c DN BĐS lớn đều chia sẻ nhận định, năm 2013, nh&agrave; gi&aacute; thấp đ&aacute;p ứng nhu cầu ở thực mới c&oacute; t&iacute;nh thanh khoản tốt. PV đ&atilde; ghi nhận một số &yacute; kiến.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="319" src="http://saigonnhadat.vn/upload/UserFiles/image/03_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2016.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /><br />\r\n<em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Nhiều ch&iacute;nh s&aacute;ch hỗ trợ thị trường BĐS sẽ được triển khai trong năm 2013</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Trần Như Trung, Ph&oacute; gi&aacute;m đốc Savills H&agrave; Nội</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="224" src="http://saigonnhadat.vn/upload/UserFiles/image/03_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2017.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="180" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong giai đoạn 2010 - 2012, nền kinh tế Việt Nam phải đối mặt với nhiều th&aacute;ch thức, n&ecirc;n mục ti&ecirc;u của Ch&iacute;nh phủ chủ yếu tập trung v&agrave;o kiềm chế lạm ph&aacute;t, ổn định kinh tế vĩ m&ocirc;. Tuy nhi&ecirc;n, đến những th&aacute;ng cuối năm 2012, vấn đề &ldquo;giải cứu&rdquo; thị trường BĐS đ&atilde; được đặt ra v&agrave; ch&iacute;nh thức được Ch&iacute;nh phủ x&aacute;c định như một lĩnh vực trọng t&acirc;m để vực dậy nền kinh tế. Điều đ&oacute; cho thấy vị tr&iacute; của thị trường n&agrave;y l&agrave; rất quan trọng trong hoạt động kinh tế của nước ta.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ch&iacute;nh s&aacute;ch của Ch&iacute;nh phủ đưa ra lần n&agrave;y chủ yếu tập trung v&agrave;o ph&aacute;t triển nh&agrave; ở x&atilde; hội v&agrave; huy động nguồn vốn cho thị trường. Theo t&iacute;nh to&aacute;n của ch&uacute;ng t&ocirc;i, ph&acirc;n kh&uacute;c nh&agrave; ở x&atilde; hội hiện nay nhiều nhất cũng chỉ chiếm từ 20 - 25% thị trường, như thế việc giải cứu ph&acirc;n kh&uacute;c n&agrave;y cũng chỉ tạo đ&agrave; cho to&agrave;n thị trường c&oacute; những chuyển biến bước đầu. Giải ph&aacute;p về vốn, c&oacute; thể huy động đến v&agrave;i chục ng&agrave;n tỷ đồng, nhưng so với cả thị trường BĐS hiện nay cũng chỉ như &ldquo;muối bỏ bể&rdquo;. V&igrave; vậy, đ&aacute;nh gi&aacute; một c&aacute;ch tổng thể th&igrave; g&oacute;i cứu trợ d&agrave;nh cho thị trường n&agrave;y thời gian tới chỉ như một &ldquo;c&uacute; hu&yacute;nh&rdquo; th&uacute;c đẩy thị trường vận động.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong năm 2013, điều kỳ vọng nhất cho thị trường BĐS l&agrave; tăng t&iacute;nh thanh khoản. Để thực hiện được điều n&agrave;y, trước hết cả Ch&iacute;nh phủ, DN v&agrave; người ti&ecirc;u d&ugrave;ng đều phải x&aacute;c định r&otilde; m&igrave;nh c&oacute; muốn tham gia v&agrave;o thị trường n&agrave;y nữa hay kh&ocirc;ng? Nếu chỉ cần 1 trong 3 yếu tố tr&ecirc;n rời bỏ thị trường th&igrave; thanh khoản kh&ocirc;ng bao giờ được cải thiện.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Đặng H&ugrave;ng V&otilde;, Nguy&ecirc;n Thứ trưởng Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="214" src="http://saigonnhadat.vn/upload/UserFiles/image/03_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2018.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="180" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Việc Ch&iacute;nh phủ sẽ ban h&agrave;nh h&agrave;ng loạt ch&iacute;nh s&aacute;ch nhằm th&aacute;o gỡ kh&oacute; khăn cho thị trường BĐS trong năm 2013 với những nội dung cụ thể như &ldquo;giải quyết lượng BĐS tồn kho gắn với giải quyết nợ xấu&rdquo;, &ldquo;cơ hội ph&aacute;t triển ph&acirc;n kh&uacute;c nh&agrave; ở gi&aacute; thấp l&agrave;m ấm lại thị trường&rdquo; v&agrave; &ldquo;c&aacute;c giải ph&aacute;p hỗ trợ từ ph&iacute;a quản l&yacute; nh&agrave; nước&rdquo; đ&atilde; đ&aacute;nh tr&uacute;ng v&agrave;o những tồn tại của thị trường trong năm 2012.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ưu đ&atilde;i t&iacute;n dụng cho b&ecirc;n cầu để tăng khả năng thanh to&aacute;n; c&aacute;c địa phương c&oacute; thể mua BĐS tồn đọng l&agrave;m nh&agrave; ở c&ocirc;ng vụ, nh&agrave; ở x&atilde; hội hay nh&agrave; ở t&aacute;i định cư; chia nhỏ c&aacute;c căn hộ lớn để giảm tổng gi&aacute; trị căn hộ cho ph&ugrave; hợp với khả năng thanh to&aacute;n của cầu; r&agrave; so&aacute;t lại c&aacute;c dự &aacute;n đang triển khai để điều chỉnh nguồn cung; giảm hoặc ho&atilde;n nghĩa vụ t&agrave;i ch&iacute;nh cho c&aacute;c DN&hellip; Nhiều giải ph&aacute;p đ&atilde; được đề ra trong thời gian qua nhưng cũng chưa được ch&iacute;nh thức ph&ecirc; duyệt.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ch&iacute;nh v&igrave; vậy, lượng BĐS gi&aacute; cao tồn kho hầu như vẫn c&ograve;n nguy&ecirc;n, ph&acirc;n kh&uacute;c nh&agrave; ở gi&aacute; thấp đ&atilde; được khởi động nhưng cung hầu như kh&ocirc;ng đ&aacute;ng kể, c&aacute;c giải ph&aacute;p từ ph&iacute;a quản l&yacute; nh&agrave; nước vẫn đang được thảo luận. Trong khi đ&oacute;, việc triển khai ch&iacute;nh s&aacute;ch v&agrave;o cuộc sống bao giờ cũng c&oacute; độ trễ nhất định.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tất cả những vướng mắc tr&ecirc;n sẽ l&agrave;m chậm qu&aacute; tr&igrave;nh chuyển biến của thị trường trong ngữ cảnh l&ograve;ng tin của kh&aacute;ch h&agrave;ng ng&agrave;y một mất dần. V&igrave; vậy, việc t&aacute;i cấu tr&uacute;c cần thực hiện quyết liệt. Thị trường BĐS t&aacute;i cấu tr&uacute;c phải c&oacute; t&iacute;nh chuy&ecirc;n nghiệp cao hơn, đầy đủ th&ocirc;ng tin minh bạch, loại bỏ tham nhũng v&agrave; đầu cơ, phục vụ trực tiếp nhu cầu ở của người d&acirc;n. Chỉ khi n&agrave;o những vấn đề tr&ecirc;n được giải quyết th&igrave; thị trường mới c&oacute; cơ hội phục hồi.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng L&ecirc; Thanh Thản, Gi&aacute;m đốc C&ocirc;ng ty X&acirc;y dựng số 1 Lai Ch&acirc;u</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Gần đ&acirc;y, nhiều DN, trong đ&oacute; c&oacute; cả c&aacute;c DN Nh&agrave; nước mời ch&uacute;ng t&ocirc;i hợp t&aacute;c triển khai dự &aacute;n, v&igrave; họ đ&atilde; để chậm tiến độ qu&aacute; l&acirc;u. C&oacute; những dự &aacute;n lớn quy m&ocirc; tới hơn 100 h&eacute;c-ta tại H&agrave; Nội. Với t&igrave;nh h&igrave;nh như vậy, Nh&agrave; nước cần c&oacute; cơ chế khuyến kh&iacute;ch c&aacute;c nh&agrave; đầu tư c&oacute; năng lực thực sự v&agrave;o đầu tư c&aacute;c dự &aacute;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="239" src="http://saigonnhadat.vn/upload/UserFiles/image/03_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2019.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Chung cư Đại Thanh được mở b&aacute;n với gi&aacute; 10 triệu đồng/m2 đ&atilde; thu h&uacute;t sự quan t&acirc;m của nhiều kh&aacute;ch h&agrave;ng</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Năm 2013, t&ocirc;i tin TP. H&agrave; Nội sẽ c&oacute; những cơ chế khuyến kh&iacute;ch c&aacute;c nh&agrave; đầu tư c&oacute; năng lực t&agrave;i ch&iacute;nh đầu tư dự &aacute;n, thậm ch&iacute; mời nh&agrave; đầu tư v&agrave;o những khu &ldquo;đất sạch&rdquo; để triển khai, v&igrave; đất thu hồi rồi kh&ocirc;ng đưa v&agrave;o sử dụng để bỏ hoang sẽ l&atilde;ng ph&iacute; v&agrave; tạo ra sự x&ocirc;i đỗ rất xấu, dở dang cho Th&agrave;nh phố. Những ch&iacute;nh s&aacute;ch về t&agrave;i kh&oacute;a hiện DN rất quan t&acirc;m v&agrave; mong muốn sớm được triển khai như việc tạm chưa thu tiền sử dụng đất, để DN c&oacute; sản phẩm, b&aacute;n h&agrave;ng rồi mới thu theo tiến độ&hellip;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ch&uacute;ng t&ocirc;i x&aacute;c định, đầu tư kinh doanh BĐS giai đoạn n&agrave;y kh&ocirc;ng c&oacute; l&atilde;i hoặc chỉ l&atilde;i rất &iacute;t, chủ yếu tạo c&ocirc;ng ăn việc l&agrave;m cho người lao động v&agrave; lu&acirc;n chuyển d&ograve;ng tiền. Nếu được giao đất sạch v&agrave; c&oacute; cơ chế ph&ugrave; hợp, khuyến kh&iacute;ch DN đầu tư nh&agrave; gi&aacute; thấp, ch&uacute;ng t&ocirc;i c&oacute; thể b&aacute;n c&aacute;c căn hộ chung cư với gi&aacute; th&agrave;nh 7 - 9 triệu đồng/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Nguyễn Thế Điệp, Chủ tịch HĐQT CTCP Đầu tư Reenco S&ocirc;ng Hồng (th&agrave;nh vi&ecirc;n Tổng c&ocirc;ng ty S&ocirc;ng Hồng)</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="211" src="http://saigonnhadat.vn/upload/UserFiles/image/03_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2020.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="180" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Năm 2012, thị trường BĐS rất kh&oacute; khăn, c&aacute;c sản phẩm BĐS tồn kho nhiều. Song, c&oacute; một thực tế l&agrave; c&aacute;c sản phẩm ph&ugrave; hợp với nhu cầu của số đ&ocirc;ng kh&aacute;ch h&agrave;ng v&agrave; c&oacute; thanh khoản tốt th&igrave; vẫn thiếu, như ph&acirc;n kh&uacute;c nh&agrave; diện t&iacute;ch nhỏ, c&oacute; mức gi&aacute; vừa phải. T&ocirc;i cho rằng, trong năm 2013 v&agrave; c&aacute;c năm sau, sẽ c&oacute; nhiều DN tham gia ph&aacute;t triển nh&agrave; c&oacute; mức gi&aacute; ph&ugrave; hợp với nhu cầu của số đ&ocirc;ng kh&aacute;ch h&agrave;ng. Đặc biệt, với nhiều ch&iacute;nh s&aacute;ch ưu đ&atilde;i như: gi&atilde;n thuế, giảm thuế, khoanh nợ, mở t&iacute;n dụng&hellip;, DN sẽ c&oacute; th&ecirc;m cơ hội giảm gi&aacute; sản phẩm hơn.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nh&igrave;n lại năm 2012, do thị trường kh&oacute; khăn n&ecirc;n c&aacute;c DN BĐS đều bị ảnh hưởng nặng nề. Ngay Reenco S&ocirc;ng Hồng, mặc d&ugrave; kh&ocirc;ng vấp v&agrave;o nợ nần do đ&atilde; lường trước được kh&oacute; khăn. Nhưng với t&igrave;nh h&igrave;nh như hiện nay, ch&uacute;ng t&ocirc;i rất kh&oacute; c&oacute; thể huy động được nguồn vốn, trong khi vốn tự c&oacute; c&oacute; hạn n&ecirc;n kh&ocirc;ng tiếp tục thực hiện được dự &aacute;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Sang năm 2013, ch&uacute;ng t&ocirc;i sẽ c&oacute; sự điều chỉnh nhất định để ph&aacute;t triển d&ograve;ng sản phẩm ph&ugrave; hợp với nhu cầu của số đ&ocirc;ng kh&aacute;ch h&agrave;ng, trong đ&oacute;, c&oacute; việc hướng đến ph&aacute;t triển nh&agrave; ở x&atilde; hội. Đồng thời, ch&uacute;ng t&ocirc;i cũng sẽ ph&aacute;t triển cả mảng x&acirc;y lắp, vốn l&agrave; một thế mạnh của Tổng c&ocirc;ng ty S&ocirc;ng Hồng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">&Ocirc;ng Trương Tr&iacute; Ki&ecirc;n, Ph&oacute; tổng gi&aacute;m đốc CTCP Him Lam Thủ Đ&ocirc;</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="261" src="http://saigonnhadat.vn/upload/UserFiles/image/03_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2021.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="180" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Thị trường BĐS năm 2012 v&ocirc; c&ugrave;ng kh&oacute; khăn l&agrave; do nguồn cung trước đ&oacute; ph&aacute;t triển chạy theo nhu cầu đầu tư, đầu cơ nhiều hơn l&agrave; nhu cầu thực. Khi đầu cơ giảm, nhu cầu thực nổi l&ecirc;n, khiến ph&acirc;n ph&uacute;c BĐS đ&aacute;p ứng nhu cầu thực c&oacute; t&iacute;nh thanh khoản tốt.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tại Việt Nam, thu nhập b&igrave;nh qu&acirc;n của đại đa số người d&acirc;n rất thấp so với gi&aacute; trị BĐS. Do vậy, ph&acirc;n kh&uacute;c nh&agrave; ở thu nhập thấp, c&oacute; diện t&iacute;ch nhỏ, gi&aacute; trị tr&ecirc;n dưới 1 tỷ đồng l&agrave; những sản phẩm ph&ugrave; hợp v&agrave; c&oacute; t&iacute;nh thanh khoản cao.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">T&ocirc;i cho rằng, thị trường BĐS ở ta đang trong giai đoạn cạnh tranh ho&agrave;n hảo nhất, theo đ&uacute;ng quy luật cung cầu. V&igrave; thế, sang năm 2013, xu hướng thị trường cũng kh&oacute; đi chệch xu hướng của năm 2012. Nghĩa l&agrave; thị trường BĐS sẽ tiếp tục ph&aacute;t triển ở ph&acirc;n kh&uacute;c nh&agrave; cho người thu nhập thấp v&agrave; trung b&igrave;nh, phục vụ nhu cầu ở thật.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">C&oacute; thể n&oacute;i năm 2012 l&agrave; năm v&ocirc; c&ugrave;ng kh&oacute; khăn, thanh khoản thị trường thấp, n&ecirc;n DN n&agrave;o c&agrave;ng đưa nhiều sản phẩm kh&ocirc;ng ph&ugrave; hợp thị trường, DN đ&oacute; c&agrave;ng kh&oacute; khăn v&agrave; tăng nợ xấu. Tuy nhi&ecirc;n, Him Lam Thủ Đ&ocirc; (th&agrave;nh vi&ecirc;n của Tập đo&agrave;n Him Lam) đ&atilde; c&oacute; những định hướng ri&ecirc;ng, n&ecirc;n hoạt động của C&ocirc;ng ty vẫn tương đối ổn định. Cụ thể, trong năm 2012, ch&uacute;ng t&ocirc;i đi theo chiến lược đi t&igrave;m mua lại c&aacute;c dự &aacute;n b&aacute;n rẻ hoặc những dự &aacute;n c&oacute; gi&aacute; cả hợp l&yacute; c&oacute; thể mang lại lợi nhuận.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Sang năm 2013, khi cầu thực đ&atilde; r&otilde;, c&aacute;c ch&iacute;nh s&aacute;ch của Nh&agrave; nước cũng hỗ trợ nhiều c&aacute;c chủ đầu tư ph&aacute;t triển dự &aacute;n ph&ugrave; hợp nhu cầu thị trường. V&igrave; thế, c&aacute;c DN BĐS v&agrave; bản th&acirc;n Him Lam Thủ Đ&ocirc; cũng sẽ định hướng cho ra c&aacute;c sản phẩm phục vụ nhu cầu thực của thị trường.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo Đầu Tư Chứng Kho&aacute;n</strong></em></p>\r\n</div>\r\n', 'BĐS 2013: Nhà giá thấp lên ngôi', 0);
INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(18, 1, '2013-01-11 17:46:09', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Từ ng&agrave;y 1/1/2013, gi&aacute; đất mặt tiền cao nhất tại TP HCM vẫn thuộc về 3 tuyến đường L&ecirc; Lợi, Đồng Khởi, Nguyễn Huệ (quận 1) ở mức 81 triệu đồng mỗi m2. Từ năm 2008 đến nay gi&aacute; đất v&agrave;ng kh&ocirc;ng đổi trong suốt 5 năm qua.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">UBND TP HCM vừa k&yacute; Quyết định ban h&agrave;nh gi&aacute; c&aacute;c loại đất tr&ecirc;n địa b&agrave;n k&egrave;m theo bảng gi&aacute; c&aacute;c loại đất năm 2013 để &aacute;p dụng từ ng&agrave;y 1/1. Bảng gi&aacute; đất mới kh&ocirc;ng c&oacute; nhiều thay đổi so với năm 2012 v&igrave; mục đ&iacute;ch kiềm chế lạm ph&aacute;t, ổn định kinh tế vĩ m&ocirc;, đảm bảo an sinh x&atilde; hội theo chủ trương của Ch&iacute;nh phủ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Theo đ&oacute;, đất mặt tiền đường ở đ&ocirc; thị cao nhất vẫn thuộc c&aacute;c khu đất v&agrave;ng tại trung t&acirc;m quận 1, thuộc 3 tuyến đường L&ecirc; Lợi, Đồng Khởi v&agrave; Nguyễn Huệ ở mức 81 triệu đồng mỗi m2. Thấp nhất l&agrave; đất tại khu d&acirc;n cư Thiềng Liềng, huyện Cần Giờ với gi&aacute; 110.000 đồng mỗi m2. Đất ở n&ocirc;ng th&ocirc;n c&oacute; mức trần l&agrave; 2,25 triệu đồng mỗi m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="321" src="http://saigonnhadat.vn/upload/UserFiles/image/03_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%202.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Đất v&agrave;ng (mặt tiền) 3 tuyến đường L&ecirc; Lợi, Đồng Khởi, Nguyễn Huệ (quận 1) ở mức 81 triệu đồng mỗi m2, kh&ocirc;ng đổi từ năm 2008 đến năm 2013. Ảnh: Vũ L&ecirc;.</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Đất phi n&ocirc;ng nghiệp bổ sung gi&aacute; cho 26 tuyến, đoạn đường mới đặt t&ecirc;n v&agrave; loại bỏ gi&aacute; 2 tuyến, đoạn đường tại quận 2 v&agrave; huyện H&oacute;c M&ocirc;n. Quận Thủ Đức c&oacute; th&ecirc;m một tuyến đường mới mở l&agrave; nh&aacute;nh dẫn đến cầu vượt n&uacute;t giao th&ocirc;ng cầu G&ograve; Dưa. 3 tuyến đường, đoạn đường mới n&acirc;ng cấp tại quận 8, B&igrave;nh T&acirc;n v&agrave; T&acirc;n B&igrave;nh được điều chỉnh gi&aacute;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Đất n&ocirc;ng nghiệp năm 2013 giữ nguy&ecirc;n mức gi&aacute; đất như năm 2012 với 5 bảng gi&aacute; kh&ocirc;ng đổi cho c&aacute;c loại: đất trồng l&uacute;a v&agrave; c&acirc;y h&agrave;ng năm, đất trồng c&acirc;y l&acirc;u năm, đất rừng c&aacute;c loại v&agrave; đất l&agrave;m muối.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Bảng gi&aacute; đất năm 2013 sẽ được &aacute;p dụng v&agrave;o 7 mục đ&iacute;ch:</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">1. T&iacute;nh thuế sử dụng đất v&agrave; chuyển quyền sử dụng đất;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">2. T&iacute;nh tiền sử dụng đất v&agrave; tiền thu&ecirc; đất khi giao đất, cho thu&ecirc; đất kh&ocirc;ng th&ocirc;ng qua đấu gi&aacute; quyền sử dụng đất hoặc đấu thầu dự &aacute;n;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">3. T&iacute;nh gi&aacute; trị quyền sử dụng đất khi giao đất kh&ocirc;ng thu tiền sử dụng đất cho c&aacute;c tổ chức, c&aacute; nh&acirc;n;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">4. X&aacute;c định gi&aacute; trị quyền sử dụng đất để t&iacute;nh v&agrave;o gi&aacute; trị t&agrave;i sản của doanh nghiệp nh&agrave; nước khi doanh nghiệp cổ phần h&oacute;a, lựa chọn h&igrave;nh thức giao đất c&oacute; thu tiền sử dụng đất;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">5. T&iacute;nh gi&aacute; trị quyền sử dụng đất để thu lệ ph&iacute; trước bạ;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">6. T&iacute;nh bồi thường khi Nh&agrave; nước thu hồi đất sử dụng v&agrave;o mục đ&iacute;ch quốc ph&ograve;ng, an ninh, lợi &iacute;ch quốc gia, lợi &iacute;ch c&ocirc;ng cộng v&agrave; ph&aacute;t triển kinh tế;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">7. T&iacute;nh bồi thường v&agrave; mức xử phạt vi phạm h&agrave;nh ch&iacute;nh đối với người vi phạm ph&aacute;p luật về đất đai g&acirc;y thiệt hại cho Nh&agrave; nước.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo VnExpress</strong></em></p>\r\n</div>\r\n', 'Đất vàng ở TP HCM giá 81 triệu đồng', 0),
(19, 1, '2013-01-11 17:46:42', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 15px; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">L&acirc;u nay do một nh&oacute;m người đầu cơ, thao t&uacute;ng v&agrave; l&agrave;m gi&aacute; bất động sản để trục lợi. Khi thị trường nh&agrave; đất &quot;đ&oacute;ng băng&quot; đ&atilde; g&acirc;y thiệt hại lớn cho nền kinh tế. Hiện nay, nhiều c&ocirc;ng nh&acirc;n ng&agrave;nh x&acirc;y dựng thất nghiệp, h&agrave;ng triệu tấn xi-măng v&agrave; vật liệu x&acirc;y dựng tồn kho vốn h&agrave;ng trăm ngh&igrave;n tỷ đồng nằm tại những căn nh&agrave; v&agrave; biệt thự x&acirc;y dở dang v&agrave; tại những l&ocirc; đất bỏ hoang.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Trong khi đ&oacute;, nhiều doanh nghiệp phải giải thể v&igrave; kh&ocirc;ng c&oacute; lương trả cho c&ocirc;ng nh&acirc;n v&agrave; h&agrave;ng chục ngh&igrave;n doanh nghiệp đang &quot;sống dở, chết dở&quot; v&igrave; thiếu vốn. &ETH;ất đai kh&ocirc;ng sinh lời, nh&agrave; ở v&agrave; biệt thự kh&ocirc;ng sử dụng đ&oacute; l&agrave; sự l&atilde;ng ph&iacute; rất lớn t&agrave;i nguy&ecirc;n v&agrave; nguồn lực quốc gia.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Vậy Nh&agrave; nước n&ecirc;n triển khai thế n&agrave;o c&aacute;c giải ph&aacute;p c&oacute; t&iacute;nh đột ph&aacute; v&agrave; đủ mạnh để chấn chỉnh thị trường bất động sản, tạo đ&agrave; cho nền kinh tế ph&aacute;t triển v&agrave; ph&aacute;t triển bền vững?</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Nh&agrave; nước phải d&ugrave;ng c&ocirc;ng cụ trong đ&oacute; c&oacute; ch&iacute;nh s&aacute;ch thuế nh&agrave; ở, đất ở để điều tiết v&agrave; l&agrave;m l&agrave;nh mạnh thị trường bất động sản, triệt ti&ecirc;u nạn đầu cơ nh&agrave; đất v&agrave; triệt ti&ecirc;u lợi &iacute;ch nh&oacute;m. Theo đ&oacute;, quy định mỗi c&ocirc;ng d&acirc;n c&oacute; quyền sở hữu một căn nh&agrave; (hoặc một biệt thự) để ở v&agrave; phải nộp thuế nh&agrave; đất như hiện nay. Từ căn nh&agrave; thứ hai phải nộp thuế nh&agrave; đất 10%. Từ căn nh&agrave; thứ ba phải nộp thuế nh&agrave; đất 15% v&agrave; căn nh&agrave; thứ tư phải nộp thuế nh&agrave; đất 20%... &ETH;ối với đất ở qu&aacute; hạn mức cũng phải nộp thuế với mức thu hợp l&yacute;. Nghĩa l&agrave; thuế nh&agrave; đất phải nộp tăng theo lũy tiến. Phải xử l&yacute; nghi&ecirc;m người trốn thuế nh&agrave;, đất v&agrave; ai nộp chậm phải t&iacute;nh l&atilde;i. Ai kh&ocirc;ng nộp thuế nh&agrave; đất th&igrave; b&aacute;n nh&agrave; đất thừa cho người c&oacute; nhu cầu sử dụng hoặc b&aacute;n cho Nh&agrave; nước để đem cho thu&ecirc;. Nh&agrave; nước n&ecirc;n từng bước chiếm lĩnh thị trường x&acirc;y nh&agrave; ở cho người d&acirc;n thu&ecirc;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&ETH;&acirc;y l&agrave; một trong những giải ph&aacute;p nhằm triệt ti&ecirc;u nạn đầu cơ nh&agrave; đất, triệt ti&ecirc;u lợi &iacute;ch nh&oacute;m, đồng thời l&agrave;m cho thị trường bất động sản ph&aacute;t triển l&agrave;nh mạnh; tạo đ&agrave; cho nền kinh tế cả nước ph&aacute;t triển bền vững. Việc n&agrave;y n&ecirc;n được triển khai thực hiện c&agrave;ng sớm, c&agrave;ng tốt.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo Nh&acirc;n D&acirc;n</strong></em></p>\r\n</div>\r\n', 'Cần lành mạnh hóa thị trường bất động sản', 0);
INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(20, 1, '2013-01-11 17:47:14', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Bất động sản gặp kh&oacute;, &aacute;p lực h&agrave;ng tồn kho, kh&ocirc;ng &iacute;t chủ đầu tư đ&atilde; mạnh tay giảm gi&aacute; căn hộ, điều n&agrave;y t&aacute;c động mạnh mẽ tới thị trường bất động sản. Trong năm qua, ch&uacute;ng ta đ&atilde; chứng kiến kh&ocirc;ng &iacute;t c&uacute; sốc về gi&aacute; hiếm c&oacute;.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">B&igrave;nh d&acirc;n thắng cao cấp</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Đầu th&aacute;ng 11/2012, thị trường bất động sản đ&atilde; bị một cơn chấn động khi chủ đầu tư dự &aacute;n chung cư Đại Thanh quyết định giảm gi&aacute; căn hộ dự &aacute;n n&agrave;y xuống chỉ c&ograve;n 10 triệu đồng/m2. Tuy mức gi&aacute; n&agrave;y chỉ &aacute;p dụng với một số căn hộ v&agrave; c&oacute; điều kiện k&egrave;m theo nhưng n&oacute; đ&atilde; tạo n&ecirc;n một cơn &quot;sốc&quot; kh&ocirc;ng chỉ cho người mua m&agrave; ngay cả c&aacute;c chủ đầu tư dự &aacute;n kh&aacute;c cũng phải d&egrave; chừng. Trước đ&oacute;, với mức b&aacute;n 14 triệu đồng/m2, căn hộ Đại Thanh đ&atilde; l&agrave;m khuấy động thị trường căn hộ ph&acirc;n kh&uacute;c gi&aacute; rẻ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ước t&iacute;nh tổng gi&aacute; trị của mỗi căn hộ sẽ dao động từ 400 - 600 triệu đồng. Đ&acirc;y cũng l&agrave; mức gi&aacute; hấp dẫn nhất tr&ecirc;n thị trường hiện nay v&agrave; được đ&aacute;nh gi&aacute; l&agrave; rẻ hơn rất nhiều so với gi&aacute; nh&agrave; thu nhập thấp. Đặc biệt, với diện t&iacute;ch căn hộ kh&aacute; nhỏ, dao động từ 42 - 66,1m2, đ&acirc;y l&agrave; lựa th&iacute;ch hợp với nhiều người c&oacute; nhu cầu về nh&agrave; ở.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ngay trong đợt mở b&aacute;n đầu ti&ecirc;n, chủ đầu tư đ&atilde; b&aacute;n hết veo h&agrave;ng trăm căn hộ. Nguồn cung gi&aacute; rẻ c&ograve;n hạn chế, kh&ocirc;ng &iacute;t nh&agrave; đầu tư thứ cấp, s&agrave;n m&ocirc;i giới c&oacute; cơ hội hiếm c&oacute; để &quot;lướt s&oacute;ng&quot; trong l&uacute;c thị trường đ&oacute;ng băng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Cũng ch&iacute;nh v&igrave; mức b&aacute;n gi&aacute; n&agrave;y, chủ đầu tư dự &aacute;n chung cư Đại Thanh đ&atilde; bị kh&ocirc;ng &iacute;t doanh nghiệp c&ugrave;ng ng&agrave;nh &quot;nh&ograve;m ng&oacute;&quot;, thậm ch&iacute; c&ograve;n l&ecirc;n tiếng tố c&aacute;o &quot;b&aacute;n ph&aacute; gi&aacute;&quot;. Mức gi&aacute; kh&aacute; &quot;mềm&quot; ph&ugrave; hợp với đại bộ phận người mua như một cơn &quot;mưa r&agrave;o giải tỏa cơn kh&aacute;t&quot; nh&agrave; gi&aacute; rẻ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong khi đ&oacute;, căn hộ cao cấp cũng đua giảm gi&aacute; kh&ocirc;ng k&eacute;m. Tại TP Hồ Ch&iacute; Minh, ph&acirc;n kh&uacute;c căn hộ cũng mấy lần đi&ecirc;u đứng bởi tuy&ecirc;n bố g&acirc;y sốc của Bầu Đức khi mạnh tay giảm gi&aacute;. C&ocirc;ng ty Ho&agrave;ng Anh Gia Lai tung ra thị trường căn hộ gi&aacute; gần 20 triệu đồng mỗi m2, thấp hơn 35-50% so với một số dự &aacute;n c&ugrave;ng vị tr&iacute; quận 7, TP HCM. Khách hàng mua căn h&ocirc;̣ sẽ đóng trước 30% giá trị căn h&ocirc;̣, 40% ti&ecirc;́p theo sẽ đóng trong thời gian 30 tháng, 25% còn lại sẽ đóng khi giao nhà và 5% sẽ n&ocirc;̣p khi hoàn t&acirc;́t gi&acirc;́y chủ quy&ecirc;̀n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trước đ&oacute;, Ho&agrave;ng Anh Gia Lai đi ti&ecirc;n phong cho tr&agrave;o lưu giảm gi&aacute; căn hộ tại Tp.HCM bằng việc giảm gi&aacute; căn hộ Ho&agrave;ng Anh River View khoảng 30% so với gi&aacute; khởi điểm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Thị trường căn hộ tại H&agrave; Nội cũng kh&ocirc;ng mấy s&aacute;ng sủa khi c&aacute;c chủ đầu tư sau thời gian d&agrave;i &quot;nh&igrave;n nhau giảm gi&aacute;&quot; đ&atilde; phải buộc l&ograve;ng xuống nước. Để tho&aacute;t được h&agrave;ng l&uacute;c n&agrave;y, việc giảm gi&aacute; l&agrave; chuyện tất yếu.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="338" src="http://saigonnhadat.vn/upload/UserFiles/image/02_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%201.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="451" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Chủ đầu tư căn hộ cao cấp VP3 Linh Đ&agrave;m tiếp tục g&acirc;y x&ocirc;n xao thị trường khi giảm gi&aacute; căn hộ th&ecirc;m 3 triệu đồng/m2. Theo đ&oacute;, gi&aacute; b&aacute;n giảm mức 25 -26 triệu đồng/m2 xuống c&ograve;n 22-23 triệu đồng/m2. Trước đ&oacute;, cuối năm 2011, chủ đầu tư dự &aacute;n n&agrave;y đ&atilde; c&ocirc;ng bố mức giảm gi&aacute; sốc tr&ecirc;n thị trường 7 triệu đồng/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Như vậy, t&iacute;nh cả hai lần giảm gi&aacute;, chung cư VP3 Linh Đ&agrave;m giảm 10 triệu đồng/m2. &quot;Ph&aacute;t s&uacute;ng&quot; giảm gi&aacute; mạnh ti&ecirc;n phong của chủ đầu tư n&agrave;y c&oacute; lẽ sẽ l&agrave; sự khởi đầu cho l&agrave;n s&oacute;ng giảm gi&aacute; mới đối với c&aacute;c dự &aacute;n chung cư tr&ecirc;n địa b&agrave;n H&agrave; Nội.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ở một v&iacute; tr&iacute; kh&aacute; đẹp nhưng chủ đầu tư dự &aacute;n Mandarin Garden sau nhiều lần đắn đo cũng ch&iacute;nh thức giảm gi&aacute; khoảng 10 triệu đồng/m2 bằng việc b&agrave;n giao nh&agrave; x&acirc;y th&ocirc; cho kh&aacute;ch h&agrave;ng. Theo đ&oacute;, c&aacute;c kh&aacute;ch h&agrave;ng mua căn hộ Mandarin Garden trong đợt mở b&aacute;n sắp tới sẽ chỉ phải trả 27-29 triệu đồng/m2 (chưa bao gồm VAT) nếu nhận nh&agrave; th&ocirc; v&agrave; khoảng 35-36 triệu đồng/m2 ho&agrave;n thiện nội thất.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Sau đợt giảm gi&aacute; của Mandarin Garden, h&agrave;ng loạt chủ đầu tư kh&aacute;c cũng buộc l&ograve;ng phải &quot;xuống nước&quot;. Điều n&agrave;y cho thấy, kh&ocirc;ng &iacute;t chủ đầu tư bất động sản hiện nay đ&atilde; &quot;hết ki&ecirc;n nhẫn&quot; trong nỗ lực giữ gi&aacute; căn hộ - một cuộc giằng co vốn đ&atilde; k&eacute;o d&agrave;i trong một thời gian kh&aacute; d&agrave;i giữa chủ đầu tư v&agrave; kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Căn hộ cao cấp dự &aacute;n Golden Palace Mễ Tr&igrave; ch&agrave;o b&aacute;n căn hộ với gi&aacute; c&ograve;n 22,6 triệu đồng/m2 (chưa VAT) d&agrave;nh cho căn hộ ho&agrave;n thiện cơ bản. Đ&acirc;y l&agrave; mức gi&aacute; kh&aacute; mềm so với mặt bằng gi&aacute; trước đ&acirc;y của căn hộ dự &aacute;n n&agrave;y. Trước đ&oacute; v&agrave;o khoảng th&aacute;ng 5 năm 2012 chủ đầu tư đ&atilde; mở b&aacute;n căn hộ dự &aacute;n với gi&aacute; từ 27 triệu đồng/m2 với hạng mục nội thất đi k&egrave;m linh hoạt.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Động th&aacute;i đồng loạt giảm gi&aacute; của doanh nghiệp bất động sản từ Bắc ch&iacute; Nam gần đ&acirc;y tiếp tục dấy l&ecirc;n một niềm tin l&agrave; l&agrave;n s&oacute;ng hạ gi&aacute; căn hộ thực sự đang diễn ra v&agrave; c&oacute; thể c&ograve;n k&eacute;o d&agrave;i. Tuy nhi&ecirc;n, s&oacute;ng giảm gi&aacute; chung cư tr&ecirc;n diện rộng trong thời gian vừa qua đ&atilde; g&acirc;y t&acirc;m l&yacute; kh&ocirc;ng tốt, người mua h&agrave;ng th&igrave; lu&ocirc;n chờ đợi tới mức gi&aacute; tốt hơn, chủ đầu tư kh&oacute; khăn lại chồng chất kh&oacute; khăn, thị trường th&ecirc;m phần ảm đạm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Biệt thự, nh&agrave; phố kh&ocirc;ng giữ nổi m&igrave;nh</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Chứng kiến gi&aacute; căn hộ trong qu&yacute; III/2011 tại TP. HCM giảm gi&aacute; mạnh từ 20-35%. Tiếp nối l&agrave;n s&oacute;ng đ&oacute;, ngay từ đầu năm 2012, từ ph&acirc;n kh&uacute;c nh&agrave; mặt phố h&agrave;ng trăm tỷ đồng, những biệt thự hạng sang đến căn hộ b&igrave;nh d&acirc;n, đều giảm gi&aacute; tr&ecirc;n thị trường.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tại H&agrave; Nội, những ng&ocirc;i nh&agrave; mặt phố đắt đỏ ở trung t&acirc;m cũng kh&ocirc;ng nằm ngo&agrave;i xu hướng giảm gi&aacute; của thị trường BĐS. Theo khảo s&aacute;t, hiện nh&agrave; mặt phố gi&aacute; đ&atilde; giảm xuống 30%, thậm ch&iacute; c&oacute; khu vực c&ograve;n giảm đến hơn 40%. Như vậy, so với thời điểm thị trường BĐS s&ocirc;i động th&igrave; hiện mỗi căn nh&agrave; mặt phố tại thủ đ&ocirc; đ&atilde; giảm từ v&agrave;i tỷ đến v&agrave;i chục tỷ đồng so với gi&aacute; rao b&aacute;n ban đầu.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Kh&ocirc;ng chỉ nh&agrave; mặt phố, ph&acirc;n kh&uacute;c biệt thự cũng giảm gi&aacute; theo xu hướng chung của thị trường. Những căn biệt thự hạng sang tại c&aacute;c dự &aacute;n VIP như Ciputra, Bắc An Kh&aacute;nh (H&agrave; Nội) hay Ph&uacute; Mỹ Hưng (TP HCM)... đều được c&aacute;c nh&agrave; đầu tư rao b&aacute;n với gi&aacute; giảm 10-20% so với đầu năm 2012.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trong khi đ&oacute;, năm 2012, thị trường văn ph&ograve;ng cho thu&ecirc; cũng như mặt bằng kinh doanh thương mại trong c&aacute;c TTTM v&agrave; mặt bằng lẻ tr&ecirc;n c&aacute;c trục đường lớn c&oacute; tỉ lệ bỏ trống cao chưa từng thấy, gi&aacute; thu&ecirc; giảm nhanh.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">So với thời điểm cực thịnh của thị trường BĐS, gi&aacute; cho thu&ecirc; văn ph&ograve;ng hạng A hiện đ&atilde; giảm c&ograve;n khoảng 35%. Tương tư, văn ph&ograve;ng hạng B, hạng C đều c&oacute; mức giảm tr&ecirc;n 50 %. Gi&aacute; giảm nhưng tỉ lệ lấp k&iacute;n ng&agrave;y c&agrave;ng hạ thấp. Nhiều cao ốc văn ph&ograve;ng đưa v&agrave;o sử dụng từ 12 đến 24 th&aacute;ng vẫn chưa lấp đầy 50% diện t&iacute;ch...</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nguồn cung của thị trường văn ph&ograve;ng cho thu&ecirc; ng&agrave;y một tăng mạnh, trong khi nền kinh tế vẫn rất kh&oacute; khăn, nhiều doanh nghiệp ph&aacute; sản, thu hẹp sản xuất... đ&atilde; khiến cho thị trường văn ph&ograve;ng cho thu&ecirc; đứng trước &aacute;p lực giảm gi&aacute; l&agrave; điều tất yếu. Thị trường văn ph&ograve;ng cho thu&ecirc; rơi v&agrave;o cảnh ế ẩm chưa từng thấy.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Để thu h&uacute;t được kh&aacute;ch thu&ecirc;, nhiều chủ t&ograve;a tự điều chỉnh mức gi&aacute; thu&ecirc; cạnh tranh, cộng với nhiều ưu đ&atilde;i v&agrave; khuyến m&atilde;i, điều khoản thu&ecirc; linh hoạt v&agrave; mức ph&iacute; hợp l&iacute; hơn cho chuyển đổi địa điểm... Thậm ch&iacute;, một số chủ đầu tư đ&atilde; chuyển đổi c&ocirc;ng năng từ cho thu&ecirc; văn ph&ograve;ng sang cho thu&ecirc; căn hộ dịch vụ hoặc căn hộ để b&aacute;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo VEF</strong></em></p>\r\n</div>\r\n', 'Những cú dội giá nhà đất khủng khiếp hiếm có', 0),
(21, 1, '2013-01-11 17:47:52', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Trong bối cảnh thị trường bất động sản (BĐS) đang đ&oacute;ng băng, Ch&iacute;nh phủ cũng chủ trương chỉ k&iacute;ch cầu mảng nh&agrave; ở x&atilde; hội, nh&agrave; ở d&agrave;nh cho người thu nhập thấp, vậy mảng bất động sản cao cấp ở khu vực H&agrave; Nội v&agrave; TP Hồ Ch&iacute; Minh c&oacute; c&ograve;n &ldquo;đất để sống&rdquo;?</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nhằm giải đ&aacute;p một phần c&acirc;u hỏi tr&ecirc;n, PV đ&atilde; c&oacute; cuộc trao đổi với &ocirc;ng Trần Như Trung - Ph&oacute; Gi&aacute;m đốc Savills H&agrave; Nội.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* Hiện nay, một số doanh nghiệp vẫn tiếp tục r&oacute;t vốn v&agrave;o ph&acirc;n kh&uacute;c cao cấp, cho d&ugrave; nh&igrave;n thấy triển vọng chưa mấy lạc quan của thị trường BĐS. Savills c&oacute; thể l&yacute; giải g&igrave; về điều n&agrave;y?</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">* Trong điều kiện kinh tế vĩ m&ocirc; c&ograve;n nhiều kh&oacute; khăn khiến triển vọng thị trường chưa mấy lạc quan nhưng một số doanh nghiệp BĐS vẫn tiếp tục r&oacute;t vốn v&agrave;o ph&acirc;n kh&uacute;c cao cấp, đ&oacute; l&agrave; thực tế! Tuy nhi&ecirc;n, những doanh nghiệp đ&atilde; nhắm tới ph&acirc;n kh&uacute;c n&agrave;y l&agrave; họ đang tận dụng vị tr&iacute; c&oacute; một kh&ocirc;ng hai của BĐS. Với những vị tr&iacute; c&oacute; thể coi l&agrave; &ldquo;v&agrave;ng&rdquo; của BĐS, nếu nhắm tới ph&acirc;n kh&uacute;c nh&agrave; ở trung b&igrave;nh, kh&ocirc;ng mang lại hiệu quả kinh tế bởi bản th&acirc;n chi ph&iacute; để c&oacute; được vị tr&iacute; đất đ&atilde; qu&aacute; cao. Ch&iacute;nh bởi vậy m&agrave; một số doanh nghiệp đ&atilde; tận dụng ưu thế về vị tr&iacute; đất v&agrave; vốn để tiếp tục nhắm tới đối tượng kh&aacute;ch h&agrave;ng cao cấp.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Hơn nữa, cũng phải khẳng định rằng, mặc d&ugrave; thị trường kh&oacute; khăn nhưng nh&oacute;m kh&aacute;ch h&agrave;ng c&oacute; những y&ecirc;u cầu đặc biệt về chất lượng sản phẩm vẫn lu&ocirc;n tồn tại. C&oacute; thể nh&igrave;n thấy điều n&agrave;y qua xu hướng sử dụng xe hơi hạng sang hay đi nghỉ tại những khu nghỉ dưỡng 5 sao của người d&acirc;n. Những nh&oacute;m người n&agrave;y vẫn lu&ocirc;n c&oacute; nhu cầu đối với điều kiện ăn ở hiện đại, tiện nghi v&agrave; thu&acirc;n lợi.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">C&oacute; thể trong ngắn hạn, thị trường đang kh&oacute; khăn với ph&acirc;n kh&uacute;c cao cấp, nhưng nếu c&aacute;c doanh nghiệp c&oacute; vốn v&agrave; tầm nh&igrave;n d&agrave;i hạn từ 3-5 năm, theo t&ocirc;i đ&acirc;y vẫn l&agrave; một hướng đi ph&ugrave; hợp khi đ&atilde; x&aacute;c định r&otilde; được nhu cầu kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* Tại H&agrave; Nội, &ocirc;ng c&oacute; thể b&igrave;nh luận g&igrave; về tiến độ triển khai c&aacute;c dự &aacute;n BĐS cao cấp hiện nay?</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">* Thực tế tại H&agrave; Nội, trong thời gian gần đ&acirc;y c&oacute; kh&ocirc;ng &iacute;t dự &aacute;n phải đ&igrave;nh, gi&atilde;n, ho&atilde;n tiến độ triển khai v&igrave; thị trường suy tho&aacute;i, kh&ocirc;ng b&aacute;n được sản phẩm. Tuy nhi&ecirc;n, như t&ocirc;i đ&atilde; n&oacute;i, với những chủ đầu tư trường vốn v&agrave; c&oacute; chiến lược d&agrave;i hạn, họ vẫn tiếp tục bơm vốn v&agrave;o để triển khai dự &aacute;n, cung cấp sản phẩm cho kh&aacute;ch h&agrave;ng theo đ&uacute;ng cam kết.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">V&iacute; như, C&ocirc;ng ty Cổ phần Đầu tư Thương mại Dịch vụ Cầu Giấy - chủ đầu tư của dự &aacute;n Discovery Complex tại 302 Cầu Giấy vẫn r&oacute;t ra khoảng 5.000 tỷ đồng để triển khai dự &aacute;n. Hiện Discovery Complex l&agrave; t&ograve;a nh&agrave; cao nhất khu vực quận Cầu Giấy, bao gồm một t&ograve;a th&aacute;p văn ph&ograve;ng 38 tầng v&agrave; một t&ograve;a th&aacute;p căn hộ cao 50 tầng. Tổ hợp chung cư cao cấp Royal City của tập đo&agrave;n Vingroup hay Mandarin Garden của tập đo&agrave;n Ho&agrave; Ph&aacute;t cũng đang được nhiều nh&agrave; thầu triển khai c&ugrave;ng l&uacute;c, đảm bảo b&agrave;n giao cho kh&aacute;ch h&agrave;ng đ&uacute;ng tiến độ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="400" src="http://saigonnhadat.vn/upload/UserFiles/image/30_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2011.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="400" /><br />\r\n<em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">H&igrave;nh phối cảnh Discovery Complex.</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* Savills c&oacute; thể nhận định về xu hướng đầu tư căn hộ cao cấp trong tương lai?</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">* X&eacute;t về xu hướng đầu tư căn hộ cao cấp trong tương lai, c&aacute;c chủ đầu tư sẽ ch&uacute; trọng nhiều hơn tới c&ocirc;ng t&aacute;c nghi&ecirc;n cứu nhu cầu của nh&oacute;m kh&aacute;ch h&agrave;ng mục ti&ecirc;u. C&aacute;c sản phẩm sẽ được thiết kế s&aacute;t với nhu cầu hơn chứ kh&ocirc;ng c&ograve;n đại tr&agrave; như trước. C&aacute;c chi tiết, c&ocirc;ng năng, tiện &iacute;ch sẽ được c&acirc;n nhắc kỹ lưỡng hơn nhằm mang lại sự thuận tiện cao nhất cho người sử dụng.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tuy nhi&ecirc;n xu hướng gi&aacute; của ph&acirc;n kh&uacute;c cao cấp trong tương lai sẽ s&aacute;t với khả năng chi trả của người mua hơn nhưng vẫn phải đảm bảo c&aacute;c chi ph&iacute; cấu th&agrave;nh n&ecirc;n sản phẩm v&agrave; hợp l&yacute; hơn so với gi&aacute; trị sản phẩm. Nếu gi&aacute; sản phẩm dưới mức chi ph&iacute;, chất lượng của sản phẩm sẽ kh&ocirc;ng được đảm bảo.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* Trong một v&agrave;i năm tới khi hạ tầng giao th&ocirc;ng tại thủ đ&ocirc; H&agrave; Nội được n&acirc;ng cấp (v&iacute; như tuyến đường sắt tr&ecirc;n cao từ Nhổn &ndash; ga H&agrave; Nội được ho&agrave;n thiện), Savills đ&aacute;nh gi&aacute; thị trường BĐS c&oacute; &ldquo;khởi sắc&rdquo; kh&ocirc;ng, gi&aacute; tăng ra sao?</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">* Tr&ecirc;n thế giới, c&aacute;ch đ&acirc;y hơn 100 năm, khi chưa c&oacute; những phương tiện giao th&ocirc;ng hiện đại, khoảng c&aacute;ch từ một địa điểm tới những địa điểm kh&aacute;c sẽ được t&iacute;nh bằng chiều d&agrave;i địa l&yacute;. Tuy nhi&ecirc;n, khi c&aacute;c phương tiện giao th&ocirc;ng hiện đại ph&aacute;t triển đem lại những tiện &iacute;ch vượt trội cho người sử dụng khoảng c&aacute;ch giữa c&aacute;c vị tr&iacute; sẽ chỉ c&ograve;n được t&iacute;nh bằng thời gian di chuyển.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tại Việt Nam, trong một v&agrave;i năm tới khi c&aacute;c yếu tố vĩ m&ocirc; dần dần ổn định, sự ho&agrave;n thiện về hạ tầng giao th&ocirc;ng với sự xuất hiện của tuyến đường sắt đ&ocirc; thị tr&ecirc;n cao đi từ Nhổn tới ga H&agrave; Nội sẽ l&agrave; một điểm cộng cho thị trường BĐS. Đ&acirc;y sẽ trở th&agrave;nh yếu tố quan trọng để người d&acirc;n lựa chọn BĐS v&igrave; gi&uacute;p gia tăng t&iacute;nh thuận tiện v&agrave; r&uacute;t ngắn thời gian di chuyển.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Quy m&ocirc; ph&acirc;n bổ BĐS sẽ kh&ocirc;ng c&ograve;n theo vị tr&iacute; địa l&yacute; như hiện nay, kh&aacute;i niệm c&aacute;c quận trung t&acirc;m cũng kh&ocirc;ng c&ograve;n quan trọng m&agrave; BĐS sẽ c&oacute; xu hướng ph&acirc;n bổ theo sự ph&aacute;t triển của hạ tầng giao th&ocirc;ng. Theo kinh nghiệm từ nhiều quốc gia tr&ecirc;n thế giới, c&aacute;c tuyến đường sắt đ&ocirc; thị, t&agrave;u điện ngầm, t&agrave;u điện tr&ecirc;n cao, c&aacute;c tuyến xe bu&yacute;t nhanh&hellip; chắc chắn sẽ c&oacute; những t&aacute;c động r&otilde; n&eacute;t đến thị trường BĐS n&oacute;i chung v&agrave; gi&aacute; trị BĐS n&oacute;i ri&ecirc;ng. Đặc biệt, với dự &aacute;n Discovery Complex c&oacute; trung t&acirc;m thương mại được kết nối trực tiếp với tuyến đường sắt tr&ecirc;n cao tại ga số 7, đ&acirc;y c&oacute; lẽ l&agrave; trường hợp đầu ti&ecirc;n để ch&uacute;ng ta chi&ecirc;m nghiệm về điều n&agrave;y tại Việt Nam.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* Xin cảm ơn &ocirc;ng!</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo H&agrave; Nội Mới</strong></em></p>\r\n</div>\r\n', 'Thị trường bất động sản cao cấp còn “đất để sống”?', 0);
INSERT INTO `saigonlandhouse_news_general` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(22, 1, '2013-01-11 17:48:47', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">C&ocirc;ng ty Cổ phần Địa ốc Novaland hiện đang ch&agrave;o b&aacute;n c&aacute;c căn hộ tại ph&acirc;n khu Central Tower thuộc dự &aacute;n Sunrise City với gi&aacute; từ 2,7 tỷ đồng/căn cho căn hộ 76m2.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="194" src="http://saigonnhadat.vn/upload/UserFiles/image/28_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2013.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="259" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Căn hộ dự &aacute;n Sunrise City gồm ba loại: loại căn hộ 2 ph&ograve;ng ngủ với diện t&iacute;ch từ 76m2 - 108m2; loại 3 ph&ograve;ng ngủ c&oacute; diện t&iacute;ch từ 120m2 - 147m2; với loại căn hộ 4 ph&ograve;ng ngủ c&oacute; diện t&iacute;ch 167m2. Gi&aacute; b&aacute;n căn hộ của dự &aacute;n Sunrise City dao động từ 32 - 38 triệu đồng/m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tọa lạc tại quận 7, TP.HCM, Sunrise City l&agrave; khu phức hợp căn hộ cao cấp v&agrave; thương mại c&oacute; vốn đầu tư gần 500 triệu USD được x&acirc;y tr&ecirc;n diện t&iacute;ch đất hơn 5ha.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Dự &aacute;n được chia l&agrave;m 3 khu: Khu South Towers; Khu Central Tower v&agrave; Khu North Towers với tổng cộng 12 th&aacute;p căn hộ cao từ 31 đến 35 tầng, cung cấp khoảng 2.200 căn hộ cao cấp v&agrave; trung t&acirc;m thương mại, t&agrave;i ch&iacute;nh với tổng diện t&iacute;ch s&agrave;n l&ecirc;n đến 50.000m2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo vnmedia</strong></em></p>\r\n</div>\r\n', 'Cận Tết, thị trường TP.HCM chào bán căn hộ tiền tỷ', 0),
(23, 1, '2013-01-11 17:49:23', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Người d&acirc;n c&oacute; mạnh tay mua nh&agrave; sau những giải ph&aacute;p giảm gi&aacute;, ưu đ&atilde;i thuế v&agrave; hỗ trợ l&atilde;i suất?</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="252" src="http://saigonnhadat.vn/upload/UserFiles/image/27_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%209.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Doanh nghiệp k&ecirc;u l&atilde;i suất qu&aacute; cao khiến người d&acirc;n kh&ocirc;ng d&aacute;m mua nh&agrave; l&agrave;m chủ đầu tư bị kẹt vốn&nbsp; - Ảnh: HO&Agrave;NG THẠCH V&Acirc;N</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tuổi Trẻ xin ghi lại &yacute; kiến của c&aacute;c doanh nghiệp kinh doanh bất động sản (BĐS), chủ đầu tư, đặc biệt người mua nh&agrave; họ mong muốn kỳ vọng những g&igrave;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* &Ocirc;ng Đo&agrave;n Nguy&ecirc;n Đức (chủ tịch Tập đo&agrave;n Ho&agrave;ng Anh Gia Lai):<br />\r\nL&atilde;i suất ng&acirc;n h&agrave;ng vẫn cao</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Điều mong muốn nhất của doanh nghiệp BĐS hiện nay l&agrave; thị trường c&oacute; thanh khoản. Nếu b&aacute;n được h&agrave;ng, doanh nghiệp sẽ thu hồi được vốn, trả nợ ng&acirc;n h&agrave;ng cũng như t&aacute;i đầu tư. Giải ph&aacute;p hỗ trợ l&atilde;i vay trực tiếp cho người mua nh&agrave; như Ch&iacute;nh phủ dự kiến &aacute;p dụng l&agrave; một giải ph&aacute;p g&oacute;p phần cải thiện thanh khoản cho thị trường.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Tuy nhi&ecirc;n, muốn &ldquo;ph&aacute; băng&rdquo; BĐS n&oacute;i ri&ecirc;ng, giải tỏa h&agrave;ng h&oacute;a của tất cả c&aacute;c ng&agrave;nh kh&aacute;c trong nền kinh tế n&oacute;i chung, mặt bằng l&atilde;i suất phải hạ xuống nữa. Ng&acirc;n h&agrave;ng Nh&agrave; nước nhiều lần hạ l&atilde;i suất đầu v&agrave;o, nhưng l&atilde;i suất cho vay của c&aacute;c ng&acirc;n h&agrave;ng đ&acirc;u c&oacute; giảm. Ch&uacute;ng ta đ&atilde; c&oacute; trần l&atilde;i suất đầu v&agrave;o rồi, cần thiết cũng n&ecirc;n c&oacute; trần l&atilde;i suất đầu ra. Phải buộc c&aacute;c ng&acirc;n h&agrave;ng giảm l&atilde;i suất cho vay chứ, sao chỉ c&oacute; người gửi tiền mới phải chịu mức l&atilde;i suất tiền gửi thấp, c&ograve;n nền kinh tế g&aacute;nh l&atilde;i suất cao.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* &Ocirc;ng Trần Quang Mỹ (chủ tịch HĐQT C&ocirc;ng ty CP X&acirc;y dựng số 5):<br />\r\nBốc đ&uacute;ng thuốc</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nếu bảo rằng giải ph&aacute;p hỗ trợ l&atilde;i suất trực tiếp cho người c&oacute; thu nhập thấp mua nh&agrave; sẽ g&oacute;p phần &ldquo;ph&aacute; băng&rdquo; BĐS th&igrave; rất kh&oacute; bởi c&aacute;c dự &aacute;n căn hộ gi&aacute; thấp hoặc trung b&igrave;nh đến nay hầu hết đ&atilde; ti&ecirc;u thụ, c&oacute; chăng chỉ c&ograve;n ở ngoại th&agrave;nh. M&agrave; chắc g&igrave; những người thu nhập thấp lại chấp nhận ra ngoại th&agrave;nh mua nh&agrave;. Trong khi đ&oacute;, h&agrave;ng tồn kho BĐS chủ yếu nằm trong khu vực nội th&agrave;nh với gi&aacute; xoay quanh mức 20 triệu đồng/m2, kh&ocirc;ng thuộc diện người mua được hỗ trợ l&atilde;i suất.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Giải ph&aacute;p giảm tiền sử dụng đất hay thuế ph&iacute; cũng kh&ocirc;ng t&aacute;c động nhiều đến c&aacute;c dự &aacute;n thuộc nh&oacute;m n&agrave;y, do hầu hết c&aacute;c chủ đầu tư đ&atilde; ho&agrave;n th&agrave;nh nghĩa vụ về tiền sử dụng đất. Do đ&oacute;, theo t&ocirc;i, để tạo thanh khoản cho thị trường BĐS, &ldquo;ph&aacute; băng&rdquo; thị trường n&agrave;y th&igrave; phải bốc đ&uacute;ng thuốc, đ&oacute; l&agrave; mặt bằng l&atilde;i suất cho người mua nh&agrave; n&oacute;i chung phải giảm nữa v&agrave; ổn định.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* &Ocirc;ng Nguyễn Xu&acirc;n Quang (chủ tịch HĐQT C&ocirc;ng ty CP đầu tư Nam Long):<br />\r\nNhiều ng&agrave;nh cũng hưởng lợi</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Trước mắt, nếu &aacute;p dụng l&atilde;i suất ưu đ&atilde;i cho người mua nh&agrave; để ở thật sự, kh&ocirc;ng những thanh khoản của BĐS được cải thiện m&agrave; thị trường n&agrave;y cũng c&oacute; sự chuyển hướng đầu tư. Doanh nghiệp sẽ tập trung đầu tư v&agrave;o những dự &aacute;n đ&aacute;p ứng nhu cầu thật của thị trường, thay v&igrave; chạy theo c&aacute;c dự &aacute;n &ldquo;hạng sang&rdquo;. Nếu thị trường c&oacute; cầu, doanh nghiệp đầu tư, người lao động sẽ được giải quyết việc l&agrave;m, đầu ra cho c&aacute;c sản phẩm vật liệu x&acirc;y dựng, trang tr&iacute; nội thất... cũng tốt l&ecirc;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ngo&agrave;i l&atilde;i suất ưu đ&atilde;i cho người mua, miễn giảm tiền sử dụng đất, giảm thuế ph&iacute;..., c&aacute;c thủ tục đầu tư như c&ocirc;ng t&aacute;c giải ph&oacute;ng mặt bằng, duyệt dự &aacute;n đầu tư, cấp ph&eacute;p x&acirc;y dựng... cũng cần phải được r&uacute;t ngắn, c&oacute; như vậy chi ph&iacute; vốn của dự &aacute;n mới giảm ở mức thấp nhất, k&eacute;o gi&aacute; nh&agrave; xuống.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* &Ocirc;ng Nguyễn Hồng Th&aacute;i (tổng gi&aacute;m đốc C&ocirc;ng ty CP đầu tư Hải Ph&aacute;t):<br />\r\nN&ecirc;n c&oacute; ch&iacute;nh s&aacute;ch khuyến kh&iacute;ch đầu tư nh&agrave; ở x&atilde; hội</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">T&ocirc;i cho rằng việc chuyển một số dự &aacute;n nh&agrave; ở thương mại sang nh&agrave; ở x&atilde; hội l&agrave; rất cần thiết, nhưng Nh&agrave; nước cần c&oacute; ti&ecirc;u ch&iacute; cụ thể về nh&agrave; ở x&atilde; hội v&agrave; nh&agrave; ở cho người thu nhập thấp. Ngo&agrave;i ra, cần c&oacute; ch&iacute;nh s&aacute;ch khuyến kh&iacute;ch nh&agrave; đầu tư tham gia đầu tư v&agrave;o ph&acirc;n kh&uacute;c x&acirc;y nh&agrave; ở cho đối tượng n&agrave;y. Chẳng hạn, Nh&agrave; nước sẽ l&agrave; chủ đầu tư, đặt h&agrave;ng cho doanh nghiệp thực hiện. Với những dự &aacute;n n&agrave;y, Nh&agrave; nước sẽ phải lo mặt bằng, c&ugrave;ng với một loạt ti&ecirc;u ch&iacute; về diện t&iacute;ch, về cảnh quan, thiết kế... cần phải được x&acirc;y dựng cụ thể. Chẳng hạn với đơn gi&aacute; 400-500 triệu đồng/căn hộ th&igrave; doanh nghiệp chắc chắn sẽ tham gia.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* &Ocirc;ng Phạm Thanh Hưng (ph&oacute; tổng gi&aacute;m đốc Tập đo&agrave;n bất động sản Thế Kỷ)<br />\r\nGiảm, miễn tiền sử dụng đất gi&aacute; nh&agrave; sẽ hạ ngay</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nếu giảm hoặc kh&ocirc;ng thu tiền sử dụng đất th&igrave; chắc chắn gi&aacute; nh&agrave; sẽ giảm đ&aacute;ng kể. V&igrave; th&ocirc;ng thường tiền sử dụng đất chiếm khoảng 10-30% gi&aacute; th&agrave;nh sản phẩm. Đấy l&agrave; đối với c&aacute;c nh&agrave; cao tầng, hiệu quả sử dụng đất cao, c&ograve;n với những nh&agrave; thấp tầng th&igrave; tiền sử dụng đất cao hơn nhiều, chiếm 70% gi&aacute; th&agrave;nh sản phẩm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Về mặt nguy&ecirc;n tắc, c&aacute;c ch&iacute;nh s&aacute;ch giảm thuế, giảm l&atilde;i suất cho vay phần n&agrave;o sẽ gi&uacute;p doanh nghiệp giảm gi&aacute; th&agrave;nh sản phẩm, mở rộng thị trường. Nhưng để thị trường bất động sản hồi phục v&agrave; ph&aacute;t triển th&igrave; c&aacute;c nh&agrave; l&agrave;m ch&iacute;nh s&aacute;ch đưa ra ch&iacute;nh s&aacute;ch vĩ m&ocirc; chứ kh&ocirc;ng n&ecirc;n d&ugrave;ng mệnh lệnh h&agrave;nh ch&iacute;nh. V&iacute; dụ khuyến kh&iacute;ch thị trường c&oacute; tập trung cung cấp nh&agrave; ở cho người thu nhập trung b&igrave;nh, nh&agrave; ở x&atilde; hội th&igrave; n&ecirc;n giảm thuế, thậm ch&iacute; miễn thuế, miễn tiền sử dụng đất, cho vay vốn ưu đ&atilde;i... Nếu can thiệp bằng c&aacute;c mệnh lệnh h&agrave;nh ch&iacute;nh th&igrave; sẽ l&agrave;m hỏng thị trường.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* Anh Đỗ Quang Ki&ecirc;n (C&ocirc;ng ty CP cơ kh&iacute; x&acirc;y dựng Thăng Long, H&agrave; Nội)<br />\r\nNh&agrave; chỉ 500 triệu đồng th&igrave; sẽ mua</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Nếu gi&aacute; nh&agrave; vẫn 700 triệu đến 1 tỉ đồng/căn hộ th&igrave; chắc chắn c&oacute; l&atilde;i suất 7-8%/ năm ch&uacute;ng t&ocirc;i cũng chả d&aacute;m mơ l&agrave; mua được. Với thu nhập của hai vợ chồng l&agrave; 15 triệu đồng/th&aacute;ng, ngo&agrave;i chi ti&ecirc;u nhu cầu thiết yếu kh&aacute;c, ch&uacute;ng t&ocirc;i d&agrave;nh dụm cả năm năm trời mới được 300 triệu đồng. Nay để mua được nh&agrave; m&agrave; ch&uacute;ng t&ocirc;i phải vay 500-700 triệu đồng d&ugrave; l&atilde;i suất c&oacute; ưu đ&atilde;i nữa th&igrave; cũng thấy qu&aacute; sức của m&igrave;nh.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Gi&aacute; như gi&aacute; chỉ dưới 500 triệu đồng/căn hộ th&igrave; chắc chắn t&ocirc;i sẽ mua. V&igrave; với hai vợ chồng v&agrave; một đứa con th&igrave; căn hộ chỉ cần rộng 40m2 l&agrave; ở được.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">* Chị L&ecirc; Thị Thanh (giảng vi&ecirc;n một trường đại học tại Q.1, TP.HCM):<br />\r\nSợ nhất l&atilde;i suất chỉ rẻ l&uacute;c đầu</em></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Với thu nhập b&igrave;nh qu&acirc;n 6-8 triệu đồng/th&aacute;ng cộng với tiền đi l&agrave;m th&ecirc;m ở ngo&agrave;i, t&ocirc;i vẫn kh&ocirc;ng t&agrave;i n&agrave;o đủ tiền để mua một căn hộ c&oacute; gi&aacute; tầm 800 triệu đến 1 tỉ đồng. T&ocirc;i đ&atilde; t&igrave;m c&aacute;c dự &aacute;n ở v&ugrave;ng ven như Q.8, T&acirc;n B&igrave;nh, G&ograve; Vấp... nhưng gi&aacute; nh&agrave; vẫn cao ngất ngưởng so với t&uacute;i tiền. Trước đ&acirc;y t&ocirc;i cũng định vay khoảng 500-600 triệu để mua nh&agrave;, nhưng sau đ&oacute; dừng lại v&igrave; l&atilde;i suất qu&aacute; cao. Nếu l&atilde;i suất ưu đ&atilde;i c&ograve;n 5-7%/năm như dự kiến, c&oacute; lẽ t&ocirc;i cũng sẽ t&igrave;m mua một căn hộ, tiền thu&ecirc; nh&agrave; sẽ được d&ugrave;ng cho việc trả l&atilde;i. Tuy nhi&ecirc;n, cũng phải c&acirc;n nhắc liệu khi ch&iacute;nh s&aacute;ch đưa ra gi&aacute; nh&agrave; c&oacute; bị đẩy l&ecirc;n? L&atilde;i suất c&oacute; ổn định hay chỉ &ldquo;mồi ch&agrave;i&rdquo; ban đầu? </p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;Theo Địa Ốc TTO</strong></em></p>\r\n</div>\r\n', '“Phá băng” bất động sản: Giảm nhanh lãi suất cho vay', 0),
(24, 1, '2013-01-15 23:45:14', '<p>dự &aacute;n mới n&egrave;</p>\r\n', 'dự án mới', 0);
INSERT INTO `saigonlandhouse_news_knowledge` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(1, 1, '2013-01-13 17:00:00', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Những ng&agrave;y cuối tuần qua, hiện tượng băng tuyết đ&atilde; xuất hiện tại Sa Pa. C&oacute; l&uacute;c, nhiệt độ xuống tới dưới 10C, sương m&ugrave; d&agrave;y đặc. Điều n&agrave;y đ&atilde; khiến nhiều du kh&aacute;ch c&oacute; mặt ở v&ugrave;ng cao n&agrave;y th&iacute;ch th&uacute;, bởi đ&acirc;y l&agrave; cơ hội hiếm c&oacute; để ngắm băng, chờ tuyết rơi.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%201.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">L&aacute;c đ&aacute;c hoa đ&agrave;o, hoa mận đ&atilde; bắt đầu nở.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%202.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%204(1).jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">N&uacute;i H&agrave;m Rồng trong nắng. Chỉ sau đ&oacute; 1 ng&agrave;y, Sa Pa bắt đầu ch&igrave;m v&agrave;o đợt lạnh v&agrave; sương.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">&nbsp;<img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%205.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" />&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">Sương phủ khắp lối, khiến tầm nh&igrave;n rất hạn chế.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">&nbsp;<img alt="" height="600" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%206.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%207.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">Băng đ&oacute;ng tr&ecirc;n c&aacute;c c&agrave;nh v&agrave; l&aacute; c&acirc;y.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">&nbsp;<img alt="" height="600" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%208.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="600" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%209.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">Kh&ocirc;ng phải du kh&aacute;ch n&agrave;o cũng may mắn để được tận mắt chứng kiến những h&igrave;nh ảnh n&agrave;y.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">&nbsp;<img alt="" height="600" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2010.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">Băng gi&aacute; đ&ocirc;ng cứng cả mạng nhện.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">&nbsp;<img alt="" height="600" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2011.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">Tr&ecirc;n những b&ocirc;ng hoa, ngọn cỏ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">&nbsp;<img alt="" height="600" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2012.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">Th&iacute;ch th&uacute; tạo d&aacute;ng với băng tuyết.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">&nbsp;<img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/14_01_2013%20nha%20dat%20dia%20oc%20bat%20dong%20san%2013.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" />&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;">Sau khi trở về từ Trạm T&ocirc;n, điều đầu ti&ecirc;n mọi người l&agrave;m l&agrave;m t&igrave;m bếp than để sưởi ấm.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: right; background-position: initial initial; background-repeat: initial initial;">Theo Infonet</p>\r\n</div>\r\n', 'Ngắm mạng nhện đóng băng ở Sa Pa', 0),
(3, 1, '2012-12-18 17:00:00', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 15px; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;Động Lechuguilla ở New Mexico, vườn Quốc gia Thung lũng c&aacute;c lo&agrave;i hoa của Ấn Độ hay Vũ Lăng Nguy&ecirc;n, chốn đ&agrave;o nguy&ecirc;n của Trung Quốc l&agrave; những thắng cảnh c&oacute; vẻ đẹp ngoạn mục, m&ecirc; hồn nhưng lại chưa được biết đến rộng r&atilde;i tr&ecirc;n thế giới.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">10. Động v&agrave; th&aacute;p băng của n&uacute;i Erebus</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="334" src="http://saigonnhadat.vn/upload/UserFiles/image/19_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2050.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;Erebus l&agrave; n&uacute;i lửa lớn thứ hai của Nam Cực, hoạt động li&ecirc;n tục kể từ năm 1972. Tr&ecirc;n đỉnh n&uacute;i l&agrave; h&agrave;ng trăm th&aacute;p băng được h&igrave;nh th&agrave;nh bởi qu&aacute; tr&igrave;nh giải ph&oacute;ng hơi nước của n&uacute;i lửa đang hoạt động. Nhiều th&aacute;p băng li&ecirc;n tục giải ph&oacute;ng hơi nước, tr&ocirc;ng giống như những ống kh&oacute;i. Ngo&agrave;i th&aacute;p băng, Erebus c&ograve;n nổi tiếng sở hữu c&aacute;c động băng tuyệt đẹp. Những động băng lộng lẫy, c&oacute; m&agrave;u xanh rực rỡ dưới l&ograve;ng đất n&agrave;y được h&igrave;nh th&agrave;nh ho&agrave;n to&agrave;n tự nhi&ecirc;n.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">9. Quần thể đ&aacute; h&igrave;nh lều Kasha-Katuwe của Mexico</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" src="http://saigonnhadat.vn/upload/UserFiles/image/19_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2051.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;Quần thể đ&aacute; h&igrave;nh lều Kasha-Katuwe của Mexico được h&igrave;nh th&agrave;nh c&aacute;ch đ&acirc;y khoảng 7 triệu năm bởi d&ograve;ng chảy của nham tầng n&uacute;i lửa. Sau đ&oacute;, gi&oacute;, mưa v&agrave; c&aacute;c điều kiện thời tiết kh&aacute;c diễn ra li&ecirc;n tục trong h&agrave;ng triệu năm l&agrave; c&aacute;c yếu tố khiến c&aacute;c đ&aacute; nham tầng ở đ&acirc;y c&oacute; h&igrave;nh th&agrave;nh dạng t&uacute;p lều hoặc dương vật độc đ&aacute;o. C&aacute;i t&ecirc;nKasha-Katuwe của khu vực n&agrave;y the tiếng bản địa c&oacute; nghĩa l&agrave; &ldquo;những tảng đ&aacute; trắng&rdquo;. Đ&aacute; ở đ&acirc;y cao từ v&agrave;i m&eacute;t cho tới 27m.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">8. Động Lechuguilla</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="361" src="http://saigonnhadat.vn/upload/UserFiles/image/19_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2052.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Một trong những điểm đến c&oacute; vẻ đẹp kỳ vĩ nhưng lại &iacute;t người biết đến l&agrave; động Lechuguilla ở New Mexico. Đ&acirc;y l&agrave; hang động d&agrave;i thứ 7 thế giới với chiều d&agrave;i 216km. C&oacute; lịch sử h&igrave;nh th&agrave;nh từ h&agrave;ng trăm triệu năm trước nhưng động mới chỉ được ph&aacute;t hiện năm 1986. Do kh&ocirc;ng qu&aacute; nổi tiếng, Lechuguilla được đ&aacute;nh gi&aacute; l&agrave; nơi giữ được hệ sinh th&aacute;i nguy&ecirc;n sơ bậc nhất thế giới.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">7. Động Krubera</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" src="http://saigonnhadat.vn/upload/UserFiles/image/19_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2053.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;Nằm trong v&ugrave;ng đ&aacute; v&ocirc;i Arabika Massif của d&atilde;y Gagra, ở Abkhazia, Gruzia, Krubera l&agrave; hang động s&acirc;u nhất từng được biết đến tr&ecirc;n tr&aacute;i đất. C&ograve;n được biết đến với c&aacute;i t&ecirc;n Voronya, nghĩa l&agrave; động Con quạ, Krubera c&oacute; độ s&acirc;u 2.191m.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">6. Vườn quốc gia Thung lũng c&aacute;c lo&agrave;i hoa</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/19_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2054.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Nằm ở ph&iacute;a T&acirc;y của khu bảo tồn Nanda Devi l&agrave; Vườn quốc gia Thung lũng c&aacute;c lo&agrave;i hoa nổi tiếng thuộc d&atilde;y T&acirc;y Himalaya với những c&aacute;nh đồng cỏ, hoa đặc hữu của v&ugrave;ng n&uacute;i cao. Vườn quốc gia n&agrave;y trải d&agrave;i tr&ecirc;n một diện t&iacute;ch l&agrave; 87,5km2.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Được người d&acirc;n địa phương truyền nhau l&agrave; nơi sinh sống của c&aacute;c n&agrave;ng ti&ecirc;n, Vườn quốc gia Thung lũng c&aacute;c lo&agrave;i hoa l&agrave; nơi quy tụ c&aacute;c lo&agrave;i hoa n&uacute;i cao như c&uacute;c vạn thọ, giai nh&acirc;n xanh Himalaya, Brahma kamal (một lo&agrave;i hoa qu&yacute; hiếm m&agrave;u trắng nở v&agrave;o ban đ&ecirc;m, được mệnh danh l&agrave; hoa của Ch&uacute;a trời), c&acirc;y rắn hổ mang (một loại c&acirc;y bắt mồi)...</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Ngo&agrave;i ra, ở đ&acirc;y c&ograve;n c&oacute; những khu rừng nhỏ, chủ yếu l&agrave; bạch dương v&agrave; đỗ quy&ecirc;n. Động vật trong vườn quốc gia bao gồm rất nhiều những lo&agrave;i động vật qu&yacute; hiếm thuộc lo&agrave;i chim (như trĩ, kền kền, đại b&agrave;ng v&agrave;ng Himalaya..), cừu xanh, sơn dương, hươu xạ, gấu n&acirc;u, gấu đen ch&acirc;u &Aacute;, c&aacute;o đỏ, voọc, b&aacute;o tuyết v&agrave; c&aacute;c lo&agrave;i bướm...</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">5. Thung lũng Glen Coe</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/19_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2055.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;Glen Coe l&agrave; một thung lũng nhỏ nằm ở mũi ph&iacute;a Bắc của Scotland. L&ograve;ng chảo d&agrave;i 11km h&igrave;nh chữ U n&agrave;y được mệnh danh l&agrave; danh lam thắng cảnh quyến rũ bậc nhất của Scotland. Nằm lọt thỏm giữa d&atilde;y n&uacute;i cao ch&oacute;t v&oacute;t, Glen Coe nổi tiếng c&oacute; hệ thực vật rất phong ph&uacute;, đa dạng v&agrave; một d&ograve;ng s&ocirc;ng uốn kh&uacute;c trong xanh tuyệt đẹp.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">4. Quinta da Regaleira, Bồ Đ&agrave;o Nha</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="333" src="http://saigonnhadat.vn/upload/UserFiles/image/19_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2056.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;Tọa lạc trong l&acirc;u đ&agrave;i cổ Quinta da Regaleira tại thị trấn Sintra, Bồ Đ&agrave;o Nha, giếng Khai t&acirc;m (Initiation Well) được mệnh danh l&agrave; nơi diễn ra c&aacute;c nghi lễ sơ khai của Hội Tam điểm. Giếng n&agrave;y c&oacute; một cầu thang xoắn theo h&igrave;nh tr&ocirc;n ốc độc đ&aacute;o. Dưới đ&aacute;y giếng c&oacute; một biểu đồ chỉ phương hướng với biểu tượng h&igrave;nh chữ thập nổi tiếng của Hội Tam điểm ở ch&iacute;nh giữa, c&oacute; &yacute; nghĩa biểu trưng cho sự sống v&agrave; c&aacute;i chết.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">3. Chuỗi hồ Plitvice, Croatia</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/19_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2057.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;Thuộc C&ocirc;ng vi&ecirc;n Quốc gia hồ Plitvice, chuỗi hồ Plitvice nằm tr&ecirc;n d&atilde;y n&uacute;i đ&aacute; v&ocirc;i ở Croatia gồm 16 hồ nước tuyệt đẹp. Được chia th&agrave;nh hai tầng, Plitvice bao gồm c&aacute;c hồ ở ph&iacute;a tr&ecirc;n v&agrave; c&aacute;c hồ ở ph&iacute;a dưới. C&aacute;c hồ ở ph&iacute;a tr&ecirc;n nằm trong thung lũng đ&aacute; trầm t&iacute;ch v&agrave; được bao quanh bởi c&aacute;c khu rừng rậm rạp, tươi tốt. Trong khi đ&oacute;, c&aacute;c hồ ở ph&iacute;a dưới thường nhỏ v&agrave; n&ocirc;ng hơn, nằm tr&ecirc;n lớp đ&aacute; v&ocirc;i v&agrave; được bao quanh bởi c&aacute;c lo&agrave;i c&acirc;y bụi thấp. Mặt c&aacute;c hồ chứa nhiều đ&aacute; v&ocirc;i travectin, t&iacute;ch tụ từ canxi cacbonat, gi&uacute;p c&aacute;c lo&agrave;i rong r&ecirc;u ph&aacute;t triển nhanh tạo ra những h&agrave;ng r&agrave;o ngăn c&aacute;ch, h&igrave;nh th&agrave;nh n&ecirc;n một chuỗi c&aacute;c hồ nước v&agrave; th&aacute;c nước tuyệt đẹp.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">2. Vũ Lăng Nguy&ecirc;n, Trung Quốc</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/19_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2058.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;Được bao bọc trong sương m&ugrave; huyền ảo quanh năm c&ugrave;ng vớii những cột đ&aacute; cao ch&oacute;t v&oacute;t như cột chống trời, Vũ Lăng Nguy&ecirc;n được người Trung Quốc mệnh danh l&agrave; c&otilde;i đ&agrave;o nguy&ecirc;n. Thắng cảnh n&agrave;y được ghi v&agrave;o danh s&aacute;ch di sản thế giới năm 1992 v&igrave; khung cảnh m&ecirc; hoặc do khoảng 3.100 cột đ&aacute; sa thạch, sa thạch hoặc đ&aacute; v&ocirc;i khổng lồ, trong đ&oacute; c&oacute; những ngọn cao tr&ecirc;n 800 m&eacute;t tạo th&agrave;nh. Tuy nhi&ecirc;n, nơi đ&acirc;y vẫn chưa phải l&agrave; thắng cảnh được biết đến rộng r&atilde;i tr&ecirc;n thế giới.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">1. Tu viện Ki</strong></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;">&nbsp;<img alt="" height="375" src="http://saigonnhadat.vn/upload/UserFiles/image/19_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2059.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="500" />&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">C&ograve;n được biết đến với c&aacute;i t&ecirc;n Key Gompa, Ki Monastery l&agrave; một tu viện Phật gi&aacute;o T&acirc;y Tạng. Tu viện n&agrave;y được x&acirc;y dựng tr&ecirc;n một ngọn đồi rộng m&ecirc;nh m&ocirc;ng nh&igrave;n xuống một ng&ocirc;i l&agrave;ng nhỏ t&ecirc;n l&agrave; Kibar của Ấn Độ. Tu viện n&agrave;y cao tr&ecirc;n 4.000m so với mực nước biển. Được x&acirc;y dựng từ thế kỷ 11, tu viện Ki c&oacute; một bề d&agrave;y lịch sử đầy thăng trầm khi kh&ocirc;ng &iacute;t lần bị t&agrave;n ph&aacute; bởi qu&acirc;n đội M&ocirc;ng Cổ, c&aacute;c trận hỏa hoạn v&agrave; động đất.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo Infonet </strong></em></p>\r\n</div>\r\n', '10 điểm đến tuyệt đẹp bị lãng quên', 0);
INSERT INTO `saigonlandhouse_news_knowledge` (`id`, `id_category`, `date`, `content`, `title`, `type`) VALUES
(4, 1, '2012-12-12 17:00:00', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 15px; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">\r\n<h2 style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); font-weight: normal; text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</h2>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">C&ugrave;ng ngắm ngh&iacute;a xem trang tr&iacute; c&acirc;y th&ocirc;ng Noel như thế n&agrave;o th&igrave; đẹp bạn nh&eacute;!</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Đừng lo lắng nếu như trong nh&agrave; bạn kh&ocirc;ng c&oacute; c&acirc;y th&ocirc;ng thật để trang tr&iacute; Gi&aacute;ng sinh, tham khảo những mẫu c&acirc;y th&ocirc;ng dưới đ&acirc;y, nhất định bạn sẽ chọn được cho m&igrave;nh 1 c&acirc;y th&ocirc;ng thật ưng &yacute; để trang ho&agrave;ng m&ugrave;a Gi&aacute;ng sinh n&agrave;y đấy!</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">1. C&acirc;y th&ocirc;ng xếp từ những chiếc vỏ hộp di&ecirc;m được bọc vỏ đỏ, b&ecirc;n trong hộp c&ograve;n c&oacute; thể nh&eacute;t v&agrave;i vi&ecirc;n kẹo ngọt xinh xinh.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="554" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%201.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">2. Những thanh gỗ d&agrave;i được đ&oacute;ng th&agrave;nh dạng c&acirc;y, sau đ&oacute; th&ecirc;m những tấm thiệp, đ&egrave;n m&agrave;u, đồ trang tr&iacute; Noel l&agrave; đ&atilde; c&oacute; c&acirc;y th&ocirc;ng thật to v&agrave; lấp l&aacute;nh trong nh&agrave;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="613" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%202.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">3. C&acirc;y th&ocirc;ng xếp từ những thanh gỗ với độ d&agrave;i kh&aacute;c nhau tr&ecirc;n tường, đừng qu&ecirc;n bọc giấy cho c&aacute;c thanh gỗ th&ecirc;m đẹp v&agrave; gắn ng&ocirc;i sao tr&ecirc;n ch&oacute;p c&acirc;y.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="584" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%203.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">4. Chỉ với 1 chiếc cọc gỗ hoặc sắt, sau đ&oacute; xi&ecirc;n c&aacute;c mảnh giấy với c&aacute;c đường cắt răng cưa, xếp theo thứ tự dưới to tr&ecirc;n nhỏ dần, tr&ocirc;ng cũng kh&aacute; l&agrave; vintage đấy chứ nhỉ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="600" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%204.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">5. Uốn thanh nh&ocirc;m, sắt, th&eacute;p... quanh 1 vật c&oacute; h&igrave;nh ch&oacute;p, đặt th&ecirc;m c&aacute;c quả th&ocirc;ng b&ecirc;n cạnh l&agrave; th&agrave;nh c&acirc;y th&ocirc;ng Noel rồi, qu&aacute; đơn giản v&agrave; dễ l&agrave;m.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="450" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%205.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">6. C&acirc;y th&ocirc;ng tr&ecirc;n cửa k&iacute;nh với c&aacute;c miếng l&oacute;t mặt b&agrave;n, l&oacute;t đĩa, l&oacute;t cốc xếp theo h&igrave;nh c&acirc;y. Một c&aacute;ch kh&aacute; hay để trang tr&iacute; cho cửa h&agrave;ng hay cửa sổ trong c&ocirc;ng ty đấy.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="600" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%206.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">7. Đ&oacute;ng những thanh gỗ ngang c&oacute; độ d&agrave;i kh&aacute;c nhau l&ecirc;n 1 thanh gỗ thẳng như h&igrave;nh c&acirc;y th&ocirc;ng, sau đ&oacute; trang tr&iacute; c&aacute;c h&igrave;nh tim, sao, hoa l&ecirc;n c&aacute;c thanh gỗ. Năm sau lại mang ra đổi th&agrave;nh h&igrave;nh l&aacute;, chim, th&uacute;..., th&uacute; vị lắm đấy bạn ạ.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="674" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%207.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">8. C&acirc;y th&ocirc;ng từ gi&aacute; s&aacute;ch, s&aacute;ng tạo v&agrave; độc đ&aacute;o chẳng nh&agrave; n&agrave;o c&oacute; nh&eacute;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="686" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%208.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">9. Ngay cả chiếc thang h&agrave;ng ng&agrave;y cũng c&oacute; thể biến th&agrave;nh c&acirc;y th&ocirc;ng Noel chỉ với c&aacute;c d&acirc;y đ&egrave;n nh&aacute;y v&agrave; đồ trang tr&iacute;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="681" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%209.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">10. Ấm &aacute;p như c&acirc;y th&ocirc;ng quấn từ len n&agrave;y nh&eacute;, chỉ cần cuộn len quanh một chiếc khu&ocirc;n h&igrave;nh ch&oacute;p l&agrave; được ngay th&ocirc;i m&agrave;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="675" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2010.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">11. Vừa ăn b&aacute;nh vừa ngắm c&acirc;y th&ocirc;ng Noel đang dần dần mất &quot;c&agrave;nh&quot; như thế n&agrave;o nh&eacute;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="798" src="http://saigonnhadat.vn/upload/UserFiles/image/13_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2011.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;" width="450" /></p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: right; background-position: initial initial; background-repeat: initial initial;"><em style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;"><strong style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; background-repeat: initial initial;">Theo Eva</strong></em></p>\r\n</div>\r\n', 'Những cây thông "độc", ngất ngây lòng người', 0),
(5, 1, '2012-12-07 17:00:00', '<div class="fi_intro_text" style="background-color: transparent; border: 0px; font-size: 12px; margin: 0px; outline: 0px; padding: 0px; font-weight: bold; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; text-align: justify; background-position: initial initial; background-repeat: initial initial;">Anh cho biết sẽ mở cổng cho kh&aacute;n giả mến mộ đến để chụp h&igrave;nh lưu niệm với hang đ&aacute; do Team Đ&agrave;m Vĩnh Hưng thực hiện.</p>\r\n</div>\r\n\r\n<div class="fi_details_text" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; color: rgb(0, 0, 0); text-align: justify; line-height: 22px; background-position: initial initial; background-repeat: initial initial;">\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ca sĩ Đ&agrave;m Vĩnh Hưng quyết định khoe h&igrave;nh ảnh b&ecirc;n trong ng&ocirc;i nh&agrave; sang trọng của m&igrave;nh. Anh cũng ch&iacute;nh l&agrave; người tận tay chọn từng m&oacute;n nội thất cho ng&ocirc;i nh&agrave;. Từ chiếc đ&egrave;n ch&ugrave;m h&igrave;nh chữ &quot;Mr.Đ&agrave;m&quot; v&agrave; chữ &quot;H&quot; c&oacute; một kh&ocirc;ng hai do anh đặt h&agrave;ng riệng từ một h&atilde;ng đ&egrave;n trang tr&iacute; nổi tiếng. Đặc biệt những giỏ x&aacute;ch Louis Vuiton đắt tiền cũng được anh d&ugrave;ng để trang tr&iacute;, t&ocirc;n l&ecirc;n vẻ sang trọng cho ng&ocirc;i nh&agrave;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Để ch&agrave;o đ&oacute;n cho m&ugrave;a Noel sắp đến, c&aacute;c th&agrave;nh vi&ecirc;n của đội The Voice Đ&agrave;m Vĩnh Hưng cũng đ&atilde; c&oacute; mặt tại nh&agrave; HLV của m&igrave;nh để c&ugrave;ng nhau trang tr&iacute; c&acirc;y th&ocirc;ng Noel v&agrave; hang đ&aacute;.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">Ca sĩ Đ&agrave;m Vĩnh Hưng cũng chia sẻ sẽ mở cổng cho kh&aacute;n giả mến mộ đến để chụp h&igrave;nh lưu niệm với hang đ&aacute; do Team Đ&agrave;m Vĩnh Hưng thực hiện, ngay tại biệt thự sang trọng của m&igrave;nh (Địa chỉ AA14 Thất Sơn - Phường 15 - Quận 10, TP.HCM).</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">C&ugrave;ng ngắm ch&ugrave;m ảnh kh&ocirc;ng gian sang trọng trong biệt thự của Đ&agrave;m Vĩnh Hưng v&agrave; kh&ocirc;ng kh&iacute; tưng bừng chuẩn bị gi&aacute;ng sinh của anh v&agrave; c&aacute;c th&agrave;nh vi&ecirc;n trong đội.</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-position: initial initial; background-repeat: initial initial;">&nbsp;</p>\r\n\r\n<p style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px 0px 10px; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; text-align: center; background-position: initial initial; background-repeat: initial initial;"><img alt="" height="300" src="http://saigonnhadat.vn/upload/UserFiles/image/07_12_2012%20nha%20dat%20dia%20oc%20bat%20dong%20san%2019.jpg" style="background-color: transparent; border: 0px; font-size: 14px; margin: 0px; outline: 0px; padding: 0px; background-position: initial initial; backgroun